#define LONGITUDE 24
#define LATITUDE 16
#define M_PI   3.141593
#define M_PI_2 1.570796

float cross2d( in vec2 a, in vec2 b )
{
    return a.x*b.y - a.y*b.x;
}

const vec3 lightDir = normalize(vec3(.5, 1, -1));
const vec3 lightCol = vec3(.5, .5, .5);
const float smoothness = 256.;

vec3 shader(vec3 view, vec3 normal, vec3 color)
{
    float deltaA = dot(lightDir,normal);
    if (deltaA < 0.) deltaA = 0.;
    vec3 halfV = normalize(lightDir + view);
    float deltaB = pow(max(dot(normal,halfV), .0), smoothness);
    return color + lightCol * deltaA + deltaB * .5;
}

