    if (coord.x < 1)
    {
        float rx = iMouse.z <= 0. ? .3 + .3 * sin(iTime * .55) : (iMouse.y/iResolution.y-.5)*-3.1415;
        float ry = iMouse.z <= 0. ? .3*iTime : iMouse.x/iResolution.x*10.;
        if (coord.y < 5)
        {
            if (coord.y == 4)
            {
                fragColor = vec4(0,0,-8,0) * setRotation( rx, 0.,  0. ) * setRotation( 0., ry, 0. );
            }
            else
            {
                mat4 mvp = setTranslation(0., 0., -8. ) * 
                   setRotation( rx, 0.,  0. ) * 
                   setRotation( 0., ry, 0. );
                fragColor = mvp[coord.y];
            }
        }
    }
}

