vec3 GetSphericalCoord(float longitude, float latitude)
{
    return vec3(cos(longitude)*cos(latitude),sin(latitude), sin(longitude) * cos(latitude));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 pos = ivec2(fragCoord);
    int index = pos.y / 7 * int(iResolution.x) + pos.x;
    int subdata = pos.y % 7;
    if (index >= LONGITUDE * (LATITUDE - 1) * 2) discard;
    float ref = texelFetch(iChannel0, ivec2(0),0).w;
    if (ref == iResolution.x)
    {
        fragColor = texelFetch(iChannel0, pos, 0);
        return;
    }
    bool second = index >= LONGITUDE * (LATITUDE - 1);
    if (subdata == 6)
    {
        if ((index & 0x1) == 0)
        {
            fragColor = vec4(.5, second ? .5 : .0, 0, 0);
        }
        else
        {
            fragColor = vec4(0, .5, second ? .5 : .0, 0);
        }
        return;
    }
    float DtY = M_PI / float(LATITUDE);
    float DtX = 2. * M_PI / float(LONGITUDE);
    int a = index % LONGITUDE;
    int b = index / LONGITUDE + 1;
    if (second)
    {
        b = b - LATITUDE + 2;
    }
    vec3 vert;
    if (subdata == 0 || subdata == 3)
    {
        vert = GetSphericalCoord(DtX * float(a), DtY * float(second ? b - 1 : b) - M_PI_2);
    }
    else if (subdata == 1 || subdata == 4)
    {
        vert = GetSphericalCoord(DtX * float(second ? a : a + 1), DtY * float(b) - M_PI_2);
    }
    else
    {
        vert = GetSphericalCoord(DtX * float(a + 1), DtY * float(b - 1) - M_PI_2);
    }
    if (subdata < 3)
    {
        fragColor = vec4(vert * 2., 0);
    }
    else
    {
        fragColor = vec4(normalize(vert), 0);
    }
    if (pos.x == 0 && pos.y == 0) fragColor.w = iResolution.x;
}

