// Scene

// Fork of "Gelami Raymarching Template" by gelami. https://shadertoy.com/view/mslGRs
// 2023-05-02 21:17:35

float map(vec2 p)
{
    float h = texture(iChannel0, (p*1.0 - vec2(-4.0, 1.0)) / iChannelResolution[0].xy).r;

    h = smoothstep(0.52, 0.8, h);
    //float h = pow(hash12(p), 12.0)*0.8+0.1;

    #ifdef MOTION
    h = h*0.8+0.1;
    h += sin((iTime*0.5 + texture(iChannel0, p / iChannelResolution[0].xy).g) * TAU)*h*0.1;
    //h += getPillarMotion(p, iTime);
    #endif

    //float h = hash12(p * 0.033);
    return clamp(h * MAX_HEIGHT, MIN_HEIGHT, MAX_HEIGHT);
}



vec4 debug = vec4(-1);
bool trace(vec3 ro, vec3 rd, out HitInfo hit)
{
    if ((ro.y > MAX_HEIGHT && rd.y > 0.0) || (ro.y < MIN_HEIGHT && rd.y < 0.0))
        return false;

    const vec2 s = BOX_SIZE;
    const vec2 s2 = s * 0.5;

    vec2 tp = floor(ro.xz / s);

    const vec2 sps = SPHERE_PERIOD;
    const vec2 sps2 = sps * 0.5;
    const float srad = SPHERE_RADIUS;
    const float spy = SPHERE_HEIGHT;

    vec2 spp = vec2(1e5);
    vec2 sp = floor(ro.xz / sps);
    bool sphit = false;
    float st = MAX_DIST;

    //if (ro.y < map(tp))
    //    return true;

    vec3 ird = 1.0 / rd;
    vec3 srd = sign(rd);
    vec3 ard = abs(ird);

    vec3 iro = (ro - vec3(s2.x, 0, s2.y)) * ird;

    float tmin = MAX_DIST;
    hit.t = MAX_DIST;
    hit.n = vec3(-1);

    int i = 0;
    for (; i < STEPS; i++)
    {
        #if 1
        if (spp != sp)
        {
            if (sphit)
            {
                hit.t = st;
                vec3 pos = ro + rd * hit.t;
                hit.n = normalize(pos - vec3((spp.x + 0.5) * sps.x, spy, (spp.y + 0.5) * sps.y));
                hit.tp = spp;
                hit.type = 1;
                return true;
            }

            vec3 ce = vec3((sp.x + 0.5) * sps.x, spy, (sp.y + 0.5) * sps.y);
            vec2 spt = sphereIntersect(ro, rd, ce, srad);
            if (spt.x < 0.0)
            {
                /*
                hit.t = spt.y;
                hit.n = normalize(ro + rd * spt.y - ce);
                hit.tp = sp;
                hit.type = 1;*/
                return true;
            }
            if (spt.x < MAX_DIST)
            {
                st = spt.x;
                sphit = true;
            }
            spp = sp;
        }
        #endif

        float d = map(tp) * 0.5;

        vec3 p = vec3(tp.x * s.x, d, tp.y * s.y);
        vec3 bs = vec3(s2.x, d, s2.y);
        vec3 n = iro - p * ird;
        vec3 k = ard * bs;

        vec3 t1 = -n - k;
        vec3 t2 = -n + k;

        float tN = max(max(t1.x, t1.y), t1.z);
        float tF = min(min(t2.x, t2.y), t2.z);

        vec3 pos = ro + rd * tN;

        if (tN < st && pos.y > MAX_HEIGHT && rd.y > 0.0)
            return false;

        if (tN < st && tN >= MAX_DIST)
            return false;

        if (tN < st && tN < tF && tN >= 0.0)
        {
            #ifndef ROUNDED
            hit.t = tN;
            hit.n = -srd * step(t1.yzx, t1) * step(t1.zxy, t1);
            hit.tp = tp;
            hit.type = 0;
            return true;

            #else

            const float rad = RADIUS;

            /*
            if (d*2.0 < MIN_HEIGHT)
            {
                hit.t = tN;
                hit.n = -srd * step(t1.yzx, t1) * step(t1.zxy, t1);
                hit.tp = tp;
                hit.type = 0;
                return true;
            }*/

            vec3 o = p + vec3(s2.x, 0, s2.y);
            vec3 rp = ro - o;
            float rt = roundedboxIntersect(pos - o, rp, rd, bs-rad, rad);

            if (rt != MAX_DIST)
            {
                hit.t = rt == 0.0 ? tN : rt;
                vec3 pos = rp + rd * hit.t;
                hit.n = normalize(roundedboxNormal(pos, bs-rad, rad));
                hit.tp = tp;
                hit.type = 0;
                return true;
            }

            if (ro.y + rd.y * min(t2.x, t2.z) < 0.0)
            {
                hit.t = -ro.y * ird.y;
                hit.n = vec3(0, 1, 0);
                hit.tp = vec2(0);
                hit.type = 2;
                return true;
            }
            #endif
        }

        tp += step(t2.xz, t2.zx) * srd.xz;
        sp = floor(tp * s / sps);

    }
    return false;
}

vec3 invReinhard(vec3 color, float exposure)
{
    return color / (exposure * max((1.0 - color) / exposure, 1e-3));
}

vec3 getSky(vec3 rd)
{
    vec3 sky = sRGBToLinear(texture(iChannel3, rd).rgb);

    sky = invReinhard(sky, 400.0);

    return sky;
}

vec3 getSky(vec3 rd, vec3 rdDx, vec3 rdDy)
{
    vec3 sky = sRGBToLinear(textureGrad(iChannel3, rd, rdDx, rdDy).rgb);

    sky = invReinhard(sky, 400.0);

    return sky;
}

vec3 shade(vec3 pos, HitInfo hit, vec3 ldir, vec3 rd)
{
    vec3 col = vec3(0);
    vec3 alb = palette2(hash12(hit.tp));
    vec3 skyCol = vec3(1, 0.5, 0.4);

    HitInfo hitL;
    bool isHitL = trace(pos + ldir * hit.t*EPS, ldir, hitL);

    vec3 ref = reflect(rd, hit.n);

    float dif = max(dot(hit.n, ldir), 0.0) * float(!isHitL);
    float bou = max(dot(ref, -ldir), 0.0);
    float ao = smoothstep(0.0, MAX_HEIGHT*0.6, pos.y);

    col += alb * (dif * 0.9 + 0.1);
    col += alb * bou * 0.08;
    col += skyCol * 0.1 * ao;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ndc = (2. * (fragCoord) - iResolution.xy) / iResolution.y;
    vec2 ndcDx = (2. * (fragCoord + vec2(1, 0)) - iResolution.xy) / iResolution.y;
    vec2 ndcDy = (2. * (fragCoord + vec2(0, 1)) - iResolution.xy) / iResolution.y;

    vec2 uv = fragCoord / iResolution.xy;

    vec3 ro = vec3(0, 0, 16);
    vec3 lo = vec3(0.2 * iTime, 2, 0.8 * iTime);

    vec2 m = iMouse.xy / iResolution.xy;

    float ax = -PI * .7 - iTime * 0.16;
    float ay = PI * 0.2;

    if (iMouse.z > 0.0)
    {
        if (iMouse.x != 0.0) ax = -m.x * TAU + PI;
        if (iMouse.y != 0.0) ay = m.y * PI * 0.5 + PI * 0.5;
    }

    ro.yz *= rot2D(ay);
    ro.xz *= rot2D(ax);
    ro += lo;

    mat3 cmat = getCameraMatrix(ro, lo);

    float tanFov = 2.0;
    vec3 rd = normalize(cmat * vec3(ndc, tanFov));
    vec3 rdDx = normalize(cmat * vec3(ndcDx, tanFov));
    vec3 rdDy = normalize(cmat * vec3(ndcDy, tanFov));

    HitInfo hit;
    bool isHit = trace(ro, rd, hit);

    float t = hit.t;
    vec3 n = hit.n;
    vec2 tp = hit.tp;

    vec3 col = vec3(0);
    vec3 alb = palette2(hash12(hit.tp));
    vec3 skyCol = vec3(1, 0.5, 0.4);
    //skyCol = vec3(0.5, 0.8, 1);

    vec3 ldir = normalize(vec3(0.8, 2, 1.2));

    vec3 pos = ro + rd * t + n * EPS;
    vec3 posDx = ro - rdDx * dot(ro - pos, n) / dot(rdDx, n);
    vec3 posDy = ro - rdDy * dot(ro - pos, n) / dot(rdDy, n);

    vec3 nDx = hit.type == 1 ? (posDx - pos) : vec3(0);
    vec3 nDy = hit.type == 1 ? (posDy - pos) : vec3(0);

    if (hit.type == 0)
    {
        float d = map(hit.tp) * 0.5;
        vec3 bpos = pos - vec3((hit.tp + 0.5) * BOX_SIZE, d).xzy;
        bpos = abs(bpos);

        vec3 size = vec3(BOX_SIZE.x * 0.5, d, BOX_SIZE.y * 0.5) - RADIUS;
        bpos -= size;

        vec3 bmpos = max( bpos.xyz, bpos.yzx );

        if( min(min(bmpos.x, bmpos.y), bmpos.z) < 0.0 )
        {
            nDx = vec3(0);
            nDy = vec3(0);
        } else if (all(greaterThan(bpos, vec3(0))))
        {
            nDx = (posDx - pos) / RADIUS;
            nDy = (posDy - pos) / RADIUS;
        } else
        {
            vec3 bm = 1.0 - step(bpos, bpos.yzx) * step(bpos, bpos.zxy);

            nDx = (posDx - pos) * bm / RADIUS;
            nDy = (posDy - pos) * bm / RADIUS;
        }
    }

    vec3 ref = reflect(rd, n);
    vec3 refDx = (rdDx - rd) - 2.0 * (dot(rd, n) * nDx + (dot(rdDx - rd, n) + dot(rd, nDx)) * n);
    vec3 refDy = (rdDy - rd) - 2.0 * (dot(rd, n) * nDy + (dot(rdDy - rd, n) + dot(rd, nDy)) * n);

    col = shade(pos, hit, ldir, rd);

    HitInfo hitR;
    bool isHitR = trace(pos, ref, hitR);

    vec3 refPos = pos + ref * hitR.t + hitR.n * EPS;
    vec3 refCol = shade(refPos, hitR, ldir, ref);

    if (!isHitR)
    {
        #if 1
        refCol = getSky(ref, refDx, refDy);
        #else
        refCol = getSky(ref);
        #endif
    }

    vec3 r0 = hit.type == 1 ? alb : vec3(0.08);
    vec3 fre = r0 + (1.0 - r0) * pow(1.0 - dot(-rd, n), 5.0);

    col = mix(col, refCol, fre);

    //if (hit.type == 1) col = refCol;

    #ifdef FOG
    vec3 fog = 1.0 - exp(-max(t - FOG_START, 0.0) * skyCol * FOG_STRENGTH);
    col = col * (1.0 - fog) + vec3(1, 0.7, 0.5) * fog;
    #endif

    if (!isHit)
    {
        col = getSky(rd);
        t = MAX_DIST;
    }

    col = max(col, vec3(0));
    #ifdef FXAA
    col = col / (1. + col);
    col = linearTosRGB(col);
    #endif

    #ifdef DOF_CAMERA_Z
    t = t * dot(rd, cmat[2]);
    #endif

    fragColor = vec4(col, t);

    if (debug != vec4(-1))
        fragColor = debug;
}

