// Depth of Field

// DoF Defines
#define DOF_FOCUS_DIST 16.0

#define DOF_RADIUS 24.0
#define DOF_FOCUS_SCALE 8.0

#define RADIUS_SCALE 1.

//#define DOF_USE_LOD
#define DOF_MAX_LOD 4.0

#define DOF_STEPS 32

#define GOLDEN_ANGLE 2.39996323

float getCoc(float t, float focalDist, float scale)
{
    return saturate(abs(1.0 / focalDist - 1.0 / t) * scale) * DOF_RADIUS;
}

vec3 depthOfField(sampler2D tex, vec2 uv)
{
    float ct = texture(tex, uv).a;
    // Auto-focus (doesn't work T_T)
    //float fd = texture(tex, vec2(0.5)).a;
    float fd = DOF_FOCUS_DIST;

    float cs = getCoc(ct, fd, DOF_FOCUS_SCALE);

    #ifdef DOF_USE_LOD
    vec3 color = textureLod(tex, uv, DOF_MAX_LOD * cs / DOF_RADIUS).rgb;
    #else
    vec3 color = texture(tex, uv).rgb;
    #endif

    vec2 px = 1.0 / iResolution.xy;
    float w = 1.0;
    float radStep = DOF_RADIUS / float(DOF_STEPS);
    float rad = radStep * 0.5;
    float ang = 0.0;

    for (int i = 0; i < DOF_STEPS; i++)
    {
        vec2 p = uv + vec2(cos(ang), sin(ang)) * rad * px;
        #ifdef DOF_USE_LOD
        vec4 sampCol = textureLod(tex, p, DOF_MAX_LOD * rad / DOF_RADIUS);
        #else
        vec4 sampCol = texture(tex, p);
        #endif

        float ss = getCoc(sampCol.a, DOF_FOCUS_DIST, DOF_FOCUS_SCALE);

        if (sampCol.a > ct)
            ss = clamp(ss, 0.0, cs * 2.0);

        float m = smoothstep(rad - 0.5, rad + 0.5, ss);

        color += mix(color / w, sampCol.rgb, m);
        ang += GOLDEN_ANGLE;
        rad += radStep / rad;
        w += 1.0;
    }

    return color / w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    #ifdef FXAA
    vec3 col = depthOfField(iChannel1, uv);
    #else
    vec3 col = depthOfField(iChannel1, uv);
    #endif
    //col = col / (1.0 + col);
    //col = linearTosRGB(col);

    fragColor = vec4(col, 1);
}

