#define WHITE vec3(1.0)
#define BLACK vec3(0.0)


struct Torus
{
    vec3 dir;
    vec3 pos;
    float r;
    float s;
};

Torus t1 = Torus(normalize(vec3(1.0, 0.5, 0.0)), vec3(0.0), 2.0, 1.0);

vec3 lightPos = vec3(2.0, -5.0, 3.0);


mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        c, 0, s, 
        0, 1, 0, 
        -s, 0, c
    );
}

float torusSDF(in vec3 p)
{
    vec3 relativePos = p - t1.pos;
    vec3 relPosProj = t1.r * normalize(relativePos - t1.dir * dot(relativePos, t1.dir));
    return length(relPosProj - relativePos) - t1.s;
}

vec3 calculateNormal(in vec3 pos)
{
    const vec3 smallStep = vec3(0.001, 0.0, 0.0);

    float gradientX = torusSDF(pos + smallStep.xyy) - torusSDF(pos - smallStep.xyy);
    float gradientY = torusSDF(pos + smallStep.yxy) - torusSDF(pos - smallStep.yxy);
    float gradientZ = torusSDF(pos + smallStep.yyx) - torusSDF(pos - smallStep.yyx);

    vec3 normal = vec3(gradientX, gradientY, gradientZ);

    return normalize(normal);
}

vec3 rayMarch(in vec3 rayOrigin, in vec3 rayDir)
{
    float totalDistTraveled = 0.0;
    const int NUMBER_OF_STEPS = 256;
    const float MINIMUM_HIT_DIST = 0.001;
    const float MAXIMUM_TRACE_DIST = 10.0;

    for (int i = 0; i < NUMBER_OF_STEPS; ++i)
    {
        vec3 currentPos = rayOrigin + totalDistTraveled * rayDir;
        float smallestDist = torusSDF(currentPos);

        if (smallestDist <= MINIMUM_HIT_DIST)
        {
            vec3 normal = calculateNormal(currentPos);
            vec3 lightDir = normalize(currentPos - lightPos);

            float diffuseIntensity = max(0.05, dot(normal, lightDir));

            return vec3(1.0) * diffuseIntensity;
        }

        if (totalDistTraveled >= MAXIMUM_TRACE_DIST)
            break;

        totalDistTraveled += smallestDist;
    }

    return BLACK;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    // convert from (0 to 1) to (-1 to 1)
    uv = uv * 2.0 - 1.0;
    // rescale
    uv.x *= iResolution.x / iResolution.y;

    t1.dir *= rotateY(-iTime * 0.2);

    vec3 rOrigin = vec3(0.0, 0.0, -5.0);
    vec3 rDir = vec3(uv, 1.0);
    vec3 col = rayMarch(rOrigin, rDir);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

