#define n_tau 6.2831
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_its = 20.;

    float n_dmax = 0.;
    vec2 o_fc = (fragCoord.xy - iResolution.xy*.5) / iResolution.y;
    for(float n_it = 0.; n_it < n_its; n_it+=1.){
        float n_it_nor = n_it / n_its;
        float n_radians = n_it_nor * n_tau;
        float n_speed = 4.;
        float n_corners = floor(mod(iTime*(1./(n_tau/n_speed)),9.));//3.;//floor(fract(iTime*0.1)*9.);
        float n_radius = 0.2+(sin(iTime*n_speed+n_it_nor*n_tau*n_corners+(n_tau/2.))*.5+.5)*0.2; 
        vec2 o_p = vec2(
            sin(n_radians)*n_radius, 
            cos(n_radians)*n_radius
        );

        vec2 o_diff = o_fc - o_p;
        float n_d_1 = length(
            o_diff*3.
        );

        float n_d_2 = (max(
            abs(o_diff.x), abs(o_diff.y)
        ))*0.5;
        float n_fact = sin(iTime*n_speed+n_it_nor*n_tau)*.5+.5;
        float n_d = 
        (1.-n_fact)* n_d_1 + 
        n_fact * n_d_2;

        // n_d = abs(n_d-(sin(iTime+n_it_nor)*0.5+.5)*0.2);
        n_d = abs(n_d-(1./n_its/2.));
        n_d = pow(n_d, 1./3.);
        n_d = n_d *2.;
        n_d = 1.- n_d;
        // n_d = n_d*2.;
        // n_d = abs(n_d-(sin(iTime)*.5+.5));
        // n_d = fract(n_d-sin(iTime)*0.5);

        if(n_d > n_dmax){
            n_dmax = n_d;
        }

    }
    fragColor = vec4(n_dmax);

}

vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
        //-vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

