{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.y += (iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    vec2 uv1 = uv;
    uv.y = mod(uv.y,.5)+.25;
    uv.x = mod(uv.x,.5)+.25;

    for(int k = 0; k < 6; k++){
        uv =
            abs(.5-uv+t2*sign(uv.y-uv.x))/1.5+sign(uv.x)/2.
        ;
        b = uv.y>uv.x;
        if(b) uv = uv.yx;
        t2 =
            triangle_wave(uv+.5).yx
            //triangle_wave(uv+.5).yx*(1.5-mod(floor(uv.y),2.)/2.)
        ;
        uv =
            t2+triangle_wave(uv.yx).yx
        ;
        if(uv.y>uv.x)
        //{t2 = t2.yx; uv=uv.yx;}
        col.x =
            abs((uv.x)-col.x)
            //fract((uv.x)-col.x-iTime/4.)
        ;

        //awesome kaleidoscope effect!
        //if(t2.y>t2.x) {t2 = t2.yx; uv=uv.yx-iTime/8.;}

        col.xyz =
            abs(col.yzx-float(b)/2.)
            //abs(vec3(col.zx,max(uv.y-uv.x,col.y)))
        ;
        //if(t2.x>t2.y) col=col.yzx;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(min(col,vec3(1.)),1.0);
}

