vec4 cell(in vec2 q) {
    ivec2 p = ivec2(q);
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0);
}

vec2 pxToF(vec2 px) { return round(sc * (px - 0.5 * res) / res.y); }
vec2 fToPx(vec2 f)  { return f / sc * res.y + 0.5 * res; }

void mainImage( out vec4 O, in vec2 px )
{
    vec2 uv = (px - 0.5 * res) / res.y;
    vec2 f = round(sc * uv);

    vec4 c = cell(px);
    float x = 1.;
    vec4 t = cell(fToPx(f - vec2( 0,  x)));
    vec4 l = cell(fToPx(f - vec2( x,  0)));
    vec4 r = cell(fToPx(f - vec2(-x,  0)));
    vec4 b = cell(fToPx(f - vec2( 0, -x)));

    // Outline (might be off by one or two pixels)
    if (c.a == 2. && (t.a != 2. || l.a != 2. || r.a != 2. || b.a != 2.))
        c.rgb *= 0.95;//= vec3(63,63,116) / 255.;

    O = c;
}

