// --- HASH FUNCTIONS ---

// ty Dave Hoskins! https://www.shadertoy.com/view/4djSRW
float hash11(float p) {
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float hash12(vec2 p) {
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


// --- PRIMITIVE SHAPES --- 

// x,y bottom left corner
float box(vec2 f, float x, float y, float w, float h) {
    return step(x, f.x) * step(f.x, x + w - 1.) *
           step(y, f.y) * step(f.y, y + h - 1.);
}
// Diamond
float dia(vec2 f, float w, float h) {
    f = abs(f - vec2(0.5, 0));
    return step(f.x, h - 2. * f.y) * step(f.x, w); 
}
// Slanted box: left+right sides are vertical, bottom+top are slanted
float sbox(vec2 f, float w, float h, float s) {
    f -= vec2(0.5 * (s - 1.), 0.); // Why -1.?
    f.x *= s;
    return step(0., f.x) * step(f.x, w) *
           step(f.x, 2.*f.y+1.) * step(2.*f.y-h, f.x);
}
// Slanted rect: b bottom point, t top point
float srect(vec2 f, vec2 b, vec2 t) {
    vec2 fb = f-b;
    vec2 ft = f-t;
    float s = step(fb.x-2.*fb.y, 0.) * step(1., fb.x+2.*fb.y); 
    return s * step(1., ft.x-2.*ft.y) * step(ft.x+2.*ft.y, 0.);
}
// Slanted rect with extra height
float srect2(vec2 f, vec2 b, vec2 t, float h) {
    vec2 fb = f-b-vec2(0,h);
    vec2 ft = f-t;
    float l = t.y-b.y+0.5*(t.x-b.x);
    float r = t.y-b.y-0.5*(t.x-b.x);
    float s = step(1., ft.x-2.*ft.y) * step(ft.x+2.*ft.y, 0.);
    s *= step(fb.x-2.*fb.y, 0.) * step(1., fb.x+2.*fb.y); 
    s *= step(f.x-r, -1.) * step(2.,f.x+l);
    return s;
}


// --- OBJECTS --- 

// b: bottom point, t: top point, h: height of lower island
void drawIsland(vec2 f, vec2 b, vec2 t, float h) {
    float top = srect(f, b, t);
    float bot = srect2(f, b, t, -h) - top;
    float uInd = mix(9., 10., step(b.x-t.x+1., f.x));
    ind = mix(ind, 7., top);  
    ind = mix(ind, uInd, bot);
}
// dm: diamond dimensions, h: height (>4), th: bot thickness
void drawTower(vec2 f, vec2 dm, float h, float th) {
    f.y -= h + 0.5*dm.y;
    float m = min(dm.x, dm.y);
    float di1    = dia(f, dm.x, dm.y);
    float di2    = dia(f, dm.x - 2., dm.y - 2.);
    float di3    = dia(f - vec2(0, -1), dm.x - 2., dm.y - 2.);
    float base   = dia(f - vec2(0, -0.5*h), th, dm.y+h);
    float top    = dia(f - vec2(0, -1.), m, 2.+dm.y);
    float shadow = dia(f - vec2(0, -2.), m-1., 2.+dm.y);  
    ind = mix(ind, mix(0., 1., step(f.x, 0.)), base);
    ind = mix(ind, 0., shadow);
    ind = mix(ind, mix(0., 1., step(f.x, 1.)), top);
    ind = mix(ind, 5., di1);
    ind = mix(ind, mix(3., 4., step(f.x, 0.)), di1 - di2);
    ind = mix(ind, 0., max(0., di2 - di3));
}
void drawWallTL(vec2 f, vec2 dm, float th) {
    float base = sbox(f, dm.x + 1., dm.y, 1.);
    float carpet = sbox(f - vec2(0, 0.5*dm.y+1.), dm.x-0., 2., 1.);
    carpet *= step(f.y - (0.5*dm.y+1.), 0.5*dm.x);
    float shadow = sbox(f - vec2(0, 0.5*dm.y+3.), dm.x-2., 0., 1.);
    float trim = sbox(f - vec2(0, 0.5*dm.y+4.), dm.x-2., 0., 1.);
    ind = mix(ind, 0., base);
    ind = mix(ind, 5., carpet);
    ind = mix(ind, 0., shadow);
    ind = mix(ind, 4., trim);
}
void drawWallTR(vec2 f, vec2 dm, float th) {
    float base = sbox(f, dm.x + 1., dm.y, -1.);
    float carpet = sbox(f - vec2(0, 0.5*dm.y+1.), dm.x, 2., -1.);
    carpet *= step(f.y - (0.5*dm.y+1.), 0.5*dm.x);
    float shadow = sbox(f - vec2(0, 0.5*dm.y+3.), dm.x-2., 0., -1.);
    float trim = sbox(f - vec2(0, 0.5*dm.y+4.), dm.x-2., 0., -1.);
    ind = mix(ind, 1., base);
    ind = mix(ind, 5., carpet);
    ind = mix(ind, 0., shadow);
    ind = mix(ind, 3., trim);
}
void drawWallBL(vec2 f, vec2 dm) {
    float base = sbox(f, dm.x, dm.y, -1.);
    float shadow = sbox(f - vec2(0, 0.5*dm.y+1.), dm.x, 0., -1.);
    float trim = sbox(f - vec2(0, 0.5*dm.y+2.), dm.x, 0., -1.);   
    float carpet = sbox(f - vec2(0, 0.5*dm.y+3.), dm.x-1., 2., -1.);
    ind = mix(ind, 1., base);
    ind = mix(ind, 0., shadow);
    ind = mix(ind, 4., trim);
    ind = mix(ind, 5., carpet);
}
void drawWallBR(vec2 f, vec2 dm) {
    float base = sbox(f, dm.x, dm.y + 2., 1.);
    float trim = sbox(f - vec2(0, 0.5*dm.y+2.), dm.x, 0., 1.);
    float carpet = sbox(f - vec2(0, 0.5*dm.y+3.), dm.x-1., 2., 1.);
    ind = mix(ind, 0., base);
    ind = mix(ind, 3., trim);
    ind = mix(ind, 5., carpet);
}
// Only B and R towers
void drawTowerShadow(vec2 f, vec2 dm, float th) {
    f.y -= 0.5 * dm.y;
    float shadow = dia(f - vec2(4, 0), dm.x, dm.y);
    ind = mix(ind, 6., shadow);
}
// Only TL and BR walls
void drawWallShadow(vec2 f, vec2 dm) {
    float shadow = sbox(f - vec2(2, -1), dm.x + 1., 3., 1.);
    ind = mix(ind, 6., shadow);
}
void drawTowerGrass(vec2 f, vec2 dm, float th) {
    f.y -= 0.5 * dm.y;
    float grass = dia(f - vec2(0, -1), th, dm.y);
    ind = mix(ind, 8., grass);
}
void drawWallGrass(vec2 f, vec2 dm) {
    float grass = sbox(f - vec2(0, -1), dm.x, 0., -1.);
    ind = mix(ind, 8., grass);
}
void drawFlag(vec2 f, float h) {
    float m = mod(floor(4. * iTime), 2.);
    float pole = box(f, 0., 0., 1., h);
    float tip  = box(f, 0., h + 1., 1., 1.);
    float flag = box(f, 0., h, 1., 1.) + box(f, 1., h-2.+m, 1., 3.) +
                 box(f, 2., h-1.-m, 1., 3.);
    float shadow = box(f, 1., 0., 2., 1.);  
    ind = mix(ind, 1., pole);
    ind = mix(ind, 4., tip);
    ind = mix(ind, 11., flag);
    ind = mix(ind, 0., shadow);
}
// Unfinished, needs to consider tower thickness? has a left bias
void drawDoor(vec2 f, float w, float h) {
    //if (ind != 1.) return; // Only draw on front wall
    w = 5.;
    h = 48.;    
    float door = sbox(f - 0.25*(2.-w)*bY, w, h, -1.);
    float e = step(f.x, -floor(0.5*w)-2.) * door;
    //ind = mix(ind, 0., e);   
    ind = mix(ind, 7., door);   
    ind = mix(ind, 0., e);  
}

void mainImage(out vec4 O, in vec2 px) {  
    vec2 uv = (px - 0.5 * res.xy) / res.y;

    // Pixelate uv
    vec2 f = round(sc * uv); // Towers look good with f = sc * uv

    // Pick palette with time
    pal = int(mod(floor(iTime), 3.));

    // Random parameters
    float[13] rand;
    float fTime = floor(iTime);
    for (int i = 0; i < 13; i++) {
        rand[i] = hash11(fTime + float(i) / 13.);
    }

    // Tower: dimensions, height, thickness
    vec2 towDim = vec2(0, 6. + 2.*floor(8. * rand[0]));
    towDim.x = floor(mix(max(5., 0.25*towDim.y), towDim.y+2., rand[1]));
    float towH = 2.*floor(mix(4., 20., rand[2]));
    float towTh = 2.;//floor(mix(6., min(towDim.x, towDim.y)-1., rand[3]));
    towTh = clamp(towTh, 4., min(towDim.x, towDim.y)-1.);

    // Wall: height, lengths (keep height even, lengths odd)
    float wallH = 2.*floor(mix(2., 0.5*towH, rand[4]));
    wallH = min(wallH, towH+2.);
    float wallX = 1. + 2.*max(8., floor(30.*rand[5]));
    float wallY = 1. + 2.*max(8., floor(30.*rand[6]));

    // Island: padding (>=4.), height
    float islPad = 4.;
    float islH = 4.;

    // Flag: height
    float flagH = floor(mix(8., 17., rand[7]));

    // Positions
    vec2 pTowT = vec2(0);
    vec2 pTowL = -0.5 * bX * (3. + wallX);
    vec2 pTowR = -0.5 * bY * (3. + wallY);
    vec2 pTowB = pTowL + pTowR;    
    vec2 pWallTL = pTowL + vec2(8.-towTh, 0.5*(1.+towTh));
    vec2 pWallTR = pTowR + vec2(towTh-6., 0.5*(1.+towTh));
    vec2 pWallBR = pTowB + vec2(1, 2.5);
    vec2 pWallBL = pWallBR;

    // Centre coords to centre of castle and translate
    f += round(0.5*pTowB);
    f.y += 18.;


    // --- DRAW --   

    // Background 
    ind = 2.;

    // Island (offset with bX, bY for non-constant padding)
    drawIsland(f, pTowB - vec2(0, islPad), pTowT - vec2(0, 1.-towDim.y-islPad), islH);

    // Grass and Shadows
    drawTowerShadow(f - pTowT, towDim, towTh); 
    drawWallGrass  (f - pWallBL, vec2(wallY, wallH));
    drawWallGrass  (f - pWallTR, vec2(wallY, wallH));
    drawTowerGrass (f - pTowL, towDim, towTh);
    drawTowerGrass (f - pTowB, towDim, towTh);
    drawTowerShadow(f - pTowL, towDim, towTh); 
    drawTowerShadow(f - pTowB, towDim, towTh);
    drawTowerShadow(f - pTowR, towDim, towTh);
    drawWallShadow (f - pWallTL, vec2(-2.+wallX, wallH));
    drawWallShadow (f - pWallBR, vec2(1.+wallX, wallH));    

    // Towers and Walls
    drawTower (f - pTowT, towDim, towH, towTh);  
    drawWallTR(f - pWallTR, vec2(-2.+wallY, wallH), towTh);
    drawWallTL(f - pWallTL, vec2(-2.+wallX, wallH), towTh);      
    drawTower (f - pTowL, towDim, towH, towTh);
    drawTower (f - pTowR, towDim, towH, towTh);    
    drawWallBR(f - pWallBR, vec2(wallX, wallH));
    drawWallBL(f - pWallBL, vec2(wallY, wallH));
    drawTower (f - pTowB, towDim, towH, towTh);      

    // Door (todo)
    // drawDoor(f - 0.5*(pTowB+pTowL)- vec2(0, 3), 9., 6.);

    // Flags
    if (rand[8] > 0.5) 
        drawFlag(f - pTowT - vec2(0, towH + 0.5 * towDim.y), flagH);     
    if (rand[9] > 0.5) 
        drawFlag(f - pTowL - vec2(0, towH + 0.5 * towDim.y), flagH);    
    if (rand[10] > 0.5) 
        drawFlag(f - pTowR - vec2(0, towH + 0.5 * towDim.y), flagH);    
    if (rand[11] > 0.5) 
        drawFlag(f - pTowB - vec2(0, towH + 0.5 * towDim.y), flagH);    

    vec3 col = getCol();

    // Texture front wall and a random part
    float hh = hash12(mod(floor(iTime) + mod(f + ind, 4.),vec2(301.)));
    float hi1 = floor(float(nCol) * hash11(floor(iTime)));
    float hi2 = floor(float(nCol) * hash11(floor(iTime) + 0.5));
    if (rand[12] > 0.4 && (ind == hi1 || ind == hi2))
        col *= 0.9 + 0.2 * hh;

    // Vignette (very weak)
    col = mix(col, vec3(1), 1.-1./cosh(0.25*length(uv)));

    O = vec4(col, ind);
}

