    uv.x *= iResolution.x / iResolution.y;
    uv.x -= 0.4;

    float a = radians(45.0 * cos(iTime * 0.2));
    uv = mat2(cos(a), -sin(a), sin(a), cos(a)) * (uv - 0.5);

    vec3 M = iMouse.xyz / iResolution.xyz;

    vec3 baseColor = vec3(0.7647, 0.2039, 0.1529), color = vec3(0.0);
