    vec2 nm = vec2(1.0*(mouse.x*SCALE),4.0*(mouse.y*SCALE)); //higher values -> shorter periods.  ratio effects overall shape

    vec2 ab = vec2(sin(t)*1.2*(mouse2.x*ESCALE),cos(t)*0.49*(mouse2.y*ESCALE)); //larger values increase "sharpness", effects shape
    float col =  abs(ab.x*sin(pi*uv.x*nm.y)*sin(pi*uv.y*nm.x) + ab.y*sin(pi*uv.x*nm.x)*sin(pi*uv.y*nm.y));   
    fragColor = mix(vec4(0.8), vec4(col*vec3(sin(t),uv),1.0), smoothstep(0., 10./iResolution.y ,col-.1));

}

float rectSDF(vec2 uv, vec2 s) {
    return max(abs(uv.x / s.x), abs(uv.y / s.y));
}
float fill(float x, float s) {
    return step(x, s);
}

// From easy RGB : xyz -> RGB
vec3 colorWheel(float x, float y, float z) {
    vec3 c = vec3(
        x * 3.2406 + y * -1.5372 + z * -0.4986,
        x * -0.9689 + y * 1.8758 + z * 0.0415,
        x * 0.0557 + y * -0.204 + z * 1.057
    );

    float t = 0.0031308;
    if (c.r > t)
        c.r = 1.055 * (pow(c.r, (1.0 / 2.4))) - 0.055;
    else
        c.r = 12.92 * c.r;

    if (c.g > t)
        c.g = 1.055 * (pow(c.g, (1.0 / 2.4))) - 0.055;
    else
        c.g = 12.92 * c.g;

    if (c.b > t)
        c.b = 1.055 * (pow(c.b, (1.0 / 2.4))) - 0.055;
    else
        c.b = 12.92 * c.b;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x / iResolution.y;
    uv.x -= 0.43;

    uv *= 20.0;

    vec2 M = iMouse.xy / iResolution.xy;


    float fRed = 0.0, fGreen = 0.0, fBlue = 0.0;

    vec2 fi = floor(uv);
    vec2 f = fract(uv);

    vec3 modulator = colorWheel(M.x, M.y, 0.5);

    vec3 refAColor = modulator;
    vec3 refBColor = modulator.bgr;

    vec3[] pcts = vec3[](
        vec3(0.0528634, 0.050209, 0.0123966),
        vec3(0.319672, 0.2823529, 0.132653),
        vec3(0.1307189, 0.1156069, 0.1173469)
    );

    int I = 0;
    vec2 bPos = vec2(0.0);

    if (mod(fi.x, 2.0) == 0.0 && mod(fi.y, 2.0) == 1.0) {
        bPos.x = -1.0;
    } else if (mod(fi.x, 2.0) == 0.0 && mod(fi.y, 2.0) == 0.0) {
        I = 1;
        bPos = vec2(-1.0);
    } else if (mod(fi.x, 2.0) == 1.0 && mod(fi.y, 2.0) == 0.0) {
        I = 2;
        bPos.y = -1.0;
    } else if (mod(fi.x, 2.0) == 1.0 && mod(fi.y, 2.0) == 1.0) {
        I = 3;
    } else {
        refAColor = vec3(0.0);
        refBColor = vec3(0.0);
    }

    if (iMouse.z >= 1.0) {
        vec3 a = refAColor;
        refAColor = refBColor;
        refBColor = a;
    }

    float d = (fi.x * fi.y) * sin(iTime);

    for (int i = 0; i < I; i++) {
        fRed += pcts[i].r * d * 0.02;
        fGreen += pcts[i].g * d * 0.02;
        fBlue += pcts[i].b * d * 0.02;
    }

    float B = fill(rectSDF(f + bPos, vec2(1.0)), 0.52);

    vec3 color = refAColor.rgb * (1.0 + vec3(fRed, fGreen, fBlue)) * fill(rectSDF(f, vec2(1.0)), 1.0);
    color = B == 1.0 ? refBColor.rgb * (1.0 + vec3(fBlue, fGreen, fRed)) : color;

    // Output to screen
    fragColor = vec4(color,1.0);
}

// Extruded Flagstone Filing - gelami
// https://www.shadertoy.com/view/cltGRl

/*
 * Ray traversal of an extruded flagstone/asymmetric tiling
 * 
 * Each step of the traversal computes the position and size
 * of the current tile which is used for the intersection
 * 
 * Mouse drag to look around
 * Defines in Common
 * 
 * Kind of a sequel from my previous shader:
 * Colorful Rectangular Pillars - gelami
 * https://www.shadertoy.com/view/dtcGzX
 *
 * The tiling setup is from my old shader here:
 * Straight Flagstone Tiles - gelami
 * https://www.shadertoy.com/view/7tKGRc
 * 
 * Anti-aliasing uses my TAA shader with an added CAS pass for sharpening
 * 
 * TAA implementation from
 * Temporal AA Study - gelami
 * https://www.shadertoy.com/view/DsfGWX
 * 
 * CAS implementation from
 * AMD FidelityFX: CAS - goingdigital
 * https://www.shadertoy.com/view/ftsXzM
 * 
 */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col;

    #ifdef TAA
    col = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;

    // CAS implementation from
    // AMD FidelityFX: CAS - goingdigital
    // https://www.shadertoy.com/view/ftsXzM
    #ifdef CAS
    col = CAS_Pass(iChannel0, uv, iChannelResolution[0].xy).rgb;
    #endif

    #else
    col = texelFetch(iChannel1, ivec2(fragCoord), 0).rgb;
    #endif

    //col = smoothstep(vec3(0), vec3(1), col);
    //col = ACESFilm(col);
    fragColor = vec4(linearTosRGB(col), 1);
    fragColor += (dot(hash23(vec3(fragCoord, iTime)), vec2(1)) - 0.5) / 255.;
}

