#define TAA
#define CAS

#define MOTION
#define WARPING

#define ROUNDED
#define RADIUS 0.06

#define MIN_HEIGHT 0.05
#define MAX_HEIGHT 8.0

#define STEPS 256
#define MAX_DIST 180.
#define EPS 1e-4

#define PI (acos(-1.))
#define TAU (PI*2.)

// CAS implementation from
// AMD FidelityFX: CAS - goingdigital
// https://www.shadertoy.com/view/ftsXzM

#define CAS_FACTOR 0.0

vec3 CAS_Pass(sampler2D tex, vec2 uv, vec2 size)
{
    // CAS algorithm
    vec3 col = texture(tex, uv).rgb;

    float max_g = col.y;
    float min_g = col.y;

    vec4 uvoff = vec4(1,0,1,-1) / size.xxyy;
    vec3 colw;
    vec3 col1 = texture(tex, uv+uvoff.yw).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw = col1;

    col1 = texture(tex, uv+uvoff.xy).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;

    col1 = texture(tex, uv+uvoff.yz).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;

    col1 = texture(tex, uv-uvoff.xy).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;

    float d_min_g = min_g;
    float d_max_g = 1.-max_g;
    float A;

    if (d_max_g < d_min_g) {
        A = d_max_g / max_g;
    } else {
        A = d_min_g / max_g;
    }

    A = sqrt(A);
    A *= mix(-.125, -.2, CAS_FACTOR);
    vec3 col_out = (col + colw * A) / (1.+4.*A);

    return col_out;
}

// Rounded box ray-intersection
// https://www.iquilezles.org/www/articles/intersectors/intersectors.htm
float roundedboxIntersect( in vec3 pos, in vec3 ro, in vec3 rd, in vec3 size, in float rad )
{
    vec3 s = sign(pos);
    ro  *= s;
    rd  *= s;
    pos *= s;

    // Faces
    pos -= size;
    pos = max( pos.xyz, pos.yzx );
    if( min(min(pos.x,pos.y),pos.z) < 0.0 ) return 0.0;

    vec3 oc = ro - size;
    vec3 dd = rd*rd;
    vec3 oo = oc*oc;
    vec3 od = oc*rd;
    float ra2 = rad*rad;

    float t = MAX_DIST;

    // Corner
    {
        float b = od.x + od.y + od.z;
        float c = oo.x + oo.y + oo.z - ra2;
        float h = b*b - c;
        if( h>0.0 ) t = - b -sqrt(h);
    }

    vec3 a = dd.yzx + dd.zxy;
    vec3 b = od.yzx + od.zxy;
    vec3 c = oo.yzx + oo.zxy - ra2;
    vec3 h = b*b - a*c;

    // Edge X
    if( h.x>0.0 )
    {
        float ht = (-b.x - sqrt(h.x)) / a.x;
        if( ht>0.0 && ht<t && abs(ro.x+rd.x*ht)<size.x ) t = ht;
    }

    // Edge Y
    if( h.y>0.0 )
    {
        float ht = (-b.y - sqrt(h.y)) / a.y;
        if( ht>0.0 && ht<t && abs(ro.y+rd.y*ht)<size.y ) t = ht;
    }

    // Edge Z
    if( h.z>0.0 )
    {
        float ht = (-b.z - sqrt(h.z)) / a.z;
        if( ht>0.0 && ht<t && abs(ro.z+rd.z*ht)<size.z ) t = ht;
    }

    return t;
}

vec3 roundedboxNormal( in vec3 pos, in vec3 siz, in float rad )
{
    return sign(pos)*normalize(max(abs(pos)-siz,0.0));
}

mat3 getCameraMatrix(vec3 ro, vec3 lo)
{
    vec3 cw = normalize(lo - ro);
    vec3 cu = normalize(cross(cw, vec3(0, 1, 0)));
    vec3 cv = cross(cu, cw);

    return mat3(cu, cv, cw);
}

float safeacos(float x) { return acos(clamp(x, -1.0, 1.0)); }

float saturate(float x) { return clamp(x, 0., 1.); }
vec2 saturate(vec2 x) { return clamp(x, vec2(0), vec2(1)); }
vec3 saturate(vec3 x) { return clamp(x, vec3(0), vec3(1)); }

float sqr(float x) { return x*x; }
vec2 sqr(vec2 x) { return x*x; }
vec3 sqr(vec3 x) { return x*x; }

mat2 rot2D(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

// https://iquilezles.org/articles/smin/
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float smax( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }


// https://iquilezles.org/articles/palettes/
vec3 palette(float t)
{
    return 0.5 - 0.5 * cos(TAU * (vec3(0.8, 0.9, 0.9) * t + vec3(0.25, .5, 0)));
}

// Hash without Sine
// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

#define coprimes vec2(2,3)
vec2 halton (vec2 s)
{
  vec4 a = vec4(1,1,0,0);
  while (s.x > 0. && s.y > 0.)
  {
    a.xy = a.xy/coprimes;
    a.zw += a.xy*mod(vec2(s),coprimes);
    s = floor(s/coprimes);
  }
  return a.zw;
}

// Taken from https://www.shadertoy.com/view/MtVGWz
vec4 SampleTextureCatmullRom(sampler2D tex, vec2 texSize, vec2 uv)
{
    // We're going to sample a a 4x4 grid of texels surrounding the target UV coordinate. We'll do this by rounding
    // down the sample location to get the exact center of our "starting" texel. The starting texel will be at
    // location [1, 1] in the grid, where [0, 0] is the top left corner.
    vec2 samplePos = uv * texSize;
    vec2 texPos1 = floor(samplePos - 0.5) + 0.5;

    // Compute the fractional offset from our starting texel to our original sample location, which we'll
    // feed into the Catmull-Rom spline function to get our filter weights.
    vec2 f = samplePos - texPos1;

    // Compute the Catmull-Rom weights using the fractional offset that we calculated earlier.
    // These equations are pre-expanded based on our knowledge of where the texels will be located,
    // which lets us avoid having to evaluate a piece-wise function.
    vec2 w0 = f * ( -0.5 + f * (1.0 - 0.5*f));
    vec2 w1 = 1.0 + f * f * (-2.5 + 1.5*f);
    vec2 w2 = f * ( 0.5 + f * (2.0 - 1.5*f) );
    vec2 w3 = f * f * (-0.5 + 0.5 * f);

    // Work out weighting factors and sampling offsets that will let us use bilinear filtering to
    // simultaneously evaluate the middle 2 samples from the 4x4 grid.
    vec2 w12 = w1 + w2;
    vec2 offset12 = w2 / w12;

    // Compute the final UV coordinates we'll use for sampling the texture
    vec2 texPos0 = texPos1 - vec2(1.0);
    vec2 texPos3 = texPos1 + vec2(2.0);
    vec2 texPos12 = texPos1 + offset12;

    texPos0 /= texSize;
    texPos3 /= texSize;
    texPos12 /= texSize;

    vec4 result = vec4(0.0);
    result += texture(tex, vec2(texPos0.x,  texPos0.y)) * w0.x * w0.y;
    result += texture(tex, vec2(texPos12.x, texPos0.y)) * w12.x * w0.y;
    result += texture(tex, vec2(texPos3.x,  texPos0.y)) * w3.x * w0.y;

    result += texture(tex, vec2(texPos0.x,  texPos12.y)) * w0.x * w12.y;
    result += texture(tex, vec2(texPos12.x, texPos12.y)) * w12.x * w12.y;
    result += texture(tex, vec2(texPos3.x,  texPos12.y)) * w3.x * w12.y;

    result += texture(tex, vec2(texPos0.x,  texPos3.y)) * w0.x * w3.y;
    result += texture(tex, vec2(texPos12.x, texPos3.y)) * w12.x * w3.y;
    result += texture(tex, vec2(texPos3.x,  texPos3.y)) * w3.x * w3.y;

    return result;
}

vec2 getJitter(vec2 pos, int frame)
{
    pos = floor(pos);
    return halton(vec2(frame%8+1)) - .5;
}

vec3 getLookAtPos(float time)
{
    return vec3(0, 6, time * 2.0);
}

float getHeightMotion(vec2 p, float time, sampler2D tex, vec2 size)
{
    float r = texture(tex, 0.2 * p / size).y;
    return (saturate(sin((r + time * 0.5) * TAU)*0.8+0.5)-0.5)*0.2;
}

vec3 sRGBToLinear(vec3 col)
{
    return mix(pow((col + 0.055) / 1.055, vec3(2.4)), col / 12.92, lessThan(col, vec3(0.04045)));
}

vec3 linearTosRGB(vec3 col)
{
    return mix(1.055 * pow(col, vec3(1.0 / 2.4)) - 0.055, col * 12.92, lessThan(col, vec3(0.0031308)));
}

// ACES tone mapping curve fit to go from HDR to LDR
//https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x + b)) / (x*(c*x + d) + e), 0.0f, 1.0f);
}

