void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 sp = ivec2(fragCoord);

    if (sp.x >= 2 || sp.y > 0)
    {
        fragColor = vec4(0, 0, 0, 1);
        return;
    }

    vec3 ro = vec3(0, 0, 18);
    vec3 lo = getLookAtPos(iTime);

    vec2 m = iMouse.xy / iResolution.xy;

    float ax = iMouse.z < 0. || iMouse.x == 0. ? -PI * .7 + iTime * .2 : -m.x * TAU;
    float ay = iMouse.z < 0. || iMouse.y == 0. ? PI * 0.2 : -m.y * PI + PI * 0.5;

    ro.yz *= rot2D(ay);
    ro.xz *= rot2D(ax);
    ro += lo;

    if (sp.x == 0)
        fragColor = vec4(ro, 1);
    else if (sp.x == 1)
        fragColor = texelFetch(iChannel0, ivec2(0, 0), 0);
}

