// Fork of "Gelami Raymarching Template" by gelami. https://shadertoy.com/view/mslGRs
// 2023-05-01 11:08:18

float getHeight(vec2 p)
{
    float h = texture(iChannel0, 0.2 * p / iChannelResolution[0].xy).x;
    return smoothstep(0.3, 0.8, h)*0.8+0.1;
}

float map(vec2 p)
{
    float h = getHeight(p);

    #ifdef MOTION
    h += getHeightMotion(p, iTime, iChannel0, iChannelResolution[0].xy);
    #endif

    return clamp(h * MAX_HEIGHT, MIN_HEIGHT, MAX_HEIGHT);
}

// 
// https://www.shadertoy.com/view/7tKGRc
float randSpan( vec2 p )
{
    vec4 r = texelFetch(iChannel1, ivec2(mod(p, 256.0)), 0);
    #ifdef WARPING
    return (sin((iTime * 0.4 + r.x) * TAU)*0.5+0.5)*0.7+0.15;
    #else
    return r.x*0.7+0.15;
    #endif
}

// Returns size and offset position
vec4 getTile(vec2 id)
{
    bool ch = mod(id.x + id.y, 2.) > .5;

    // Get the random spans
    float r00 = randSpan(id + vec2(0, 0));
    float r10 = randSpan(id + vec2(1, 0));
    float r01 = randSpan(id + vec2(0, 1));
    float r11 = randSpan(id + vec2(1, 1));

    // Tile Size
    vec2 s0 = ch ? vec2(r00, r10) : vec2(r01, r00);
    vec2 s1 = ch ? vec2(r11, r01) : vec2(r10, r11);
    vec2 s = 1. - s0 + s1;

    return vec4(s * 0.5, s0);
}

vec2 getTileID(vec2 p)
{
    vec2 fl = floor(p);
    vec2 fr = p - fl;

    bool ch = mod(fl.x + fl.y, 2.) > .5;

    float r1 = randSpan(fl);
    vec2 ax = ch ? fr.xy : fr.yx;

    float a1 = ax.x - r1;
    float si = sign(a1);
    vec2 o1 = ch ? vec2(si, 0) : vec2(0, si);

    float r2 = randSpan(fl + o1);
    float a2 = ax.y - r2;

    vec2 st = step(vec2(0), vec2(a1, a2));

    // Tile ID
    vec2 of = ch ? st.xy : st.yx;
    vec2 id = fl + of - 1.;

    return floor(id);
}

bool trace(vec3 ro, vec3 rd, out float t, out vec2 tp, out vec3 normal)
{
    if ((ro.y > MAX_HEIGHT && rd.y > 0.0) || (ro.y < MIN_HEIGHT && rd.y > 0.0))
        return false;

    tp = getTileID(ro.xz);

    //if (ro.y < map(tp))
    //    return false;

    vec3 ird = 1.0 / rd;
    vec3 srd = sign(rd);
    vec3 ard = abs(ird);

    vec3 iro = ro * ird;

    t = MAX_DIST;
    normal = vec3(-1);

    int i = 0;
    for (; i < STEPS; i++)
    {
        float d = map(tp) * 0.5;

        vec4 tsp = getTile(tp);

        vec3 p = vec3(tp.x + tsp.z + tsp.x, d, tp.y + tsp.w + tsp.y);
        vec3 s = vec3(tsp.x, d, tsp.y);
        vec3 n = iro - p * ird;
        vec3 k = ard * s;

        vec3 t1 = -n - k;
        vec3 t2 = -n + k;

        float tN = max(max(t1.x, t1.y), t1.z);
        float tF = min(min(t2.x, t2.y), t2.z);

        vec3 pos = ro + rd * (tN + EPS);

        if (pos.y > MAX_HEIGHT && rd.y > 0.0)
            return false;

        if (tN < tF && tN >= 0.0)
        {
            if (tN > MAX_DIST)
                return false;

            #ifndef ROUNDED
            t = tN;
            normal = -srd * step(t1.yzx, t1) * step(t1.zxy, t1);
            return true;

            #else

            const float rad = RADIUS;

            if (d < MIN_HEIGHT + rad)
            {
                t = tN;
                normal = -srd * step(t1.yzx, t1) * step(t1.zxy, t1);
                return true;
            }

            vec3 o = p;
            vec3 rp = ro - o;
            vec3 rs = max(s-rad, vec3(0));
            float rt = roundedboxIntersect(pos - o, rp, rd, rs, rad);
            if (rt != MAX_DIST)
            {
                t = rt == 0.0 ? tN : rt;
                vec3 pos = rp + rd * t;
                normal = normalize(roundedboxNormal(pos, rs, rad));
                return true;
            }
            #endif
        }

        float dt;
        vec2 st;
        if (t2.x <= t2.z)
        {
            dt = t2.x;
            st = vec2(1, 0);
        } else
        {
            dt = t2.z;
            st = vec2(0, 1);
        }

        // Hacky way to get next tile ID v_v
        // Ideally you would only check the neighboring span
        // to pick which one from the two neighbors to advance to
        //tp = getTileID(ro.xz + rd.xz * (min(t2.x, t2.z) + 1e-3) + step(t2.xz, t2.zx) * srd.xz * 5e-3);
        tp = getTileID(ro.xz + rd.xz * (dt + 1e-3) + st * srd.xz * 5e-3);
        //tp += st * srd.xz;
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o = getJitter(fragCoord, iFrame);
    vec2 ndc = (2. * (fragCoord + o) - iResolution.xy) / iResolution.y;

    vec2 uv = fragCoord / iResolution.xy;

    vec3 ro = texelFetch(iChannel3, ivec2(0, 0), 0).rgb;
    vec3 lo = getLookAtPos(iTime);

    mat3 cmat = getCameraMatrix(ro, lo);

    vec3 rd = normalize(cmat * vec3(ndc, 2));

    vec3 col = vec3(0);

    float t;
    vec2 tp;
    vec3 n;
    bool hit = trace(ro, rd, t, tp, n);

    if (!hit) t = MAX_DIST;

    vec3 pos = ro + rd * t;

    vec3 ldir = normalize(vec3(1, 2, 0.9));

    float lt;
    vec2 ltp;
    vec3 ln;
    bool lhit = trace(pos + n * EPS, ldir, lt, ltp, ln);

    float dif = max(dot(n, ldir), 0.0) * float(!lhit);
    float bou = max(dot(-reflect(n, rd), ldir), 0.0);

    float r = getHeight(tp);

    col = palette(hash12(tp));
    col *= vec3(1, 0.45, 0.08);

    float h = map(tp);

    vec3 bcol = vec3(1.2, 1, 0.8);
    vec2 br = hash22(-tp.yx);
    if (n.y == 0.0 && br.x < 0.1)
    {
        if (br.y < 0.5)
        {
            col = mix(col, bcol, step(fract(0.5 + (1.0-pos.y / h) * 1.2*ceil(r * MAX_HEIGHT)), 0.5));
        } else
        {
            vec4 tsp = getTile(tp);
            vec2 ww = 0.5 + 0.4999 * (pos.xz - tp - tsp.zw - tsp.xy) / tsp.xy;
            ww = step(fract(ww * 2.0 - 0.25), vec2(0.5));

            col = mix(col, bcol, abs(n.x) > 0.5 ? ww.y : ww.x);
        }
    }

    col *= dot(abs(n), vec3(0.8, 1, 0.9));
    col *= smoothstep(MIN_HEIGHT, MAX_HEIGHT*0.8, pos.y)*0.75+0.25;

    col *= dif + 0.15 + bou*0.15;

    vec3 ref = textureLod(iChannel2, reflect(rd, n), 2.0).rgb * palette(hash12(tp.yx));
    ref *= vec3(1, 0.45, 0.08);

    float r0 = 0.05;
    float fre = r0 + (1.0 - r0) * pow(1.0 - max(dot(-rd, n), 0.0), 5.0);

    col = mix(col, ref, fre * 0.8);

    vec3 sky = vec3(0.9, 0.6, 1);

    float fog = 1.0 - exp(-max(t - 30.0, 0.0) * 0.008);
    col = mix(col, vec3(0.9, 0.6, 1), fog);

    col = mix(col, sky, smoothstep(MAX_DIST*0.9, MAX_DIST, t));
    //if (t >= MAX_DIST)
    //    col = rd;

    col = max(col, vec3(0));
    col = col / (1. + col);

    fragColor = vec4(col, t);
}

