// TAA implementation from
// Temporal AA Study - gelami
// https://www.shadertoy.com/view/DsfGWX

#if 0
#define OFFSET_COUNT 8

const ivec2 off[OFFSET_COUNT] = ivec2[OFFSET_COUNT](
    ivec2(-1, -1), ivec2(-1,  1), 
	ivec2( 1, -1), ivec2( 1,  1), 
	ivec2( 1,  0), ivec2( 0, -1), 
	ivec2( 0,  1), ivec2(-1,  0)
);

#else
#define OFFSET_COUNT 4

const ivec2 off[OFFSET_COUNT] = ivec2[OFFSET_COUNT](
 	ivec2( 1,  0), ivec2( 0, -1), 
	ivec2( 0,  1), ivec2(-1,  0)
);
#endif

vec3 RGBtoYCoCg(vec3 c)
{
    return mat3(0.25, 0.5, -0.25, 0.5, 0, 0.5, 0.25, -0.5, -0.25) * c;
}

vec3 YCoCgToRGB(vec3 c)
{
    return mat3(1, 1, 1, 1, 0, -1, -1, 1, -1) * c;
}

// From https://github.com/playdeadgames/temporal/blob/master/Assets/Shaders/TemporalReprojection.shader
vec3 clipToAABB(in vec3 cOld, in vec3 cNew, in vec3 center, in vec3 halfSize)
{
// Clip towards AABB center
#if 0
    vec3 o = cOld - center;
    vec3 v = o / halfSize;
    vec3 a = abs(v);
    float t = max(a.x, max(a.y, a.z));

    if (t > 1.)
        return cNew + o / t;
    else
        return cOld;
#else
    vec3 r = cOld - cNew;
    vec3 m = (center + halfSize) - cNew;
    vec3 n = (center - halfSize) - cNew;

    if (r.x > m.x + EPS)
		r *= (m.x / r.x);
	if (r.y > m.y + EPS)
		r *= (m.y / r.y);
	if (r.z > m.z + EPS)
		r *= (m.z / r.z);

	if (r.x < n.x - EPS)
		r *= (n.x / r.x);
	if (r.y < n.y - EPS)
		r *= (n.y / r.y);
	if (r.z < n.z - EPS)
		r *= (n.z / r.z);

	return cNew + r;
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = .1;

    ivec2 sp = ivec2(fragCoord);

    vec2 o = getJitter(fragCoord, iFrame);

    vec2 ndc = (2. * (fragCoord) - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    vec4 curr = texture(iChannel0, (fragCoord - o) / iResolution.xy);

    vec3 new = RGBtoYCoCg(curr.rgb);

    vec3 ro = texelFetch(iChannel2, ivec2(0, 0), 0).rgb;
    vec3 pro = texelFetch(iChannel2, ivec2(1, 0), 0).rgb;

    vec3 lo = getLookAtPos(iTime);
    vec3 plo = getLookAtPos(iTime - iTimeDelta);

    mat3 cmat = getCameraMatrix(ro, lo);
    mat3 pmat = getCameraMatrix(pro, plo);

    vec3 rd = normalize(cmat * vec3(ndc, 2));

    vec3 p = ro + rd * curr.a;

    #ifdef MOTION
    vec2 tp = floor(p.xz);
    float h0 = getHeightMotion(tp, iTime, iChannel3, iChannelResolution[3].xy);
    float h1 = getHeightMotion(tp, iTime - iTimeDelta, iChannel3, iChannelResolution[3].xy);

    p.y += (h1 - h0);
    #endif

    vec3 cp = (p - pro) * pmat;
    vec2 np = 2. * cp.xy / cp.z;
    vec2 puv = np * vec2(iResolution.y / iResolution.x, 1) * .5 + .5;

    //vec4 hist = texture(iChannel1, puv);
    vec4 hist = SampleTextureCatmullRom(iChannel1, iResolution.xy, puv);

    vec3 old = RGBtoYCoCg(hist.rgb);

    vec3 avg = new;
    vec3 var = new * new;

    for (int i = 0; i < OFFSET_COUNT; i++)
    {
        vec3 tex = RGBtoYCoCg(texelFetch(iChannel0, sp + off[i], 0).rgb);

        avg += tex;
        var += tex * tex;
    }
    avg /= float(OFFSET_COUNT + 1);
    var /= float(OFFSET_COUNT + 1);

    vec3 sig = sqrt(max(var - avg * avg, vec3(0)));

    const float g = 1.0;
    vec3 cmin = avg - sig * g;
    vec3 cmax = avg + sig * g;

    vec3 clip = clipToAABB(old, clamp(avg, cmin, cmax), avg, sig);

    vec3 col = iFrame != 0 ? mix(clip, new, a) : new;

    fragColor = vec4(saturate(YCoCgToRGB(col)), curr.a);
}

