// View Options
#define BIRD_AMOUNT    3.0
#define BIRD_SCALE     0.35
#define SCROLL_SPEED   0.2
#define FLOOR_SCROLL   false
#define SEED           124
#define SATURATION     0.85

// SDF Options
#define LINE_SOFTNESS  0.005
#define LINE_THICKNESS 0.015

// Base Colors
#define BG_COLOR       vec3(0.459, 0.878, 0.808)
#define GLOW_COLOR     vec3(1.000, 1.000, 1.000)
#define OUTLINE_COLOR  vec3(0.132, 0.045, 0.016)
#define BIRD0_COLOR    vec3(0.84, 0.48, 0.53)
#define BIRD1_COLOR    vec3(0.19, 0.95, 0.85)
#define BIRD2_COLOR    vec3(0.91, 0.85, 0.47)
#define EYEBASE_COLOR  vec3(0.934, 0.882, 0.753)
#define EYE_COLOR      vec3(0.830, 0.618, 0.074)

// Bird Variation
float _HeadRoundness;
float _BodyVariation;
float _TailVariation;

// Noise https://blog.demofox.org/2022/01/01/interleaved-gradient-noise-a-different-kind-of-low-discrepancy-sequence/
float IGN(int pixelX, int pixelY)
{
    return mod(52.9829189f * mod(0.06711056*float(pixelX) + 0.00583715*float(pixelY), 1.0), 1.0);
}

// https://www.shadertoy.com/view/WttXWX
float Hash(float f)
{
    uint x = uint(f)+uint(SEED);
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return float(x) / float( 0xffffffffU );
}

// HSV conversion https://www.shadertoy.com/view/XljGzV

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


// Rotation
vec2 Rotate2D(float angle, vec2 original)
{
    float c = cos(angle);
    float s = sin(angle);
    return original * mat2(c, -s, s, c);
}

// SDF https://iquilezles.org/articles/distfunctions2d/
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdUnevenCapsule( vec2 p, float r1, float r2, float h )
{
    p.x = abs(p.x);
    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(p,vec2(-b,a));
    if( k < 0.0 ) return length(p) - r1;
    if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
    return dot(p, vec2(a,b) ) - r1;
}

float sdEllipse( in vec2 p, in vec2 ab )
{
    p = abs(p); if( p.x > p.y ) {p=p.yx;ab=ab.yx;}
    float l = ab.y*ab.y - ab.x*ab.x;
    float m = ab.x*p.x/l;      float m2 = m*m; 
    float n = ab.y*p.y/l;      float n2 = n*n; 
    float c = (m2+n2-1.0)/3.0; float c3 = c*c*c;
    float q = c3 + m2*n2*2.0;
    float d = c3 + m2*n2;
    float g = m + m*n2;
    float co;
    if( d<0.0 )
    {
        float h = acos(q/c3)/3.0;
        float s = cos(h);
        float t = sin(h)*sqrt(3.0);
        float rx = sqrt( -c*(s + t + 2.0) + m2 );
        float ry = sqrt( -c*(s - t + 2.0) + m2 );
        co = (ry+sign(l)*rx+abs(g)/(rx*ry)- m)/2.0;
    }
    else
    {
        float h = 2.0*m*n*sqrt( d );
        float s = sign(q+h)*pow(abs(q+h), 1.0/3.0);
        float u = sign(q-h)*pow(abs(q-h), 1.0/3.0);
        float rx = -s - u - c*4.0 + 2.0*m2;
        float ry = (s - u)*sqrt(3.0);
        float rm = sqrt( rx*rx + ry*ry );
        co = (ry/sqrt(rm-rx)+2.0*g/rm-m)/2.0;
    }
    vec2 r = ab * vec2(co, sqrt(1.0-co*co));
    return length(r-p) * sign(p.y-r.y);
}

// Bird Drawing
float SDFOval(vec2 uv, float r, float angle)
{
    float sdf = sdEllipse(Rotate2D(angle, uv), vec2(r*(1.7 + 0.2 * _BodyVariation),r));
    return sdf;
}

float SDFLine(vec2 uv, float l, float angle)
{
    float sdf = sdBox(Rotate2D(angle, uv), vec2(l,0.018));
    return sdf;
}

float SDFFlatTriangle(vec2 uv, float l, float angle)
{
    float sdf = sdUnevenCapsule(Rotate2D(angle, uv), 0.015, 0.01, l);
    return sdf;
}

float SDFFoot(vec2 uv)
{
    uv.y += 0.03 + 0.01 * _BodyVariation;
    uv.x += 0.02 * _BodyVariation;
    float toeAdd = 0.01 * _BodyVariation;
    float legsdf = SDFLine(vec2((uv.x - 0.055), (uv.y+0.28)), 0.12, -0.8);
    float footl1sdf = SDFFlatTriangle(vec2((uv.x+0.02), (uv.y+0.365)), 0.12 + toeAdd, 4.6);
    float footl2sdf = SDFFlatTriangle(vec2((uv.x+0.01), (uv.y+0.365)), 0.1 + toeAdd, 4.33);
    float footl3sdf = SDFFlatTriangle(vec2((uv.x+0.02), (uv.y+0.36)), 0.1 + toeAdd, 4.8);
    float footl4sdf = SDFFlatTriangle(vec2((uv.x+0.025), (uv.y+0.365)), 0.08 + toeAdd, 1.57);
    float sdf = min(legsdf, footl1sdf);
    sdf = min(sdf, footl2sdf);
    sdf = min(sdf, footl3sdf);
    sdf = min(sdf, footl4sdf);
    return sdf;
}

float SDFHead(vec2 uv, float r, float angle)
{
    float roundsdf = sdEllipse(Rotate2D(angle, uv), vec2(r*(1.8 + 0.2 * _BodyVariation),r));
    uv.x -= 0.03 + 0.05*(1.0 - _HeadRoundness);
    float sharpersdf = sdUnevenCapsule(Rotate2D(angle - 1.6, uv), 
                        0.17, 
                        0.03 + 0.05 * _HeadRoundness, 
                        r*(1.9 + 0.2 * _BodyVariation) + 0.05 * (1.0 - _HeadRoundness));
    uv.x += 0.04;
    uv.y -= 0.05;
    float sdf = mix(sharpersdf, sdCircle(uv, 0.22), 0.15);
    sdf = mix(sdf, roundsdf, _HeadRoundness);
    return sdf;
}

void SDFEye(vec2 uv, vec3 eyecolor, inout vec3 col)
{
    float eyeadj = 0.01 * (1.0 - _HeadRoundness);
    uv.x += 0.15 + 0.02 * _BodyVariation;
    uv.y -= 0.14;
    float sdf = sdEllipse(uv, vec2(0.1 - eyeadj,0.12- eyeadj));
    float eyeoutlineblend = smoothstep(0.0,LINE_SOFTNESS * 3.5, -sdf);
    float eyeblend = smoothstep(0.0,LINE_SOFTNESS * 3.5, -(sdf + LINE_THICKNESS));

    uv.x += 0.03;
    float pupilsdf = sdEllipse(uv, vec2(0.065- eyeadj,0.09- eyeadj));
    float pupiloutlineblend = smoothstep(0.0,LINE_SOFTNESS, -pupilsdf);
    float pupilblend = smoothstep(0.0, LINE_SOFTNESS * 3.5, -sdEllipse(uv, vec2(0.028- eyeadj/2.0,0.05- eyeadj)));

    col = mix(col, OUTLINE_COLOR.rgb, eyeoutlineblend);
    col = mix(col, EYEBASE_COLOR.rgb, eyeblend);
    col = mix(col, OUTLINE_COLOR.rgb, pupiloutlineblend);
    col = mix(col, eyecolor, pupilblend);
    uv -= 0.05 - 0.01 * (1.0 - _HeadRoundness);
    col = mix(col, GLOW_COLOR.rgb, smoothstep(0.0, LINE_SOFTNESS * 3.5, -sdCircle(uv, 0.022)));
}

void SDFBird(vec2 uv, float cell, inout vec3 col)
{
    // Random bird variations
    _HeadRoundness = Hash(cell * 78523.0);
    _BodyVariation = (Hash(cell * 34443.0) - 0.5) * 2.0;
    _TailVariation = (Hash(cell * 43523.0) - 0.5) * 2.0;

    // Select Color and add variation
    float colorChoice = mod(floor(Hash(cell * 12516.0) * 3.0),3.0);
    vec3 birdColor = float(colorChoice == 0.0) * BIRD0_COLOR + float(colorChoice == 1.0) * BIRD1_COLOR + float(colorChoice == 2.0) * BIRD2_COLOR;
    vec3 eyecolor = EYE_COLOR;

    birdColor = rgb2hsv(birdColor);
    birdColor.x += mix(-0.1, 0.1, Hash(cell * 1837.0));
    birdColor.y += mix(-0.07, 0.0, Hash(cell * 8547.0));
    birdColor.z += mix(-0.1, 0.05, Hash(cell * 2253.0));
    birdColor = hsv2rgb(birdColor);

    eyecolor = rgb2hsv(eyecolor);
    eyecolor.x += mix(-0.05, 0.2, Hash(cell * 3242.0));
    eyecolor.y += mix(-0.3, 0.0, Hash(cell * 5643.0));
    eyecolor.z += mix(0.0, 0.3, Hash(cell * 7545.0));
    eyecolor = hsv2rgb(eyecolor);

    // Random Flip
    float flip = float(!FLOOR_SCROLL) * Hash(cell * 15632.0) + float(FLOOR_SCROLL);
    uv.x = float(flip>0.5)*-uv.x + float(flip<0.5)*uv.x;

    // Tweaks for placement
    float scale = 1.35 + 2.0 * (1.0 - BIRD_SCALE);
    uv *= scale;
    uv.x += 0.11;
    uv.y -= 0.1;

    // Draw Legs
    float leg1sdf = SDFFoot(uv);
    float leg2sdf = SDFFoot(vec2(uv.x - 0.08, uv.y + 0.05));
    float legsdf = min(leg2sdf, leg1sdf);

    // Random Rotation of main body
    uv = Rotate2D(mix(-0.18, 0.1, Hash(cell * 898.0)), uv);

    // Draw Head, Body, Wing, and Tail
    float headsdf = SDFHead(vec2(uv.x+0.18, uv.y-0.12), 0.16, -0.1);
    float bodysdf = SDFOval(vec2(uv.x-0.13- 0.03 * _BodyVariation, uv.y+0.1), 0.2, 0.1 - 0.05 * _BodyVariation);
    float rwingsdf = SDFOval(vec2(uv.x-0.25, uv.y+0.09), 0.2, 0.23);
    float tailsdf = SDFOval(vec2(uv.x-0.45 - 0.05 * _TailVariation, uv.y+0.12- 0.02 * _TailVariation), 0.2, 1.0 + 0.5 * _TailVariation);
    float rbacksdf = SDFOval(vec2((uv.x-0.56), (uv.y-0.45)), 0.6, 0.9);
    float sdf = min(headsdf, bodysdf);
    sdf = min(sdf, tailsdf);
    sdf = max(sdf, -rwingsdf);
    sdf = min(sdf, rwingsdf + LINE_THICKNESS - LINE_THICKNESS * mix(0.0, 0.5, LINE_SOFTNESS * 200.0));
    sdf = max(sdf, -rbacksdf);

    // Scale adjustment to renormalize scale from tweaks
    sdf /= scale;

    // Get blend values for coloring
    float birdbodyblend = smoothstep(0.0,LINE_SOFTNESS, -sdf);
    float outlineblend = smoothstep(0.0,LINE_SOFTNESS, -(sdf - LINE_THICKNESS));
    sdf = min(sdf- LINE_THICKNESS, legsdf);
    float glowlineblend = smoothstep(0.0,LINE_SOFTNESS, -(sdf - LINE_THICKNESS));
    float legblend = smoothstep(0.0,LINE_SOFTNESS * 2.0, -legsdf);

    // Draw Mouth 
    float mouthsdf = SDFFlatTriangle(
                        vec2((uv.x+0.34 + 0.03 * _BodyVariation + 0.08 * (1.1 - _HeadRoundness)), 
                        (uv.y+0.015 - 0.06 * (1.0 - _HeadRoundness))), 
                        0.06 + 0.04 * (1.0 - _HeadRoundness), 
                        0.6 + 0.65 * (1.0 - _HeadRoundness));
    float mouthblend = smoothstep(0.0,LINE_SOFTNESS, -mouthsdf);

    // Draw Beak 
    float beaksdf = sdEllipse(Rotate2D(-0.7,
                        vec2(uv.x+0.52 + 0.02 * _BodyVariation+0.03 * (1.0 - _HeadRoundness), 
                              uv.y)), 
                        vec2(0.288, 0.19));
    beaksdf = max(beaksdf, sdf + LINE_THICKNESS);
    float beakblend = smoothstep(0.0,LINE_SOFTNESS, -beaksdf);

    // Slight shade warmer or cool depending on color
    float shade = min(1.0, uv.y + 0.8);
    float iscooler = float((birdColor.r - 0.3) <=birdColor.b);
    float iswarmer = 1.0 - iscooler;
    birdColor.rg = iscooler * mix(birdColor.rg/2.0, birdColor.rg, shade) + iswarmer * birdColor.rg;
    birdColor.gb = iswarmer * mix(birdColor.gb/2.0, birdColor.gb, shade) + iscooler * birdColor.gb;

    // Add colors
    col = mix(col, GLOW_COLOR.rgb, glowlineblend);
    col = mix(col, OUTLINE_COLOR.rgb, legblend);
    col = mix(col, OUTLINE_COLOR.rgb, outlineblend);
    col = mix(col, birdColor.rgb, birdbodyblend);
    // Add colors for beak
    col.rg *= beakblend * 0.65 + (1.0 - beakblend);
    col.b *= beakblend * 0.85 + (1.0 - beakblend);
    col = mix(col, OUTLINE_COLOR.rgb, mouthblend);
    // Add eye wholesale
    SDFEye(uv, eyecolor, col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv.x -= (iResolution.x-iResolution.y)/iResolution.y/2.0;

    vec3 col = BG_COLOR;

    // 100 times different birds
    float scale = 100.0;
    float time = mod(iTime * SCROLL_SPEED, scale);

    time = floor(time) * float(FLOOR_SCROLL) + time * float(!FLOOR_SCROLL);
    uv += time;
    vec2 scaleduv = uv*scale;

    // Scale back to specified amount
    scaleduv *= BIRD_AMOUNT/scale;

    // Offset even rows
    float evenrow = float(mod(scaleduv.y,2.0) > 1.0);
    scaleduv.x += evenrow * 0.5;

    // Get random value for each cell
    float cell = IGN(int(mod(scaleduv.x,scale)),int(mod(scaleduv.y,scale)));
    uv = (fract(scaleduv) - 0.5);
    SDFBird(uv, cell, col);

    col = rgb2hsv(col);
    col.y *= SATURATION;
    col = hsv2rgb(col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// generate a simple quantized colour cycle grid (12 colour steps)
// used to generate a character-map grid for the Sega Genesis Version of A Blast From the Past.
// 320x224 pixels packs down to 96 characters due to flipx/flipy (3072 bytes total)
// https://store.steampowered.com/app/1751970/A_Blast_From_The_Past/
// https://www.youtube.com/watch?v=LUbrzg21X9c - The lost art of color cycling - Animating with color

float Quant(float cc,float steps)
{
    return floor(steps*cc)/steps;           // pure quantization
}

vec2 CycleGrid(vec2 uv,float colorsteps)
{
	float fade = (abs(uv.y)-.1)/.9;
    fade = 1.0-step(fade,0.085);
	vec2 v = vec2(uv.x*abs(1.0/uv.y),abs(1.0/uv.y));
    vec2 fs =  fract(v+0.5)-0.5;
    float e = length(vec2(abs(fs.x)-0.5,0.0));

    float lines = step(e,.05);
    float fill = fs.y+0.5;
    if (iMouse.z<0.5)
        fill+=Quant(fract(iTime*0.5),colorsteps);// Cycle colours
    fill = Quant(fract(fill),colorsteps);
    return vec2(lines,(fill+0.1)) * fade;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Pixel coordinates
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec2 dat = CycleGrid(uv , 12.0);  // get grid with 12 colour steps
    vec3 col = vec3(dat.y,0.0,0.0);
	col.rgb += dat.x;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

