//  Saw this tweet: https://twitter.com/junkiyoshi/status/1652996530146787329?s=20
//  Thought I could do something like.
//  It's not quite good enough but good enough to share
#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

float df(vec2 p, float tm) {
  p *= ROT(-tm*0.5);
  float d = -p.y;
  return abs(d)-0.003;
}

vec3 bubbles(vec3 col, vec2 p) {
  float aa = 4.0/RESOLUTION.y;
  for (float i = 1.0; i < 6.0; ++i) {
    float sz = 0.3/(1.0+i);
    vec2 pp = p;
    pp /= sz;
    vec2 rp = round(pp);
    vec2 cp = pp;
    cp -= rp;
    float dt = 0.001;
    float dp = df(rp*sz, TIME-dt);
    float dn = df(rp*sz, TIME);
    float dd = (dn-dp)/dt;
    float h0 = hash(rp);
    float h1 = fract(3667.0*h0);
    float h2 = fract(8667.0*h0);
    float r  = 0.33*sqrt(h0);
    r *= smoothstep(0.1, 0.9, length(p));
//    r *= tanh(1.0*dd);
    cp -= (0.5-r)*vec2(h1, h2);
    float fo = smoothstep(12.0*sz*r, -0.1, dn);
    float d = (length(cp)-mix(r, 0., fo*fo*fo));
    d = abs(d);
    d *= sz;
    d -= 0.003;
    vec3 bcol = vec3(sqrt(fo))*smoothstep(0.0, -aa, d)*step(0.0, dd)*smoothstep(-0.05, 0.1, dn);
    col += bcol;
  }
  return col;
}

vec3 effect(vec2 p, vec2 pp) {
  float ds = df(p, TIME);
  vec3 col = vec3(0.0);
  col = bubbles(col, p);
  float aa = 4.0/RESOLUTION.y;
  col = mix(col, vec3(1.0, 0.0, 0.25), smoothstep(0.0, -aa, ds));
  col = sqrt(col);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp);
  fragColor = vec4(col, 1.0);
}

float rad = 3.0;
float distanceFade = 6.0;
float timeScale = 1.0;
float depthScale = 0.25;
float widthAdjustmentAmount = 0.5;
vec4 col1 = vec4(145, 215, 227,255) / 255.0;
vec4 col2 = vec4(36, 39, 58, 255) / 255.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2.0 * (fragCoord.xy / iResolution.xy - 0.5) * vec2(iResolution.x / iResolution.y, 1.0);
    vec3 position = vec3(uv + vec2(cos(iTime * timeScale), sin(iTime * timeScale)), 0);
    vec3 dir = normalize(vec3(uv, 1.0));
    for(int i = 0; i < 256; i++) {
        float dist = rad - distance(position.xy, vec2(0));
        fragColor = vec4((1.0 - 1.0 / ((position.z + distanceFade) / distanceFade)) * col1 + (1.0 / ((position.z + distanceFade) / distanceFade)) * col2);
        if(dist < 0.01) break;
        position += dist * normalize(dir + vec3(widthAdjustmentAmount * vec2(cos(iTime * timeScale + position.z * depthScale), sin(iTime * timeScale + position.z * depthScale)), 0));
    }
}

float rectSDF(vec2 uv, vec2 s) {
    return max(abs(uv.x / s.x), abs(uv.y / s.y));
}
float fill(float x, float s) {
