mat2 rot(float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 p){return fract(sin(dot(p, vec2(27.609,47.983)))*43758.5453);}
float lsp(float b, float e, float t) { return clamp((t - b) / (e - b), 0.0, 1.0); }

//@iq shapes
float box( vec3 p, vec3 s ) {
    p = abs(p)-s;
    return length(max(p,0.))+min(max(p.x, max(p.y, p.z)),0.)-.025;
}
float box( vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
    return length(max(d,0.)) + min(max(d.x,d.y),0.);
}
float cap( vec3 p, float r, float h ) {
    vec2 d = abs(vec2(length(p.xy),p.z))-vec2(h,r);
    return min(max(d.x,d.y),0.)+length(max(d,0.));
}
float vcap( vec3 p, float h, float r ) {
    p.y -= clamp(p.y,0.,h);
    return length(p) - r;
}
float trs( vec3 p,vec2 t){
    vec2 q = vec2(length(p.zx)-t.x,p.y);
    return length(q)-t.y;
}

const float rep = 14.;
const float hrep = rep/2.;
const float angle = 2.*PI/rep;
const float angfl = angle/2.;

float polar(inout vec2 p)  {
    float a = atan(p.y, p.x) + angfl;
    float c = floor(a/angle);
    a = mod(a,angle) - angfl;
    p = vec2(cos(a), sin(a))*length(p);
    return (abs(c) >= (hrep)) ? abs(c) : c;
} 

float column(vec3 q) {
    q.y += 1.4;
    vec3 p = vec3(q.x,abs(q.y)-2.35,q.z);
    float b = cap(q.xzy,2.,1.1);
    float c = box(p,vec3(1.35,.25,1.35))-.005;
    polar(q.xz);
    float g = vcap(q-vec3(1.1,-1.25,0),2.5,.175);
    return min(c,max(b,-g));
}

vec3 hit=vec3(0),hitPoint=vec3(0),gid=vec3(0),sid=vec3(0),speed=vec3(0);
mat2 r45,r25;

const float size = 5.;
const float hlf = size/2.;
const vec3  grid = vec3(1,1,1);

vec2 map(in vec3 p) {
    vec2 res = vec2(1e5,0.);
    p += speed;

    vec2 id = floor((p.xz+hlf)/size);
    vec2 q = mod(p.xz+hlf,size)-hlf;

    vec3 q2 = p+vec3(hlf,0,hlf);
    vec2 ip = floor((q2.xz+hlf)/size);
    q2.xz = mod(q2.xz+hlf,size)-hlf;

    float chk = mod(ip.y + ip.x,2.) * 2. - 1.;
    float hs = hash21(id);
    if(hs>.5) q.x*=-1.;

    vec2 d2 = vec2(length(q-hlf), length(q+hlf));
    vec2 gx = d2.x<d2.y ? vec2(q-hlf) : vec2(q+hlf);

    vec3 r = vec3(gx.x,p.y,gx.y);
    vec3 t = vec3(q.x,p.y,q.y);
    float tk = .35+.15*sin(p.x*.4+T*2.94);

    float d3 = box(r,vec3(size));
    float d4 = trs(r,vec2(hlf,tk));

    if(hs>.75) d4 = min(length(t.yz)-tk,length(t.yx)-tk);

    if(hs>.075 && hs<.1) d4 = min(length(vec3(abs(t.x)-hlf,t.yz))-tk,length(vec3(t.yx,abs(t.z)-hlf))-tk);

    if(hs<.075) d4 = min(length(t.yx)-tk, length(vec3(abs(t.x)-hlf,t.yz))-tk);

    d3=max(d3,d4);

    if(d3<res.x){
        float rs=fract(hs*432.32);
        res = vec2(d3,rs<.65?4.:3.);
        hit = p; 
        gid = vec3(id,1);
    }

    float rs=hash21(ip*432.32);
    float cl = column(q2-vec3(0,2,0));
    if(cl<res.x &&chk>.5 && rs>.5){
        res = vec2(cl,5.);
        hit = t;
        gid=vec3(ip.xyx);
    }


    float fl = p.y+2.;
    if(fl<res.x){
        res = vec2(fl,2.);
        hit = p;
    }
    return res;
}

vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h = vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec2 marcher(vec3 ro, vec3 rd, int steps) {
    float d = 0., m = 0.;
    for(int i=0;i<steps;i++){
        vec2 ray = map(ro + rd * d);
        if(ray.x<MIN_DIST*d||d>MAX_DIST) break;
        d += i<32?ray.x*.4:ray.x;
        m  = ray.y;
    }
    return vec2(d,m);
}

vec3 hue(float t) {
    return .45 + .35*cos(PI2*t*(vec3(.12,.47,.92)+vec3(.88,.97,.85))); 
}

vec4 FC = vec4(.65,.75,.75,0);
vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, bool last, inout float d, vec2 uv) {

    vec3 C = vec3(0);
    vec2 ray = marcher(ro,rd,110);
    hitPoint = hit;  
    sid = gid;
    d = ray.x;
    float m = ray.y;
    float alpha = 0.;

    if(d<MAX_DIST)
    {
        vec3 p = ro + rd * d;
        vec3 n = normal(p,d);
        vec3 lpos =vec3(-5,20,-15);
        vec3 l = normalize(lpos-p);

        vec3 h = vec3(.05);
        vec3 hp = hitPoint*vec3(-1.,1,1);

        float diff = clamp(dot(n,l),.1,.95);

        float shdw = 1.;
        for( float t = .01; t < 18.; ) {
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) { shdw = 0.; break; }
            shdw = min(shdw, 25.*h/t);
            t += h;
            if( shdw < MIN_DIST || t > 25. ) break;
        }

        diff = mix(diff,diff*shdw,.35);

        // materials
        if(m==2.){
            h = vec3(.25);//hue((311.-hp.x)*.005);

            vec2 k = fract(hp.xz*.1)-.5;
            vec2 f = fract(hp.xz*1.)-.5;
            if(k.x*k.y>0.) f = k;

            if(f.x*f.y>0.) {
                h=vec3(.1); 
                ref = h;
            }
        }
        if(m==3.){
            h = hue((321.-hp.x)*.005);
            ref = h*.35;
        }
        if(m==4.){
            h = vec3(.05);
            ref = vec3(.15);
        }
        if(m==5.){
            h = vec3(.95);
            h = hue( (sid.x*.02)+(sid.y*.05)+(hp.y*.01) );
            ref = vec3(0);
        }

        C = (diff*h);
        C = mix(FC.rgb,C,exp(-.0000095*d*d*d));

        ro = p+n*.0001;
        rd = reflect(rd,n);
    } 
    return vec4(C,alpha);
}

void mainImage( out vec4 O, in vec2 F )
{   
    r45=rot(-.685);
    mat2 r25=rot(-.685+.1*sin(T*.25));
    speed = vec3(T*.75,0,0);
    float zoom = 15.;
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(uv*zoom,-(zoom+10.));
    vec3 rd = vec3(0.,0.,1.);

    ro.yz*=r45,ro.xz*=r25;
    rd.yz*=r45,rd.xz*=r25;

    // reflection loop (@BigWings)
    vec3 C = vec3(0),ref=vec3(0), fil=vec3(1);
    float d = 0.;

    for(float i=0.; i<2.; i++) {
        vec4 pass = render(ro, rd, ref, i==2.-1., d, uv);
        C += pass.rgb*fil;
        fil*=ref;
    }
    C = clamp(C,vec3(.01),vec3(1));
    C=pow(C, vec3(.4545));
    O = vec4(C,1);
}

// Fork of "Water Droplets" by Marrrk. https://shadertoy.com/view/4sBcDh
// 2023-04-30 17:03:42

#define GET 2 // 0 - Height, 1 - Normal, 2 - Textured

const int maxDroplets = 10;

const float dropletExpandSpeed = 1.5;
const float dropletHeightFactor = 1.;
const float dropletRipple = 80.0;
const float PI = 3.141;

float getDropletHeight(vec2 uv)
{
    float time = (iTime + 1245.6789) * 0.5;
    vec2 ddxy = 1.0 / iChannelResolution[0].xy;
    float height = 0.0;
    for (int i = 0; i < maxDroplets; ++i)
    {
        float decayRate = 0.323 + 2.0 * texture(iChannel0, vec2(float(i) * ddxy.x, 0.237)).x;
        float dropletStrength = texture(iChannel0, vec2(float(i) * ddxy.x, 0)).x;
        float dropletStrengthBias = 0.1 + 0.2 * texture(iChannel0, vec2(float(i) * ddxy.x, 0.345)).x;
        float dropFraction = time / decayRate;
        float dropIndex = floor(dropFraction);
        dropFraction = fract(dropFraction);

        float dropletX = texture(iChannel0, vec2(float(i) * ddxy.x + dropIndex * 0.1, dropIndex)).x;
        float dropletY = texture(iChannel0, vec2(float(i) * ddxy.y + dropIndex * 0.1, dropIndex)).y;
        vec2 dropletPosition;

        //if (iMouse.z > 0. || iMouse.w > 0.)
        //{
        //    dropletPosition = iMouse.xy / iResolution.xy - .5;
        //}
        //else
        //{
        //    dropletPosition = (vec2(dropletX, dropletY) * 2.0 - 1.0);
        //}

        dropletPosition = (vec2(dropletX, dropletY) * 2.0 - 1.0);

        float ringRadius = dropletExpandSpeed * dropFraction * dropletStrength - dropletStrengthBias;
        float distanceToDroplet = distance(uv, dropletPosition);
        float distanceToEdge = max(0.0, ringRadius - distanceToDroplet) / ringRadius;

        float dropletHeight = distanceToDroplet > ringRadius ? 0.0 : distanceToDroplet;
        dropletHeight = cos(PI + (dropletHeight - ringRadius) * dropletRipple * dropletStrength) * 0.5 + 0.5;
        dropletHeight *= 1.0 - dropFraction;
        dropletHeight *= distanceToDroplet > ringRadius ? 0.0 : distanceToDroplet / ringRadius;

        height += (1.0 - (cos(dropletHeight * 3.141) + 1.0) * 0.5) * dropletHeightFactor;
    }

    return height;
}

vec3 getDropletNormal(vec2 uv)
{
    float NORMAL_OFF = (1.0 / 128.0);
    vec3 off = vec3(-NORMAL_OFF, 0, NORMAL_OFF);

    // s11 = Current
    float s11 = getDropletHeight(uv);

    // s01 = Left
    float s01 = getDropletHeight(vec2(uv.xy + off.xy));

    // s21 = Right
    float s21 = getDropletHeight(vec2(uv.xy + off.zy));

    // s10 = Below
    float s10 = getDropletHeight(vec2(uv.xy + off.yx));

    // s12 = Above
    float s12 = getDropletHeight(vec2(uv.xy + off.yz));

    vec3 va = normalize( vec3(off.z, 0.0, s21 - s11) );
    vec3 vb = normalize( vec3(0.0, off.z, s12 - s11) );

    vec3 normal = cross(va, vb);
    normal.xy *= s11;
    return normalize(normal);
}

vec3 generateNormalMap(vec2 uv)
{
    vec3 d = getDropletNormal(uv - 0.5);
    return normalize(d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = gl_FragCoord.xy / iResolution.xy;
	if (GET > 0)
	{
    	vec3 normal = generateNormalMap(uv);
    	if (GET == 1)
    	{
    		fragColor = vec4((normal + 1.0) * 0.5, 1.0);
    	}
    	else if (GET == 2)
    	{
    		fragColor.xyz = texture(iChannel1, normal).xyz;
    	}
	}
	else
	{
        float h = getDropletHeight(uv);
        fragColor = vec4(h, h, h, 1.0);
    }
}

vec2 f1(vec2 a){
    float s1 = 16.;
    return
        fract(a)
        //fract(a-fract(-a)/16.)
        //abs(fract(a)+fract(a/s1)/s1)/(1.+1./s1)
        //abs(fract(a)-fract(a/s1)/s1)/(1.-1./s1)
    ;
}

vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
        //-vec2(1.+sign(a.x)/2.,0.5-sign(a.x)/2.)
    ,
    a1 = a+a2;
    return
        abs(f1((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.x/t1;
    uv.y += (iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    vec2 uv1 = uv;
    //uv.x = mod(uv.x,.5)+.25;
    uv.y = mod(uv.y,.5)+.25;
    uv.x = mod(uv.x,.5)+.25;
    for(int k = 0; k < 6; k++){
        uv =
            abs(.5-uv-t2*sign(uv.y-uv.x))/1.5-1.
            //abs(.5-uv/1.5-t2)/1.5-1.
        ;
        b = uv.y<uv.x;
        if(b)
            uv = uv.yx
            //uv.x += .5
            //uv.y += .5
        ;
        t2 =
            triangle_wave(uv-.5).yx
        ;
        uv =
            t2+triangle_wave(uv.yx).yx
        ;
        if(uv.y<uv.x)
        //{t2 = t2.yx; uv=uv.yx;}
        col.x =
            abs((uv.x)-col.x)
            //min((uv.x)/2.,col.x)
        ;

        //awesome kaleidoscope effect!
        //if(t2.y>t2.x) {t2 = t2.yx; uv=uv.yx-iTime/8.;}

        col.xyz =
            abs(col.yzx-float(b)/2.)
        ;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(min(col,vec3(1.)),1.0);
}

