// sdgTorus() returns the torus SDF and its gradient, by 
// computing it analytically. This means the normal to
// the torus surface can be used during the raymarch loop
// rather inexpensivelly (compared to sampling the SDF
// multiple times to evaluate a normal for it)

// Other SDF analytic gradients:
//
// Torus:   https://www.shadertoy.com/view/wtcfzM
// Capsule: https://www.shadertoy.com/view/WttfR7
// Ellipsoid: https://www.shadertoy.com/view/flXyRS


// .x = f(p)
// .y = f(p)/x
// .z = f(p)/y
// .w = f(p)/z
// .yzw = f(p) with f(p) = 1

//where is major radius and minor radius?
//ra is major and rb is minor?
vec4 sdgTorus( vec3 p, float ra, float rb )
{
    float h = length(p.xz);
    return vec4( length(vec2(h-ra,p.y))-rb,
                 normalize(p*vec3(h-ra,h,h-ra)) );
}


#define AA 3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*(iTime-10.0);
	vec3 ro = 1.2*vec3( 1.0*cos(an), 0.65, 1.0*sin(an) );
    vec3 ta = vec3( 0.0, -0.15, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    float size = 0.2;

   float idk = 1.0;

    // animate torus
    float majorRadius = idk*size + idk*size*sin(iTime); //major radius
    float minorRadius = size; //minor radius

    // render    
    vec3 tot = vec3(0.0);
    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        #else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 5.0;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = sdgTorus(pos,majorRadius,minorRadius).x;
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = sdgTorus(pos,majorRadius,minorRadius).yzw;

            // compute normal numerically, for comparison
            // https://iquilezles.org/articles/normalsSDF
            #if 0
            const vec2 e = vec2(1,-1);
            const float eps = 0.0002;
            nor = normalize( e.xyy*sdgTorus( pos + e.xyy*eps, majorRadius, minorRadius ).x + 
                             e.yyx*sdgTorus( pos + e.yyx*eps, majorRadius, minorRadius ).x + 
                             e.yxy*sdgTorus( pos + e.yxy*eps, majorRadius, minorRadius ).x + 
                             e.xxx*sdgTorus( pos + e.xxx*eps, majorRadius, minorRadius ).x );

            #endif

            float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.85,0.75,0.65)*dif;
            col *= (0.5+0.5*nor)*(0.5+0.5*nor);
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0; // normalize coordinates
    vec2 z = vec2(0.0); // initialize complex number

    // fractal iteration
    for (int i = 0; i < 10; i++) {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + vec2(p.x*2.0, p.y*2.0);
    }

    // color the dots based on the position
    vec3 color = vec3(sin(z.x + iTime), cos(z.y + iTime), sin(z.x + z.y + iTime));
    fragColor = vec4(color, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (fragCoord.xy / iResolution.xy) - 0.5; // center the coordinate system
    p.x *= iResolution.x / iResolution.y; // correct the aspect ratio

    // background color
    vec4 bgColor = vec4(1.0, 0.0, 0.0, 1.0); // red

    // sphere properties
    vec3 spherePos = vec3(0.0, 0.0, 0.0); // sphere center position
    float sphereRadius = 0.3;
    vec3 sphereColor = vec3(0.0, 0.0, 1.0); // blue

    // sphere animation
    float speed = 0.5;
    float time = iTime * speed;
    float bounce = abs(sin(time)); // bouncing effect
    vec3 sphereOffset = vec3(0.0, bounce * 0.5, 0.0);
    spherePos += sphereOffset;

    // calculate distance between pixel and sphere center
    float dist = length(p - spherePos.xy);

    // check if pixel is inside the sphere
    if (dist < sphereRadius) {
        // calculate color based on distance from sphere center
        vec3 color = mix(sphereColor, vec3(1.0), smoothstep(0.0, sphereRadius, dist));
        fragColor = vec4(color, 1.0);
    } else {
        fragColor = bgColor;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // normalize the coordinates to range from -1 to 1
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

    // calculate the pixel aspect ratio
    float aspect = iResolution.x / iResolution.y;

    // set the wave parameters
    float time = iTime * 0.5;
    float freq1 = 1.0;
    float freq2 = 2.0;
    float freq3 = 3.0;
    float amp1 = 0.1;
    float amp2 = 0.15;
    float amp3 = 0.2;

    // calculate the wave values
    float wave1 = sin(uv.x * freq1 + time) * amp1;
    float wave2 = sin(uv.x * freq2 + time) * amp2;
    float wave3 = sin(uv.x * freq3 + time) * amp3;

    // combine the waves
    float totalWave = wave1 + wave2 + wave3;

    // map the value to colors
    vec3 color = vec3(0.0);
    if (totalWave < -0.4) {
        color = vec3(0.1, 0.1, 0.1);
    } else if (totalWave < 0.0) {
        color = vec3(0.0, 0.0, 1.0);
    } else if (totalWave < 0.4) {
        color = vec3(1.0, 0.0, 0.0);
    } else {
        color = vec3(0.1, 0.1, 0.1);
    }

    // render the waves
    if (uv.y < -0.4 || uv.y > 0.4 || uv.x > -aspect + 0.2) {
        fragColor = vec4(color, 1.0);
    } else {
        fragColor = vec4(0.1, 0.1, 0.1, 1.0);
    }
}
