#define PI 3.14159265359
#define RANDOM_DETAILS 20.0
#define SMOKE_WAVES 8
#define SMOKE_RADIUS 1.0
#define SMOKE_EDGE_BRIGHTNESS 0.0
#define SMOKE_CENTER_BRIGHTNESS 0.0
#define SMOKE_PULSATION 1.2
#define SMOKE_STABILITY 10.0

float random(vec2 uv) {
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453123);
}

float blured_random(vec2 uv, float scale) {
    vec2 uv2 = fract(uv * scale);
    float d1 = random(floor(uv * scale));
    float d2 = random(floor(uv * scale + vec2(1.0, 0.0)));
    float d3 = random(floor(uv * scale + vec2(0.0, 1.0)));
    float d4 = random(floor(uv * scale + vec2(1.0, 1.0)));
    d1 = mix(d1, d3, uv2.y);
    d2 = mix(d2, d4, uv2.y);
    return mix(d1, d2, uv2.x);
}

float layered_random(vec2 uv, float scale) {
    float d = blured_random(uv, scale);
    d += blured_random(uv, scale * 2.0) * 0.5;
    d += blured_random(uv, scale * 4.0) * 0.25;
    d += blured_random(uv, scale * 8.0) * 0.125;
    d += blured_random(uv, scale * 16.0) * 0.0625;
    return d / 2.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    // Random circle in the center
    float d = layered_random(uv, RANDOM_DETAILS)
            * smoothstep(0.0, 0.3, +0.4 - length(uv));

    for (int i = 0; i < SMOKE_WAVES; i++) {
        // Add smoke wave
        float time = iTime + SMOKE_PULSATION * 2.0 * PI * float(i) / float(SMOKE_WAVES);
        float scale = RANDOM_DETAILS - abs(sin(time / 2.0)) * SMOKE_STABILITY;
        float d2 = layered_random(uv, scale)
                 * smoothstep(SMOKE_RADIUS, SMOKE_EDGE_BRIGHTNESS, length(uv))
                 * smoothstep(-SMOKE_CENTER_BRIGHTNESS, SMOKE_RADIUS, length(uv))
                 * sin(time);
        d += smoothstep(0.0, 0.5, d2);
        // Add rays
        d *= abs(sin(iTime / 3.0 + 1.5 * uv.x / uv.y))
           * abs(sin(iTime / 5.0 + 0.5 * uv.y / uv.x));
    }

    fragColor = vec4(d, d, d, 1.0);
}

