#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(0.8),b,d)
#define S2(d,b) smoothstep(7.0*antialiasing(1.0),b,d)
#define PI 3.141516


float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}
float unionSDF(float distA, float distB)
	{ return min(distA, distB);}
float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}


///oneshade    
vec2 Rotate(in vec2 p, in vec2 o, in float r) 
{   float c = cos(r), s = sin(r);
    return (p - o) * mat2(c, -s, s, c) + o;
}

float sdCircle( vec2 p, float r )
{return length(p) - r; }

float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b; return length(max(d,0.0)) + min(max(d.x,d.y),0.0); }

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
vec2 pa = p-a, ba = b-a;
float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
return length( pa - ba*h );
}



float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}    

vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.001,0.0));
return colOut;
}
vec3 DrawFigBordeCoL(vec3 pColObj, vec3 colOut, float distObj , vec3 colBorde )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,colBorde ,S2(abs( distObj)-0.01,0.0));
  return colOut;
}

vec3 DrawFigDelgado(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj-0.001,0.0)); return colOut; }



