void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 uv2=uv*5.0;
    uv-=vec2(-1.5,-0.5);
    uv*=4.0;

    vec3 col=vec3(0.5);
    col=escena(uv,col,uv2);

    fragColor = vec4(col,1.0);
}

