vec2 screen_to_uv(vec2 coord)
{
    return (2.0 * coord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

vec3 view_transform(vec3 col)
{
	// OETF (Gamma)
    return pow(col, vec3(1.0 / 2.2));
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    float t = 0.9 * floor(iTime * 30.0) / 30.0;
    vec2 c = floor(frag_coord / 2.0) * 2.0 + 0.5;

    // Initialize PRNG
    prng_init(vec3(c / iResolution.y, t));

    // UV
    vec2 uv = screen_to_uv(c);

    // Render
    uv += vec2(0.5, 0.0);
    float r = length(uv) + random() * 0.05;
    float a = (0.55 * get_angle(uv) / TAU) + t * 0.1 + random() * 0.01;
    vec3 col = rgb_sweep(a);
    col = mix(col, col * dot(col, vec3(0.5, 0.3, 0.2)) * 1.1, 0.25);
    col = mix(col, vec3(1.02), pow(map_range_clamp(r, 2.5, 0.0, 0.0, 1.0), 6.0));
    float ring = 0.6 + cos(t * TAU * 0.5) * 0.04 + cos(t * TAU * 0.3) * 0.02 + cos(t * TAU * 1.5) * 0.005;
    float ring_width = 0.04 + cos(t * TAU * 0.4) * 0.015;
    if (r > ring && r < (ring + ring_width))
    {
        float fac = 1.0 - 2.0 * abs((get_angle(uv) / TAU) - 0.5);
        fac *= fac;
        col = pow(col, vec3(map_range(fac, 0.0, 1.0, 1.0, 2.5)));
        col *= map_range(fac, 0.0, 1.0, 1.0, 1.2);
    }
    col = pow(col, vec3(map_range_clamp(r, 0.5, 5.0, 0.9, 5.0)));

    // Output
    col = view_transform(col);
    frag_col = vec4(col, 1.0);
}

