float get_angle(vec2 v)
{
    v /= length(v);
    return atan(-v.y, -v.x) + PI;
}

vec3 rgb_sweep(float hue)
{
    hue = wrap(hue * 360.0, 0.0, 360.0);

    vec3 col = vec3(1, 0, 0);
    col = mix(col, vec3(1, 1, 0), smoothstep(0.0, 60.0, hue));
    col = mix(col, vec3(0, 1, 0), smoothstep(60.0, 120.0, hue));
    col = mix(col, vec3(0, 1, 1), smoothstep(120.0, 180.0, hue));
    col = mix(col, vec3(0, 0, 1), smoothstep(180.0, 240.0, hue));
    col = mix(col, vec3(1, 0, 1), smoothstep(240.0, 300.0, hue));
    col = mix(col, vec3(1, 0, 0), smoothstep(300.0, 360.0, hue));

    return col;
}

