float mandelbulbDist(vec3 pos) {
    // Rotate the fractal over time
    float angle = iTime/10.;
    mat3 rot = rotationMatrix(vec3(0.0, 1.0, 0.0), angle);
    pos = rot * pos;

     // Scale the fractal to make it appear larger
    float scaleFactor = 0.85;  // Adjust this value to control the zoom level
    pos *= scaleFactor;

    vec3 z = pos;
    float dr = 1.0;
    float r = 0.0;
    const int iterations = 5;
    const float bailout = 2.0;
    const float power = 3.0;
    for (int i = 0; i < iterations; i++) {
        r = length(z);
        if (r > bailout) return r;

        float theta = acos(z.z/r);
        float phi = atan(z.y, z.x);
        dr = pow(r, power - 1.0) * power * dr + 1.0;

        float zr = pow(r, power);
        theta *= power;
        phi *= power;

        z = zr * vec3(sin(theta) * cos(phi), sin(theta) * sin(phi), cos(theta));
        z += pos;
    }
    return 0.5 * log(r) * r/dr;
}

float rayMarch(vec3 ro, vec3 rd) {
    float totalDist = 0.0;
    for(int i = 0; i < 128; i++) {
        vec3 p = ro + rd * totalDist;
        float d = mandelbulbDist(p);
        totalDist += d;
        if(d < 0.001) {
            return totalDist;
        }
        if(totalDist > 4.0) break;
    }
    return totalDist;
}

vec3 getNormal(vec3 p) {
    const vec2 eps = vec2(0.001, 0.0);
    vec3 n;
    n.x = mandelbulbDist(p + eps.xyy) - mandelbulbDist(p - eps.xyy);
    n.y = mandelbulbDist(p + eps.yxy) - mandelbulbDist(p - eps.yxy);
    n.z = mandelbulbDist(p + eps.yyx) - mandelbulbDist(p - eps.yyx);
    return normalize(n);
}

float lightIntensity(vec3 p, vec3 lightDir) {
    vec3 normal = getNormal(p);
    return max(0.1, dot(normal, lightDir));
}

mat3 rotationMatrix(vec3 axis, float angle) {
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s, oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,   oc * axis.y * axis.y + c,         oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,   oc * axis.y * axis.z + axis.x * s, oc * axis.z * axis.z + c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 color = vec3(0.0);
    vec3 ro = vec3(0.0, 0.0, -2.5);
    vec3 target = vec3(0.0, 0.0, 0.0);

    // Rotate the fractal over time
    float angle = iTime;
    mat3 rot = rotationMatrix(vec3(0.0, 1.0, 0.0), angle);
    target = rot * target;

    vec3 forward = normalize(target - ro);
    vec3 right = cross(vec3(0.0, 1.0, 0.0), forward);
    vec3 up = cross(forward, right);
    vec3 c = ro + forward * 2.0;
    vec3 i = c + right * uv.x;
    vec3 j = c + up * uv.y;
    vec3 p = i + j;
    vec3 rayDir = normalize(p - ro);

    float dist = rayMarch(ro, rayDir);
    if(dist < 4.0) {
        vec3 lightDir = normalize(vec3(0.5, 1.0, 0.8)); // Direction of the light
        float diff = lightIntensity(ro + rayDir * dist, lightDir);
        color = vec3(1.0, 0.0, 0.0) * diff;
    }

    fragColor = vec4(color,1.0);
}

