float hash12(vec2 position, float s)  // s=50.
{
    vec2 p = (position + mod(iTime + float(iFrame)/37.9, 200.) * 1500. + s + 27.);
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
