float fbm(vec2 u,float t)
{
    float l = length(u)-t*.15;
    l = sin(l)+sin(l*2.3)+sin(l*3.1);
    float a = 1.-l*.6;
    mat2 m = mat2(.8,.6, -.6,.8)*2.;
    u*=.007;
    a += .50*texture(iChannel0,u).x; u *= m;
    a += .25*texture(iChannel0,u).x; u *= m;
    a += .12*texture(iChannel0,u).x; u *= m;
    a += .06*texture(iChannel0,u).x; u *= m;
    a += .03*texture(iChannel0,u).x;
    return a;
}
float tal(vec2 u,vec2 n)
{
    float s = 1.;
    for(float i=0.; i<8.; ++i)
    {
        u = abs(u);
        float l = 1.5/dot(u,u);
        s*=l;
        u*=l;
        u+=n;
    }
    return -abs(u.y)/s;
}
float tal2(vec2 u,vec2 n,out vec4 o)
{
    vec2 m = vec2(6);
    float s = 1.;
    for(float i=0.; i<8.; ++i)
    {
        u = abs(u);
        float l = 1.5/dot(u,u);
        s*=l;
        u*=l;
        u+=n;
        o+=(cos(i*m.x+l+vec4(2,1,3,4)*m.y))*(.1-abs(u.y)/s);
    }o=o*.5+1.;
    return -abs(u.y)/s;
}
vec4 my(vec2 u, vec2 pp)
{
    vec2 v = u*10.;
    float t = pp.y;
    float a = fbm(v-vec2(2,88)*.0001,t)-fbm(v,t);
    vec4 b = a*7.+vec4(.77,.74,.67,0);
    vec2 n = cos(-t*.1+vec2(.0,.5)*3.145)*.02;
    n -= vec2(2.41,.09);
    v = vec2(abs(u.x)+.2,u.y);
    vec4 c = vec4(0);
    float s = tal2(v,n,c);
    c*=(tal(v-vec2(2,88)*.00005,n)-s)*22.+.7;
    c*=s*3.+1.1;
    s = s*4.+.5-a*8.;
    b = mix(b,c,clamp(s+.5,0.,1.));
    b = mix(b,c,clamp(s*6.,0.,1.));
    return b;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 1.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 1.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;

    fragColor = my(u, vec2(iTime*4.));
}

// MANDELBROT FRACTAL SHADER
// Credits to CoderSpaceChannel on YT, tutorial: https://www.youtube.com/shorts/h5PuIm6fRr8
float MAX_ITER = 1055.0;
float mandelbrot(vec2 uv) {
    vec2 c = 4.0 * uv - vec2(0.7, 0.0);
    c = c / pow(iTime, 4.0) - vec2(0.65, 0.45);
    vec2 z = vec2(0.0);
    float iter = 0.0;
    for(float i = 0.0; i < MAX_ITER; i += 1.0) {
        z = vec2(
            z.x * z.x - z.y * z.y,
            2.0 * z.x * z.y) + c;
        if(dot(z, z) > 4.0) return iter / MAX_ITER;
        iter += 1.0;
    }
    return 0.0;
}

vec3 hash13(float m) {
    float x = fract(sin(m) * 0.246);
    float y = fract(sin(m + x) * 0.486);
    float z = fract(sin(x + y) * 100.352 * cos(iTime)*.05+.5);
    return vec3(x, y, z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);

    float m = mandelbrot(uv);
    col += hash13(m);
    //col += m;

    //col = pow(col, vec3(0.45));

    fragColor = vec4(col,1.0);
}

// GrayFractals
// Credits to Radian628 on Youtube, tutorial here: https://www.youtube.com/watch?v=8bbTkNZYdQ8
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = cos(iTime) * 0.22;
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    for (float i = 0.0; i < 50.0; i += 1.0) {
        uv = abs(uv);
        uv -= 0.5;
        uv *= 1.15;
        uv *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }
    fragColor = vec4(vec3(length(uv)), 1.0);
}

// cosine based palette, 4 vec3 params
vec3 palette(float t)
{
    vec3 a = vec3(0.668, 0.608, 0.768);
    vec3 b = vec3(1.048, 0.548, 0.362);
    vec3 c = vec3(0.778, 1.338, 1.388);
    vec3 d = vec3(1.617, 1.528, 3.007);

    return a + b*cos( 6.28318*(c*t+d) );
}

vec2 rotateUV(vec2 uv, vec2 pivot, float rotation) {
    float cosa = cos(rotation);
    float sina = sin(rotation);
    uv -= pivot;
    return vec2(
        cosa * uv.x - sina * uv.y,
        cosa * uv.y + sina * uv.x 
    ) + pivot;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;

    // Translate (0, 0) to center
    vec2 uv = ((fragCoord * 2.0) - res) / res.y;
    uv = rotateUV(uv, vec2(0.0), iTime * .1);


    vec2 uv0 = uv * sin(iTime / 5.);

    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 3.0; i++) {
        uv = fract(uv * 1.6) - 0.5;

        float dist = length(uv) * exp(-length(uv0));

        // Color palette
        vec3 color = palette(length(uv0) + i * 0.4 - iTime * .2);

        dist = sin(dist * 8. + iTime * .3) / 8.;
        dist = abs(dist);

        dist = 0.01 / dist;

        finalColor += color * dist / 1.8;
    }

    finalColor.r = pow(finalColor.r, 1.2);
    finalColor.g = pow(finalColor.g, 1.6);
    finalColor.b = pow(finalColor.b, 1.6);

    float clmp = 0.05;

    finalColor = clamp(finalColor, 0., 1.0 - clmp) + clmp;

    fragColor = vec4(finalColor, 1.0);
}

/* -----------------------------------------------
  Shadertoy Unit: Image
  Description: None
  iChannel0: None
  iChannel1: None
  iChannel2: None
  iChannel3: None
----------------------------------------------- */

vec2 screen_to_uv(vec2 coord)
{
    return (2.0 * coord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

vec3 view_transform(vec3 col)
{
	// OETF (Gamma)
    return pow(col, vec3(1.0 / 2.2));
}

vec3 render(vec2 frag_coord)
{
    // UV
    vec2 uv = screen_to_uv(frag_coord);

    // Camera params
    const vec3 cam_pos = vec3(0.0, -1.6, 0.5);
    const float cam_fov = 90.0;

    // Camera details
    float cam_zoom = 90.0 / cam_fov;
    vec3 cam_forward = normalize(-cam_pos);
    vec3 cam_right = normalize(cross(cam_forward, vec3(0, 0, 1)));
    vec3 cam_up = cross(cam_right, cam_forward);

    // Setup ray
    ray_info r;
    r.orig = cam_pos;
    r.dir = normalize(cam_forward + cam_right * (uv.x / cam_zoom) + cam_up * (uv.y / cam_zoom));

    // Cast ray
    ray_sphere(0.8, r);
    if (r.hit)
    {
        // Material params
        const vec3 mat_diffuse = vec3(1.0, 0.5, 0.0);
        const float mat_specular = 0.1;
        const float mat_specular_pow = 300.0;

        // Point light params
        const vec3 light_col = vec3(10.5);
        const vec3 light_pos = vec3(1.0, -1.8, 3.0);

        // Light details
        float light_dist = distance(r.pos, light_pos);
        vec3 light_dir = (light_pos - r.pos) / light_dist;

        // Lambertian diffuse
        vec3 diffuse =
            mat_diffuse * light_col
            * max(0.0, dot(r.normal, light_dir))
            / (light_dist * light_dist);

        // Blinn specular
        vec3 hv = normalize(light_dir - r.dir);
        vec3 spec =
            mat_specular * light_col
            * pow(max(0.0, dot(hv, r.normal)), mat_specular_pow);

        // Ambient
        vec3 ambient = 0.05 * mat_diffuse + 0.02 * vec3(0.04, 0.3, 0.7);

        // Shade
        vec3 col = diffuse + spec + ambient;

        return col;
    }
    else
    {
        // Background color
        vec3 col = vec3(0.04, 0.3, 0.7);
        col *= map_range_clamp(uv.y, -0.5, 0.7, 1.1, 0.8);
        col *= map_range(smoothstep(2.0, 0.7, length(uv)), 0.0, 1.0, 0.9, 1.0);
        col *= 1.1;
        col = pow(col, vec3(map_range(smoothstep(2.0, 0.7, length(uv)), 0.0, 1.0, 1.1, 1.0)));
        return col;
    }
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    // Initialize PRNG
    prng_init(vec3(frag_coord / iResolution.y, iTime));

    // Render with jitter sampling
    const int num_samples = 24;
    vec3 col = vec3(0.0);
    for (int i = 0; i < num_samples; i++)
    {
        vec2 offs = vec2(random(), random()) - 0.5;
        col += render(frag_coord + offs);
    }
    col /= float(num_samples);

    // Output
    col = view_transform(col);
    frag_col = vec4(col, 1.0);
}

