struct ray_info
{
    vec3 orig;
    vec3 dir;
    bool hit;
    vec3 pos;
    vec3 normal;
};

void ray_sphere(float radius, inout ray_info r)
{
    // Quadratic equation
    float a = dot(r.dir, r.dir);
    float b = 2.0 * dot(r.orig, r.dir);
    float c = dot(r.orig, r.orig) - radius*radius;
    float discriminant = (b * b) - (4.0 * a * c);

    if (discriminant < 0.0)
    {
        // No hits
        r.hit = false;
        return;
    }

    float sqrt_d = sqrt(discriminant);
    float t1 = (-b - sqrt_d) / (2.0 * a);
    float t2 = (-b + sqrt_d) / (2.0 * a);

    if (t1 < 0.0 && t2 < 0.0)
    {
        // Fully behind the origin
        r.hit = false;
        return;
    }

    if (t1 < 0.0)
    {
        // Partially behind the origin
        r.hit = true;
        r.pos = r.orig + (t2 * r.dir);
    }
    else
    {
        // Fully in front
        r.hit = true;
        r.pos = r.orig + (t1 * r.dir);
    }

    // Normal
    r.normal = r.pos / radius;
}

