// Analytic box occlusion with clipping optimized using less acos / conditions. 
// It's based on the cube's perimeter instead of the individual faces' perimeter.

//=====================================================

float sacos( float x ) { return acos( min(max(x,-1.0),1.0) ); }

vec3 clip( in vec3 a, in vec3 b, in vec4 p )
{
    return a - (b-a)*(p.w + dot(p.xyz,a))/dot(p.xyz,(b-a));
    //return ( a*dot(p.xyz,b) - b*dot(p.xyz,a)  - (b-a)*p.w ) / dot(p.xyz,(b-a));
}

float fedgeOcclusion(in vec3 pos, in vec3 nor, in vec3 v0, in vec3 v1)
{
    vec3 nv0 = normalize(v0 - pos);
    vec3 nv1 = normalize(v1 - pos);
    return dot(nor, normalize(cross(nv0,nv1))) * sacos(dot(nv0,nv1)) / 6.283185;
}

float edgeOcclusion(in vec3 pos, in vec3 nor, in vec3 v0, in vec3 v1, inout vec3 c0, inout vec3 c1, in vec4 plane)
{
    float s0 = dot(vec4(v0,1.0), plane);
    float s1 = dot(vec4(v1,1.0), plane);
    float sn = sign(s0) + sign(s1);

    vec3 c = clip( v0, v1, plane );

    float occ = 0.0;
    // All vertices above horizon
    if(sn>1.0) 
    {
        occ += fedgeOcclusion(pos, nor, v0, v1);
    }
    // 1 vertex above horizon
    else if(sn>-1.0) 
    {
        vec3 pa, pb;
        if(s0 > 0.0 )        { pa = v0; pb = c;  }
        else/*if( s1>0.0 )*/ { pa = c;  pb = v1; }
        occ += fedgeOcclusion(pos, nor, pa, pb);

        // Save the intersection
        if(dot(c0, c0) != 0.0) { c1 = c; } 
        else { c0 = c; }
    }
    // zero (no) vertices above horizon
    return occ;
}

float boxOcclusion( in vec3 pos, in vec3 nor, in mat4 txx, in mat4 txi, in vec3 rad ) 
{
	vec3 p = (txx*vec4(pos,1.0)).xyz;
	vec3 n = (txx*vec4(nor,0.0)).xyz;
    vec4 w = vec4( n, -dot(n,p) ); // clipping plane

    // Orient the hexagon based on p
    vec3 sp = sign(p);
    vec3 f = rad * sp;

    // Make sure the hexagon is always convex
    vec3 s = sign(rad - abs(p));

    // 6 verts
    vec3 v0 = vec3( 1.0, 1.0, min(s.x,s.y))*f;
    vec3 v1 = vec3( 1.0, s.x, s.x)*f;
    vec3 v2 = vec3( 1.0, min(s.x,s.z), 1.0)*f;
    vec3 v3 = vec3( s.z, s.z, 1.0)*f;
    vec3 v4 = vec3( min(s.y,s.z), 1.0, 1.0)*f;
    vec3 v5 = vec3( s.y, 1.0, s.y)*f;

    // Points of intersection with the plane
    vec3 c0 = vec3(0.0);
    vec3 c1 = vec3(0.0);

    // 6 edges
    float occ = edgeOcclusion(p, n, v0, v1, c0, c1, w);
    occ += edgeOcclusion(p, n, v1, v2, c0, c1, w);
    occ += edgeOcclusion(p, n, v2, v3, c0, c1, w);
    occ += edgeOcclusion(p, n, v3, v4, c0, c1, w);
    occ += edgeOcclusion(p, n, v4, v5, c0, c1, w);
    occ += edgeOcclusion(p, n, v5, v0, c0, c1, w);
    occ *= sp.x * sp.y * sp.z;
    // 1 edge for intersection with plane
    occ += abs(fedgeOcclusion(p, n, c0, c1));
    return max(0.0, occ);
}

