int solve_quadric(vec2 coeffs, inout vec2 roots){

	// normal form: x^2 + px + q = 0
	float p = coeffs[1] / 2.;
	float q = coeffs[0];

	float D = p * p - q;

	if (D < 0.){
		return 0;
	}
	else{
		roots[0] = -sqrt(D) - p;
		roots[1] = sqrt(D) - p;

		return 2;
	}
}

//From Trisomie21
int cubic_bezier_int_test(vec2 p0, vec2 p1, vec2 p2, vec2 p3, vec2 uv){

	float cu = (-p0.y + 3. * p1.y - 3. * p2.y + p3.y);
	float qu = (3. * p0.y - 6. * p1.y + 3. * p2.y);
	float li = (-3. * p0.y + 3. * p1.y);
	float co = p0.y - uv.y;

	vec3 roots = vec3(1e38);
	int n_roots;

    int n_ints=0;

    if(uv.x<min(min(p0.x,p1.x),min(p2.x,p3.x))){
		if(uv.y>=min(p0.y,p3.y) && uv.y<=max(p0.y,p3.y)){
			n_ints=1;
		}
	}
    else{

        if(abs(cu) < .00001){
            n_roots = solve_quadric(vec2(co/qu,li/qu),roots.xy);
        }
        else{
            n_roots = solve_cubic(vec3(co/cu,li/cu,qu/cu),roots);
        }

        for(int i=ZERO;i<n_roots;i++){
            if(roots[i] >= 0.0 && roots[i] <= 1.){
                float x_pos = -p0.x + 3. * p1.x - 3. * p2.x + p3.x;
                x_pos = x_pos * roots[i] + 3. * p0.x - 6. * p1.x + 3. * p2.x;
                x_pos = x_pos * roots[i] + -3. * p0.x + 3. * p1.x;
                x_pos = x_pos * roots[i] + p0.x;

                if(x_pos > uv.x){
                    n_ints++;
                }
            }
        }
    }

	return n_ints;
}

vec2 bez(vec2 A, vec2 B, vec2 C, vec2 D, float t)
{
    return mix(
            mix(mix(A, B, t), mix(B, C, t), t),
            mix(mix(B, C, t), mix(C, D, t), t), t);
}

vec2 thirdCp(vec2 A, vec2 B, vec2 C, vec2 D, float t)
{
    return mix(mix(A, B, t), mix(B, C, t), t);
}

vec2 fifthCp(vec2 A, vec2 B, vec2 C, vec2 D, float t)
{
    return mix(mix(B, C, t), mix(C, D, t), t);
}

vec2 evalPatch(vec2 uv, vec2 b00, vec2 b10, vec2 b20, vec2 b30, vec2 b01, vec2 b11, vec2 b21, vec2 b31, vec2 b02, vec2 b12, vec2 b22, vec2 b32, vec2 b03, vec2 b13, vec2 b23, vec2 b33) {
    vec2 bu0 = bez(b00, b10, b20, b30, uv.x);
    vec2 bu1 = bez(b01, b11, b21, b31, uv.x);
    vec2 bu2 = bez(b02, b12, b22, b32, uv.x);
    vec2 bu3 = bez(b03, b13, b23, b33, uv.x);

    return bez(bu0, bu1, bu2, bu3, uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y*2.0 - vec2(1.3, 0.5);

    //perturb base control points randomly
    vec2 B00 = vec2(0.0, 0.0) +0.05*vec2(cos(iTime/2.6), sin(iTime/0.5));
    vec2 B10 = vec2(1.0/3.0, 0.0) + 0.1*vec2(cos(iTime/2.6), 2.0*sin(iTime/0.5));
    vec2 B20 = vec2(2.0/3.0, 0.0) - 0.1*vec2(cos(iTime/0.7), 2.0*sin(iTime));
    vec2 B30 = vec2(1.0, 0.0) + +0.05*vec2(cos(iTime/0.5), sin(iTime/2.6));

    vec2 B01 = vec2(0.0, 1.0/3.0) + 0.1*vec2(cos(iTime/2.5), sin(iTime));
    vec2 B11 = vec2(1.0/3.0, 1.0/3.0) + 0.1*vec2(cos(iTime), sin(iTime));
    vec2 B21 = vec2(2.0/3.0, 1.0/3.0) + 0.1*vec2(cos(iTime/1.3), sin(iTime/0.7));
    vec2 B31 = vec2(1.0, 1.0/3.0) + 0.1*vec2(cos(iTime), sin(iTime));

    vec2 B02 = vec2(0.0, 2.0/3.0) - 0.1*vec2(cos(iTime/0.7), 2.0*sin(iTime));
    vec2 B12 = vec2(1.0/3.0, 2.0/3.0) + 0.1*vec2(cos(iTime/0.3), sin(iTime/1.1));
    vec2 B22 = vec2(2.0/3.0, 2.0/3.0) + 0.1*vec2(cos(iTime), sin(iTime));
    vec2 B32 = vec2(1.0, 2.0/3.0) - 0.1*vec2(cos(iTime/0.7), 2.0*sin(iTime));

    vec2 B03 = vec2(0.0, 1.0) + 0.05*vec2(cos(iTime/2.6), sin(iTime/0.5));
    vec2 B13 = vec2(1.0/3.0, 1.0) + 0.1*vec2(cos(iTime), sin(iTime));
    vec2 B23 = vec2(2.0/3.0, 1.0) + 0.1*vec2(cos(iTime), sin(iTime));
    vec2 B33 = vec2(1.0, 1.0) + 0.05*vec2(cos(iTime/2.6), sin(iTime/0.5));

    vec2 b00 = B00;
    vec2 b10 = B10;
    vec2 b20 = B20;
    vec2 b30 = B30;

    vec2 b01 = B01;
    vec2 b11 = B11;
    vec2 b21 = B21;
    vec2 b31 = B31;

    vec2 b02 = B02;
    vec2 b12 = B12;
    vec2 b22 = B22;
    vec2 b32 = B32;

    vec2 b03 = B03;
    vec2 b13 = B13;
    vec2 b23 = B23;
    vec2 b33 = B33;

    //switch for colour interpolation to texture mode
    int mode = 0;
    float changeTime = 2.*float(maxIterations);
    if(mod(iTime, changeTime) > changeTime * 0.50)
        mode = 1;


    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 t = vec2(0.5);
    float d;
    //new control point positions
    vec2 b00_n, b10_n, b20_n, b30_n, b01_n, b11_n, b21_n, b31_n, b02_n, b12_n, b22_n, b32_n, b03_n, b13_n, b23_n, b33_n;

    float f = 0.5;

    vec2 miu = vec2(0.0);
    vec2 mau = vec2(1.0);

    int s0 = cubic_bezier_int_test(b00, b10, b20, b30, uv);
    s0 += cubic_bezier_int_test(b30, b31, b32, b33, uv);
    s0 += cubic_bezier_int_test(b33, b23, b13, b03, uv);
    s0 += cubic_bezier_int_test(b03, b02, b01, b00, uv);

    int iterations = maxIterations;
    iterations = max(1, int(iTime) % maxIterations);

    if(s0 % 2 == 1) { //first test whether uv is bound by 4 main boundary curves
        col = vec3(1.0);
        for(int i = 0; i < iterations; ++i) {
            f = f * 0.5;

            vec2 bu0 = bez(b00, b10, b20, b30, 0.5);
            vec2 bu1 = bez(b01, b11, b21, b31, 0.5);
            vec2 bu2 = bez(b02, b12, b22, b32, 0.5);
            vec2 bu3 = bez(b03, b13, b23, b33, 0.5);

            vec2 bv0 = bez(b00, b01, b02, b03, 0.5);
            vec2 bv1 = bez(b10, b11, b12, b13, 0.5);
            vec2 bv2 = bez(b20, b21, b22, b23, 0.5);
            vec2 bv3 = bez(b30, b31, b32, b33, 0.5);

            vec2 C = bez(bu0, bu1, bu2, bu3, 0.5);

            col = vec3(1.0);

            //lo
            vec2 b10_lo = 0.5*(b00 + b10);
            vec2 b20_lo = thirdCp(b00, b10, b20, b30, 0.5);

            vec2 b01_lo = 0.5*(b00 + b01);
            vec2 b02_lo = thirdCp(b00, b01, b02, b03, 0.5);

            vec2 b31_lo = 0.5*(bu0 + bu1);
            vec2 b32_lo = thirdCp(bu0, bu1, bu2, bu3, 0.5);

            vec2 b13_lo = 0.5*(bv0 + bv1);
            vec2 b23_lo = thirdCp(bv0, bv1, bv2, bv3, 0.5);

            //lb
            vec2 b01_lb = fifthCp(b00, b01, b02, b03, 0.5);
            vec2 b02_lb = 0.5 * (b02 + b03);

            vec2 b13_lb = 0.5 * (b03 + b13);
            vec2 b23_lb = thirdCp(b03, b13, b23, b33, 0.5);

            vec2 b31_lb = fifthCp(bu0, bu1, bu2, bu3, 0.5);
            vec2 b32_lb = 0.5 * (bu3 + bu2);

            //rb
            vec2 b13_rb = fifthCp(b03, b13, b23, b33, 0.5);
            vec2 b23_rb = 0.5*(b23 + b33);

            vec2 b31_rb = fifthCp(b30, b31, b32, b33, 0.5);
            vec2 b32_rb = 0.5*(b32 + b33);

            vec2 b10_rb = fifthCp(bv0, bv1, bv2, bv3, 0.5);
            vec2 b20_rb = 0.5*(bv2 + bv3);

            //ro
            vec2 b10_ro = fifthCp(b00, b10, b20, b30, 0.5);
            vec2 b20_ro = 0.5*(b20 + b30);

            vec2 b31_ro = 0.5*(b30 + b31);
            vec2 b32_ro = thirdCp(b30, b31, b32, b33, 0.5);

            //we know it is on the patch check three quadrants to find out in which one we are
            int s_lo = cubic_bezier_int_test(b00, b10_lo, b20_lo, bu0, uv);
            s_lo += cubic_bezier_int_test(bu0, b31_lo, b32_lo, C, uv);
            s_lo += cubic_bezier_int_test(C, b23_lo, b13_lo, bv0, uv);
            s_lo += cubic_bezier_int_test(bv0, b02_lo, b01_lo, b00, uv);

            int s_lb = cubic_bezier_int_test(bv0, b13_lo, b23_lo, C, uv);
            s_lb += cubic_bezier_int_test(C, b31_lb, b32_lb, bu3, uv);
            s_lb += cubic_bezier_int_test(bu3, b23_lb, b13_lb, b03, uv);
            s_lb += cubic_bezier_int_test(b03, b02_lb, b01_lb, bv0, uv);

            int s_rb = cubic_bezier_int_test(C, b10_rb, b20_rb, bv3, uv);
            s_rb += cubic_bezier_int_test(bv3, b31_rb, b32_rb, b33, uv);
            s_rb += cubic_bezier_int_test(b33, b23_rb, b13_rb, bu3, uv);
            s_rb += cubic_bezier_int_test(bu3, b32_lb, b31_lb, C, uv);

            vec2 small_c = 0.25*(b11 + b21 + b22 + b12);

            if(s_lo % 2 == 1) {
                //left bottom
                col = vec3(1.0, 0.0, 0.0);
                miu = miu;
                mau = mau - f;

                t = vec2(t.x - f, t.y - f);

                vec2 lo = 0.25*(b00 + b10 + b01 + b11);
                vec2 ro = 0.25*(b10 + b20 + b21 + b11);
                vec2 lb = 0.25*(b01 + b11 + b12 + b02);

                //compute control points of quadrant
                b00_n = b00;
                b10_n = b10_lo;
                b20_n = b20_lo;
                b30_n = bu0;

                b01_n = b01_lo;
                b11_n = lo;
                b21_n = 0.5 * (lo + ro); 
                b31_n = b31_lo;

                b02_n = b02_lo;
                b12_n = 0.5 * (lo + lb);
                b22_n = 0.25 * (lo + ro + lb + small_c);
                b32_n = b32_lo;

                b03_n = bv0;
                b13_n = b13_lo;
                b23_n = b23_lo;
                b33_n = C;
            }
            else if(s_lb % 2 == 1) {
                //right bottom
                col = vec3(0.0, 0.0, 1.0);
                miu = vec2(miu.x - f, miu.y);
                mau = vec2(mau.x, mau.y - f);

                t = vec2(t.x - f, t.y + f);

                vec2 lo = 0.25*(b01 + b11 + b12 + b02);
                vec2 lb = 0.25*(b02 + b12 + b13 + b03);
                vec2 rb = 0.25*(b12 + b22 + b23 + b13);

                //compute control points of quadrant
                b00_n = bv0;
                b10_n = b13_lo;
                b20_n = b23_lo;
                b30_n = C;

                b01_n = b01_lb;
                b11_n = 0.5*(lb + lo);
                b21_n = 0.25*(lo + lb + rb + small_c); 
                b31_n = b31_lb;

                b02_n = b02_lb;
                b12_n = lb;
                b22_n = 0.5*(lb + rb);
                b32_n = b32_lb;

                b03_n = b03;
                b13_n = b13_lb;
                b23_n = b23_lb;
                b33_n = bu3;
            }
            else if(s_rb % 2 == 1) {
                //right top
                col = vec3(1.0, 0.0, 1.0);
                miu = vec2(miu.x + f, miu.y + f);
                mau = vec2(mau.x, mau.y);

                t = vec2(t.x + f, t.y + f);

                vec2 lb = 0.25*(b12 + b22 + b23 + b13);
                vec2 ro = 0.25*(b21 + b31 + b32 + b22);
                vec2 rb = 0.25*(b22 + b32 + b33 + b23);

                //compute control points of quadrant
                b00_n = C;
                b10_n = b10_rb;
                b20_n = b20_rb;
                b30_n = bv3;

                b01_n = b31_lb;
                b11_n = 0.25*(lb + ro + rb + small_c);
                b21_n = 0.5 * (rb + ro); 
                b31_n = b31_rb;

                b02_n = b32_lb;
                b12_n = 0.5*(rb + lb);
                b22_n = rb;
                b32_n = b32_rb;

                b03_n = bu3;
                b13_n = b13_rb;
                b23_n = b23_rb;
                b33_n = b33;
            }
            else {
                //left top
                col = vec3(0.0, 1.0, 0.0);
                miu = vec2(miu.x, miu.y + f);
                mau = vec2(mau.x - f, mau.y);

                t = vec2(t.x + f, t.y - f);

                vec2 lo = 0.25*(b10 + b20 + b21 + b11);
                vec2 ro = 0.25*(b20 + b30 + b31 + b21);
                vec2 rb = 0.25*(b21 + b31 + b32 + b22);

                //compute control points of quadrant
                b00_n = bu0;
                b10_n = b10_ro;
                b20_n = b20_ro;
                b30_n = b30;

                b01_n = b31_lo;
                b11_n = 0.5*(lo + ro);
                b21_n = ro; 
                b31_n = b31_ro;

                b02_n = b32_lo;
                b12_n = 0.25*(lo + ro + rb + small_c);
                b22_n = 0.5*(ro + rb);
                b32_n = b32_ro;

                b03_n = C;
                b13_n = b10_rb;
                b23_n = b20_rb;
                b33_n = bv3;
            }


            b00 = b00_n;
            b10 = b10_n;
            b20 = b20_n;
            b30 = b30_n;

            b01 = b01_n;
            b11 = b11_n;
            b21 = b21_n;
            b31 = b31_n;

            b02 = b02_n;
            b12 = b12_n;
            b22 = b22_n;
            b32 = b32_n;

            b03 = b03_n;
            b13 = b13_n;
            b23 = b23_n;
            b33 = b33_n;
        }
    }

    //compute parametric corners of subpatch
    vec2 h = vec2(f * 0.5);
    vec2 miuv = t - h;
    vec2 mauv = t + h;
    //do something with it to approximate interpolation? 
    //inverse bilinear does not work because there will still be discontinuties 


    vec3 c1 = vec3(1.0, 0.0, 0.0);
    vec3 c2 = vec3(0.0, 1.0, 0.0);
    vec3 c3 = vec3(0.0, 0.0, 1.0);
    vec3 c4 = vec3(1.0, 1.0, 0.0);

    //interpolate colour
    vec3 cd = mix(mix(c1, c2, t.x), 
                   mix(c3, c4, t.x), t.y); 

    //comment this to show quadrant debug colours
    col = cd;

    //show control points
    if(length(uv - B00) < 0.01) col = vec3(0.0);
    if(length(uv - B10) < 0.01) col = vec3(0.0);
    if(length(uv - B20) < 0.01) col = vec3(0.0);
    if(length(uv - B30) < 0.01) col = vec3(0.0);

    if(length(uv - B01) < 0.01) col = vec3(0.0);
    if(length(uv - B11) < 0.01) col = vec3(0.0);
    if(length(uv - B21) < 0.01) col = vec3(0.0);
    if(length(uv - B31) < 0.01) col = vec3(0.0);

    if(length(uv - B02) < 0.01) col = vec3(0.0);
    if(length(uv - B12) < 0.01) col = vec3(0.0);
    if(length(uv - B22) < 0.01) col = vec3(0.0);
    if(length(uv - B32) < 0.01) col = vec3(0.0);

    if(length(uv - B03) < 0.01) col = vec3(0.0);
    if(length(uv - B13) < 0.01) col = vec3(0.0);
    if(length(uv - B23) < 0.01) col = vec3(0.0);
    if(length(uv - B33) < 0.01) col = vec3(0.0);

    if(mode == 1)
        col = textureLod(iChannel0, t, 0.).xyz  ;  


    fragColor = vec4(col, 1.0);
    //uncomment to show parametrisation
    //fragColor = vec4(vec3(t, 0.0), 1.0);
}

float N21(vec2 uv) { return fract(sin(uv.x * 21.281 + uv.y * 93.182) * 5821.92); }

float line(vec2 uv) { return smoothstep(0.0, 0.05, uv.x) - smoothstep(0.0, 0.95, uv.x); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;

    vec2 offset = abs(uv.yx) / vec2(30., 5.2);
    uv = uv + uv * offset * offset;
    uv = uv * 0.5 + 0.5;

    vec2 scale = vec2(128, 90);

    vec2 lUV = fract(uv * scale);
    vec2 gID = floor(uv * scale);

    float rowNoise = N21(vec2(0.0, gID.y));
    float dir = ((rowNoise * 2.0) - 1.0) + 0.2;
    gID.x += floor(iTime * dir * 30.);

    float cellNoise = N21(gID);
    float drawBlock = float(cellNoise > 0.38);
    int even = int(gID.y) % 2;

    vec3 col = vec3(line(lUV)) * drawBlock * float(even);
    col *= fract(sin(gID.y)) + 0.24;
    col *= vec3(0.224,0.996,0.557);

    fragColor = vec4(vec3(col), col.g * 0.2);
}

#define ctiles  4.0 
#define cgrid 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float dx = 1.0/iResolution.y;

     col = vec3(0.0);


    vec2 tileduv = mod(uv*ctiles,vec2(1.0));
    int idxy = int(uv.y*ctiles)*int(ctiles);
    int idxx = int(uv.x*ctiles);
    int idx = idxx+idxy;

    vec3 ix = vec3(tileduv.x);vec3 iy = vec3(tileduv.y-.2)*1.25;
    col = vec3( 0.2 + 0.02*mod(floor(ix*cgrid)+floor(iy*cgrid),2.0) );
    if(tileduv.y<0.2){
        col = plotA(ix,iTime,idx);
    }else{

        vec3 y = plotA( ix,iTime,idx);
        vec3 dy = plotA(ix-dx,iTime,idx) - y;
        vec3 di = iy-y;
        col = mix( col, vec3(1), smoothstep(1., 0., abs(di)/fwidth(di) ) );
    }
    //col.x = float(idx==0?1:0);
    // Output to screen
    fragColor = vec4(col,1.0);
}

