void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float  GridMaxRandom = 0.9;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 XY = fragCoord/iResolution.xy;
    XY=XY*20.;


    vec2 GridPos = floor(XY);
    vec2 GridOffset = fract(XY);
    vec2 tempResult = vec2(0.0,0.0);
    for(int i = -1;i <= 1; i ++)
    {
        for(int j = -1; j <= 1; j++)
        {
            vec2 currGridPos = GridPos + vec2(i, j);

            uvec2 p = uvec2(currGridPos);
            uint n = p.x + 1920U*p.y;

            n = (n << 13U) ^ n;
            n = n * (n * n * 15731U + 789221U) + 1376312589U;
            uvec3 k = n * uvec3(n,n*16807U,n*48271U);
            vec3 hashPos = vec3( k & uvec3(0x7fffffffU))/float(0x7fffffff);

            vec3 hashPointInfo = hashPos*vec3(GridMaxRandom,GridMaxRandom,1.0);
            vec2 currPointLocation = hashPointInfo.xy + vec2(i, j);
            float distanceRatio = pow(clamp(1.4-dot((GridOffset - currPointLocation),(GridOffset - currPointLocation)), 0.0, 1.4),64.0);
            tempResult += vec2(hashPointInfo.z*distanceRatio,distanceRatio);
        }
    }

    // Time varying pixel color
    vec3 col = vec3(tempResult.x/tempResult.y,tempResult.x/tempResult.y,tempResult.x/tempResult.y);
    fragColor = vec4(col,1.0);
}

