    //logo
    float divisionLine6 = 16.0/19.0;

    float divisonTime;
    float divisionLine;
    int count; 

    count = 1;

    //from earthmap to digital hardware
    if(count == 1){
    divisionLine = divisionLine1 + (divisionLine2 - divisionLine1) * smoothstep(0.0,1.0, iTime*0.2);}

    //from digital hardware to machine social network
    if(count == 2){
    divisionLine = divisionLine2 + (divisionLine3 - divisionLine2) * smoothstep(0.0,1.0, iTime*0.2);}

    //from machine social network to FE
    if(count == 3){
    divisionLine = divisionLine3 + (divisionLine4 - divisionLine3) * smoothstep(0.0,1.0, iTime*0.2);}

    //from FE to operation center
    if(count == 4){
    divisionLine = divisionLine4 + (divisionLine5 - divisionLine4) * smoothstep(0.0,1.0, iTime*0.2);}

    //from operation center to logo
    if(count == 5){
    divisionLine = divisionLine5 + (divisionLine6 - divisionLine5) * smoothstep(0.0,1.0, iTime*0.2);}

    //division line
    divisionLine = fract(iTime * 0.1);
    float d = cubicPulse(divisionLine,0.21,uv.x);
    vec3 maskColor = vec3(d);
    float pct = plot(uv,d);

    //
    //maskColor = (1.0-pct)*maskColor+pct*vec3(0.0,1.0,0.0);
    maskColor = maskColor+pct*vec3(0.0,1.0,0.0);

    //
    float PI = 3.1415926;
    float time = iTime * 0.015; 
    float scale = 0.5;

    vec4 vcolor0 = vec4(0.078,0.357,1.000,1.0);
    vec4 vcolor1 = vec4(0.102,0.369,1.000,1.0);
    vec4 vcolor2 = vec4(0.561,0.678,1.000,1.0);
    vec4 vcolor3 = vec4(1.000,0.616,0.522,1.0);
    vec4 vcolor4 = vec4(1.000,0.906,0.702,1.0);

    float step0 = 0.2 * scale;
    float step1 = 0.50 * scale;
    float step2 = 0.65 * scale;
    float step3 = 0.8  * scale;

    float offset = uv.x-0.35-time;
    float y = mod(offset,scale);

    vec4 darkColor; 
    if (y < step0) {
    float dist0 = smoothstep(0.0, step0, y);
    darkColor = mix(vcolor0, vcolor1, dist0);
    darkColor = correct(vcolor0, vcolor1, dist0);
    }

    if (y > step0 && y < step1) {
    float dist1 = smoothstep(step0, step1, y);
    darkColor = mix(vcolor1, vcolor2, dist1);
    //darkColor = correct(vcolor1, vcolor2, dist1);
    }

    else if (y > step1 && y < step2) {
    float dist2 = smoothstep(step1, step2, y);
    darkColor = mix(vcolor2, vcolor3, dist2);
    //darkColor = correct(vcolor2, vcolor3, dist2);
    }

    else if (y > step2 && y < step3) {
    float dist02 = smoothstep(step2, step3, y);
    darkColor = mix(vcolor3, vcolor4, dist02);
    darkColor = correct(vcolor3, vcolor4, dist02);
    }

    else if (y > step3){
    float dist3 = smoothstep(step3, 1.0 * scale, y);
    darkColor = mix(vcolor4, vcolor0, dist3);
    //darkColor = correct(vcolor4, vcolor0, dist3);
    }

    darkColor = sqrt(darkColor);

    //
    float qscale = 0.25;
    float qtime = iTime* 0.015;

    float qstep0 = 0.15 * qscale;
    float qstep1 = 0.35 * qscale;
    float qstep2 = 0.45 * qscale;
    float qstep3 = 0.65 * qscale;

    vec4 qcolor0 = vec4(0.502,0.651,1.000,1.0);
    float brightness = 0.15;
	float contrast = 1.0;
    float saturation = 1.0;
    qcolor0 = saturationMatrix(saturation) * qcolor0; 
    brightnessAdjust(qcolor0, brightness); 
    contrastAdjust(qcolor0, contrast); 

    vec4 qcolor1 = vec4(0.502,0.651,1.000,1.0);
    float brightness1 = 0.15;
	float contrast1 = 1.0;
    float saturation1 = 1.0;
    qcolor1 = saturationMatrix(saturation1) * qcolor1; 
    brightnessAdjust(qcolor1, brightness1); 
    contrastAdjust(qcolor1, contrast1); 

    vec4 qcolor2 = vec4(0.600,0.702,1.000,1.0);
    float brightness2 = 0.15;
	float contrast2 = 1.0;
    float saturation2 = 1.0;
    qcolor2 = saturationMatrix(saturation2) * qcolor2; 
    brightnessAdjust(qcolor2, brightness2); 
    contrastAdjust(qcolor2, contrast2); 

    vec4 qcolor3 = vec4(0.600,0.702,1.000,1.0);
    float brightness3 = 0.15;
	float contrast3 = 1.0;
    float saturation3 = 1.;
    qcolor3 = saturationMatrix(saturation3) * qcolor3; 
    brightnessAdjust(qcolor3, brightness3); 
    contrastAdjust(qcolor3, contrast3); 

    vec4 qcolor4 = vec4(0.878,0.918,1.000,1.0);
    float brightness4 = 0.15;
	float contrast4 = 1.0;
    float saturation4 = 1.;
    qcolor4 = saturationMatrix(saturation4) * qcolor4; 
    brightnessAdjust(qcolor4, brightness4); 
    contrastAdjust(qcolor4, contrast4); 

    float qoffset = uv.x-qtime;
    float qy = mod(qoffset,qscale);

    vec4 lightColor; 
    if (qy < qstep0) {
    float dist0 = smoothstep(0.0, qstep0, qy);
    lightColor = vec4(dist0);
    lightColor = mix(qcolor0, qcolor1, dist0);
    lightColor = correct(qcolor0, qcolor1, dist0);
    }

    if (qy > qstep0 && qy < qstep1) {
    float dist1 = smoothstep(qstep0, qstep1, qy);
    //lightColor = vec4(dist1);
    //lightColor = mix(qcolor1, qcolor2, dist1);
    lightColor = correct(qcolor1, qcolor2, dist1);
    }

    else if (qy > qstep1 && qy < qstep2) {
    float dist2 = smoothstep(qstep1, qstep2, qy);
    //lightColor = vec4(dist2);
    lightColor = mix(qcolor2, qcolor3, dist2);
    lightColor = correct(qcolor2, qcolor3, dist2);
    }

    else if (qy > qstep2 && qy < qstep3) {
    float dist02 = smoothstep(qstep2, qstep3, qy);
    //lightColor = vec4(dist02);
    lightColor = mix(qcolor3, qcolor4, dist02);
    lightColor = correct(qcolor3, qcolor4, dist02);
    }

    else if (qy > qstep3){
    float dist3 = smoothstep(qstep3, 1.0 * qscale, qy);
    lightColor = vec4(dist3);
    lightColor = mix(qcolor4, qcolor0, dist3);
    lightColor = correct(qcolor4, qcolor0, dist3);
    }

    lightColor = sqrt(lightColor);

    //start to move
    fragColor = mix(darkColor, lightColor, maskColor.r);

    //adding mask
    float endMask = smoothstep(0.6,1.0,uv.x);
    vec4 endMaskColor = vec4(0.741,0.812,1.000,1.0);
    fragColor = mix(fragColor,endMaskColor,endMask);

    //night
    vec4 nightColor1 = vec4(0.000,0.067,0.259,1.0);
    vec4 nightColor0 = vec4(0.000,0.165,0.620,1.0);
    vec4 nightColor = mix(nightColor0, nightColor1, pow(uv.x,1.));
    nightColor = sqrt(nightColor);

    //iStart
    float iStart = 5.0;

    //night to awake
	fragColor = mix(nightColor, fragColor, smoothstep(0.0,1.0,iStart*0.2));

    //Debug
    //fragColor = vec4(maskColor,1.0);
    //fragColor = vec4(lightColor);
    //fragColor = vec4(darkColor);

    // Black bars at the sides
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    
    if (uv.y<-0.1&&uv.y > -1.2){
    fragColor = vec4(darkColor);
    }
    if (uv.y<-1.3&&uv.y > -2.4){
    fragColor = vec4(lightColor);
    }
}

