void mainImage(out vec4 O, in vec2 I)
{
    float sdButton = sdBox(iMouse.xy -  BUTTON_ORIGIN, BUTTON_SZ);
    if (sdBox(I - BUTTON_ORIGIN, BUTTON_SZ) <= 0.) {
        O = vec4(0,0,1. - step(0.5, iMouse.z)*step(0., -sdButton)*0.5,1);
        vec2 inner_sz = BUTTON_SZ - 2.;
        float sdf = min(sdEquilateralTriangle(-(I - BUTTON_ORIGIN - vec2(3,0)).yx/inner_sz.y)*inner_sz.y,
                        sdBox(I - BUTTON_ORIGIN + vec2(inner_sz.y,0), vec2(2,inner_sz.y)));
        O.rgb = mix(O.rgb, vec3(1,1,1), smoothstep(1., -1., sdf));
        return;
    }

    O = vec4(0,0,0,1);
    mat2 rot_yz, rot_xy;            // rotations
    if (iMouse.x < 0.5 || sdButton <= 0.) {
      // https://graphtoy.com/?f1(x,t)=(floor(x/5)+smoothstep(0,1,fract(x/5)*10))*0.5&v1=true&f2(x,t)=&v2=false&f3(x,t)=&v3=false&f4(x,t)=&v4=false&f5(x,t)=&v5=false&f6(x,t)=&v6=false&grid=true&coords=0,0,7.451055876709886
      rot_xy = rot(-iTime/2.);
      //rot_xy = rot(float(-iFrame)/70.);
      rot_yz = rot(1.2);
    } else {
      vec2 M = (iMouse.xy / R2.xy * vec2(1., -0.7) + vec2(0.5, 0.1)) * 2. * PI;
      rot_yz = rot(-M.y);
      rot_xy = rot(-M.x);
    }
    vec3 p = vec3(0,0,START_DIST);                                       // current ray pos
    p.yz *= rot_yz;  p.xy *= rot_xy;

    vec3 D = normalize(vec3(I+I, -FOV*R2.y) - vec3(R2.xy, 0.));    // ray direction
    D.yz *= rot_yz;  D.xy *= rot_xy;

    vec3 R3 = Res2to3(R2.xy);
    float scale2 = R3.x;
    float c = 0.;
    float n_c = 0.;
    float cmax = 0.;
    float st = 0.5/R3.z;
    int n_steps = int(3./st);
    vec3 st1 = st * D;
    int n;
    float axis = 99.;
    for (p += D*(START_DIST - 1.5), n = 0; n < n_steps; n++, p += st1) {
        vec3 coord = (p + 0.5) * scale2;
        if (coord.x >= 0. && coord.y >= 0. && coord.z >= 0. &&
            coord.x < R3.x && coord.y < R3.y && coord.z < R3.z) {
#if PLOT_AVG
            c += T(coord).x;
            n_c++;
#endif
#if PLOT_MAX
            cmax = max(cmax, T(coord).x);
#endif
#define SDF_AXIS(XX)   distance(coord.XX + coord.XX, R3.XX)
            axis = min(
                    min(axis, SDF_AXIS(xy)),
                    min(SDF_AXIS(yz), SDF_AXIS(xz)));
#define SDF_EDGE(XX)   distance(mod(coord.XX+coord.XX + R3.XX, R3.XX+R3.XX), R3.XX)
            axis = min(
                    min(axis, SDF_EDGE(xy)),
                    min(SDF_EDGE(yz), SDF_EDGE(zx)));
        }
    }

#if PLOT_AVG
	if (n_c > 0.) {
        c = log(max(1., c*c*10000.)/n_c)/15.;
        //c += c*2.2/n_c;
        //c = pow(c*20., 0.5)*1.5/n_c;
    }
#endif
    //O.rgb = col(c + cmax);
    O.rgb = col(cmax) + col2(c)/2.;
    if (iMouse.z > 0.5) O.rgb = mix(O.rgb, vec3(1), smoothstep(1., 0., axis)/2.);
}

