// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 position, float iTime)
{
    vec2 p = (position + mod(iTime, 200.) * 1500. + 50.0);
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float hash13(vec3 position, float iTime)
{
    vec3 p3 = vec3(position) + iTime * 500. + 50.0;
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 Res2to3(in vec2 R2) {
    return vec3(floor(pow(R2.x*R2.y, 1./3.)));  // 1200 / 80 = 13
}

vec2 I3to2(in vec3 I3, in vec3 R3, in vec2 R2) {
    I3 = floor(clamp(I3, vec3(0), R3-1.));
    float offset = I3.x + I3.y*R3.x + I3.z*R3.x*R3.y;
    vec2 I2;
    I2.x = mod(offset, R2.x);
    I2.y = floor(offset/R2.x);
    return I2;
}

void I2to3(out vec3 I3, in vec2 I, in vec3 R3, in vec2 R2) {
    I = floor(I);
    //I -= 0.5;
    float offset = I.x + I.y*R2.x;
    I3.x = mod(offset, R3.x);
    I3.y = mod(floor(offset/R3.x), R3.y);
    I3.z = min(floor(offset/R3.x/R3.y), R3.z-1.);
}

// https://iquilezles.org/articles/distfunctions2d
float sdEquilateralTriangle( in vec2 p ) {
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}
float sdBox( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

mat2 rot(float a) { float s = sin(a), c = cos(a); return mat2(c,-s,s,c); }

