#define IS_NOT_MOBILE

const float maxDist = pow(7.,2.);
const float horizon = 1.;
const float disk = 3.;
const float diskT = 0.05;
vec3 col = vec3(0);
float stepSize;
const int maxSteps = 512;
const float orbit = 2.2;
const float gap = .4;
const float spin = .05;
const float warp = 0.;
const float focalLength = 1.2;

int ssaa = 1;

mat2x2 rotMat(float t)
{
    return mat2x2(
        cos(t), sin(t),
        -sin(t), cos(t)
    );
}

float rand(float t)
{
    return fract(sin(t*234.89));
}

vec3 bhuv(vec3 p)
{
    vec3 buv = 1.-vec3(length(p.xy) - disk + gap * 2.5)/disk;
    buv.y = ((atan(p.y/p.x)+0.5*pi)/pi + (p.x > 0.?1.:0.))*.5;
    buv.z = p.z/diskT;
    if (buv.x > 1.) buv.x = 0.0;
    return clamp(buv, 0., 1.);
}

vec3 diskCol(vec3 p)
{
    vec3 buv = bhuv(p);
    buv.y += iTime*spin;

    float x = pow(buv.x, 2.);
    float s = 0.5;
    vec3 o = texture(iChannel2, vec3(buv.x *= 3., 0.5+s*sin(buv.y*pi*2.), 0.5+s*cos(buv.y*pi*2.))).xxx * vec3(1, .5, 0.2);
    s *= 0.5;
    o += texture(iChannel2, vec3(buv.x*=.5, 0.5+s*sin(buv.y*pi*2.), 0.5+s*cos(buv.y*pi*2.))).xxx * vec3(0, 0.1, 1);
    s *= 0.5;
    o += texture(iChannel2, vec3(buv.x*=.5, 0.5+s*sin(buv.y*pi*2.), 0.5+s*cos(buv.y*pi*2.))).xxx * vec3(0.6, 1, 0.4);
    return o * x;
}

const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}
//hash by IQ https://www.shadertoy.com/view/XlXcW4
vec2 ps(uvec3 x){
    return hash(x).xy * 2. - 1.;
}


void mainImage( out vec4 O, in vec2 U )
{

    vec2 r = iResolution.xy;
    int i = 0;
    vec2 muv = iMouse.z > 0.?(2.*iMouse.xy - r.xy)/r.xy:vec2(iTime, sin(iTime*.3)*.5);



    // Performance adjusting ssaa //

    ssaa = iFrame < 20?2:
        4*int(max(iFrameRate*.1,min(iFrameRate*.4, ceil(pow(1.1, iFrameRate - 50.)*3.))));

    ///////////////////////////////




    for (int m = 0; m < ssaa; m++){

        vec2 offset = ps(uvec3(iFrame + m, int(U.y) - m, int(U.x)+m))*2.;
        // SSAA
        float c = 1./(sqrt(length(offset))+0.3);
        //Varied contribution of ssaa sample based on distance from centre
        vec2 uv = (2.*U - r.xy
            +offset
        )/r.y;

        /*
        vec3 dirV = normalize(vec3(uv.x, 1.0, uv.y)) * mat3x3(
            cos(iTime), -sin(iTime), 0,
            sin(iTime), cos(iTime), 0,
            0, 0, 1
        );/*/


        vec3 camDir = normalize(vec3(sin(muv.xx + vec2(0,11)), -muv.y*0.5));
        vec3 r = cross(camDir, vec3(0, 0, 1));
        vec3 u = cross(r, camDir);
        vec3 dirV = normalize(camDir * focalLength + r * uv.x + u * uv.y);
        //*/

        vec3 camPos = vec3(sin(-muv.xx - vec2(0,11)) * disk * orbit, muv.y*orbit*1.2);

        float g = 0.;
        vec3 p = camPos;

        for (i = 0; i<maxSteps; i++)
        {
            stepSize = min(pow(length(p)/8., 0.5), abs(p.z)+diskT*rand(p.x + p.y * pi + p.z / pi));
            p += stepSize * dirV;

            if (abs(p.z) < diskT)
            {
                col += diskCol(p)*0.5*c;
            }

            g = (stepSize / pow(length(p), 3.));
            dirV = normalize(dirV - p * horizon*g);

            p.xy *= rotMat(g*warp);
            // rotating black hole, simulate dragging of space
            // by rotating space by the strength of gravity

            if (length(p) < horizon || dot(p,p) > maxDist){
                break;
            }
        }

        if (i == maxSteps || dot(p,p) > maxDist){ 
            col += c
                //The skybox
                * pow(clamp(texture(iChannel1, fract(dirV.yz)).xxx,0.,1.), vec3(8));
        }

    }

    col /= float(ssaa);
    #ifdef IS_NOT_MOBILE
        col = flim(col, 0.0);
    #endif

    /*
    O = vec4(float(i) / float(maxSteps));//col.xyzz);
    /*/
    O = vec4(col.xyzz);//*/
}

