const float pi = 3.14159;
#define flim(x, y) flim_transform(iChannel0, x, y)






/***********************************

flim - Filmic Color Transform

Input Color Space:   Linear BT.709 I-D65
Output Color Space:  sRGB 2.2

Shadertoy Unit:
  Common

Description:
  Experimental port of flim for GLSL/Shadertoy
  matching flim v1.1.0.
  The common unit contains most of the code, and
  receives gamut extension matrices from a buffer.

Author:
  Bean (beans_please on Shadertoy)
  Minified by 01000001

Original Repo:
  https://github.com/bean-mhm/flim

Original Shader:
  https://www.shadertoy.com/view/dd2yDz

***********************************/

// Parameters

const float pe = 4.3; const vec3 fpff = vec3(1); const float fpffs = 0.;
const float egr = 1.05; const float egg = 1.12; const float egb = 1.045;
const float grr = .5; const float ggr = 2.; const float gbr = .1;
const float grm = 1.; const float ggm = 1.; const float gbm = 1.; const float flsi = -10.;
const float flsa = 22.; const float fsx = .44; const float fsy = .28;
const float fssx = .591; const float fssy = .779; const float fe = 6.;
const float fd = 5.; const vec3 pb = vec3(1); const float pfe = 6.;
const float pfd = 27.5; const float bp = -1.; const vec3 pff = vec3(1);
const float pffs = 0.; const float ms = 1.02;
float wrap(float i, float s, float e){return s + mod(i - s, e - s);}
float lerp(float a, float b, float t){return a + t * (b - a);}
vec3 lerp(vec3 a, vec3 b, float t){return a + t * (b - a);}
float mrc(float i, float is, float ie, float os, float oe){
    float t = clamp((i - is) / (ie - is), 0., 1.);
    float v = os + t * (oe - os);
    return v; }
vec3 rth(vec3 rgb){
    float ca, ci, h, s, v, cd;
    vec3 c;
    ca = max(rgb[0], max(rgb[1], rgb[2]));
    ci = min(rgb[0], min(rgb[1], rgb[2]));
    cd = ca - ci;
    v = ca;
    if (ca != 0.) s = cd / ca;
    else s = 0.0; h = 0.0;
    if (s == 0.) h = 0.0; 
    else{
        c = (vec3(ca) - rgb.xyz) / cd;

        if (rgb.x == ca) h = c[2] - c[1];
        else if (rgb.y == ca) h = 2.+ c[0] - c[2];
        else h = 4.+ c[1] - c[0];
        h /= 6.0;
        if (h < 0.) h += 1.0; }
    return vec3(h, s, v); }
vec3 htr(vec3 hsv){
    float i, f, p, q, t, h, s, v;
    vec3 rgb;
    h = hsv[0];
    s = hsv[1];
    v = hsv[2];
    if (s == 0.) rgb = vec3(v, v, v);
    else {
        if (h == 1.) h = 0.0;
        h *= 6.0;
        i = floor(h);
        f = h - i;
        rgb = vec3(f, f, f);
        p = v * (1.- s);
        q = v * (1.- (s * f));
        t = v * (1.- (s * (1.- f)));
        if (i == 0.) rgb = vec3(v, t, p);
        else if (i == 1.) rgb = vec3(q, v, p);
        else if (i == 2.) rgb = vec3(p, v, t);
        else if (i == 3.) rgb = vec3(p, q, v);
        else if (i == 4.) rgb = vec3(t, p, v);
        else rgb = vec3(v, p, q); }
    return rgb; }
vec3 bhs(vec3 col, float hue, float sat, float value){
    vec3 hsv = rth(col);
    hsv[0] = fract(hsv[0] + hue + 0.5);
    hsv[1] = clamp(hsv[1] * sat, 0., 1.);
    hsv[2] = hsv[2] * value;
    return htr(hsv); }
float rgb_avg(vec3 i){
    return (i.x + i.y + i.z) / 3.0; }
float rgb_sum(vec3 i){
    return i.x + i.y + i.z; }
float rgb_max(vec3 i){
    return max(max(i.x, i.y), i.z); }
float rgb_min(vec3 i){
    return min(min(i.x, i.y), i.z); }
vec3 uo(vec3 i, float bp, float wp){
    float mono = rgb_avg(i);
    float mono2 = mrc(mono, bp / 1000., 1.- (wp / 1000.), 0., 1.);
    return i * (mono2 / mono); }
// https://www.desmos.com/calculator/khkztixyeu
float ss(float i, float tx, float ty, float sx, float sy){
    i = clamp(i, 0., 1.);
    tx = clamp(tx, 0., 1.);
    ty = clamp(ty, 0., 1.);
    sx = clamp(sx, 0., 1.);
    sy = clamp(sy, 0., 1.);
    float slope = (sy - ty) / (sx - tx);
    if (i < tx){
        float toe_pow = slope * tx / ty;
        return ty * pow(i / tx, toe_pow); }
    if (i < sx){
        float intercept = ty - (slope * tx);
        return slope * i + intercept; }
    float shoulder_pow = -slope / (((sx - 1.) / pow(1.- sx, 2.)) * (1.- sy));
    return (1.- pow(1.- (i - sx) / (1.- sx), shoulder_pow)) * (1.- sy) + sy; }
float dmf(float mono, float max_density){
    float offset = pow(2., flsi);
    float fac = mrc(log2(mono + offset), flsi, flsa, 0., 1.);
    fac = ss(fac, fsx, fsy, fssx, fssy);
    fac *= max_density;
    fac = pow(2., -fac);
    return clamp(fac, 0., 1.); }
vec3 cl(vec3 i, vec3 sensitivity_tone, vec3 dye_tone, float max_density){
    vec3 sensitivity_tone_norm = sensitivity_tone / rgb_sum(sensitivity_tone);
    vec3 dye_tone_norm = dye_tone / rgb_max(dye_tone);
    float mono = dot(i, sensitivity_tone_norm);
    float mixFac = dmf(mono, max_density);
    return lerp(dye_tone_norm, vec3(1.), mixFac); }
vec3 rgbd(vec3 i, float e, float max_density){
    i *= pow(2., e);
    vec3 result = cl(i, vec3(0, 0, 1), vec3(1, 1, 0), max_density);
    result *= cl(i, vec3(0, 1, 0), vec3(1, 0, 1), max_density);
    result *= cl(i, vec3(1, 0, 0), vec3(0, 1, 1), max_density);
    return result; }
vec3 emr(float ph, float s, float r, float m){
    vec3 result = htr(vec3(wrap(ph + (r / 360.), 0., 1.), 1./ s, 1.));
    result /= rgb_sum(result);
    result *= m;
    return result; }
mat3 gem(float rs,float gs,float bs,float rr,float gr, float br, float rm, float gm, float bm){
    mat3 m;
    m[0] = emr(0., rs, rr, rm);
    m[1] = emr(1./ 3., gs, gr, gm);
    m[2] = emr(2./ 3., bs, br, bm);
    return m; }
vec3 nap(vec3 i, vec3 bex){
    i = rgbd(i, fe, fd);
    i *= bex;
    i = rgbd(i, pfe, pfd);
    return i; }
bool flm(sampler2D mc, out mat3 em, out mat3 emi){
    const float valid_w = 2.0;
    vec4 row;
    row = texelFetch(mc, ivec2(0, 0), 0);
    if (row.w != valid_w)
        return false;
    em[0] = row.xyz;

    row = texelFetch(mc, ivec2(1, 0), 0);
    if (row.w != valid_w)
        return false;
    em[1] = row.xyz;

    row = texelFetch(mc, ivec2(2, 0), 0);
    if (row.w != valid_w)
        return false;
    em[2] = row.xyz;
    row = texelFetch(mc, ivec2(3, 0), 0);
    if (row.w != valid_w)
        return false;
    emi[0] = row.xyz;

    row = texelFetch(mc, ivec2(4, 0), 0);
    if (row.w != valid_w)
        return false;
    emi[1] = row.xyz;

    row = texelFetch(mc, ivec2(5, 0), 0);
    if (row.w != valid_w)
        return false;
    emi[2] = row.xyz;
    return true; }
vec3 flim_transform(sampler2D mc, vec3 i, float e){
    i = max(i, 0.);
    i *= pow(2., pe + e);
    i = min(i, 5000.);
    mat3 em, emi;
    if (!flm(mc, em, emi)){ return vec3(1, 0, 1); }
    vec3 bex = pb * em;
    const float big = 10000000.0;
    vec3 white_cap = nap(vec3(big, big, big), bex);
    i = lerp(i, i * fpff, fpffs);
    i *= em;
    i = nap(i, bex);
    i *= emi;
    i = max(i, 0.);
    i /= white_cap;
    if (bp == -1.){
        vec3 black_cap = nap(vec3(0.), bex);
        black_cap /= white_cap;
        i = uo(i, rgb_avg(black_cap) * 1000., 0.);
    }else{ i = uo(i, bp, 0.); }
    i = lerp(i, i * pff, pffs);
    i = clamp(i, 0., 1.);
    float mono = rgb_avg(i);
    float mixFac = (mono < 0.5) ? mrc(mono, 0.05, 0.5, 0., 1.) : mrc(mono, 0.5, 0.95, 1., 0.);
    i = lerp(i, bhs(i, 0.5, ms, 1.), mixFac);
    i = clamp(i, 0., 1.);
    i = pow(i, vec3(1./ 2.2));
    return i; }

/***********************************

End of flim's implementation for Shadertoy Unit: Common

***********************************/

