// Audio Analyser 0.5.230727 by QuantumSuper
// analyse audio spectrum to estimate bpm, offset & beat count
// looks at spectrum history, assumes static resolution & frame rate
// Buffer A: spectrum history
// Buffer B: filter beats
// Buffer C: analyse (filtered) spectrum
// Image: design mockup to visualize results (& highlight use for designs to build upon & debugging/refining)
// 
// - use with audio in iChannel0 of Buffer A -


#define showBuffer true


float aaStep( float fun){return smoothstep( fwidth(fun), .0, fun);} //simple antialiasing
float sdCircle( vec2 p, float r){return length(p)-r;}
float sdBox( vec2 p, vec2 b){vec2 d = abs(p) - b; return length(max(d,.0)) + min(max(d.x,d.y),.0);} //source: https://iquilezles.org/articles/distfunctions2d/
float sdSegment( vec2 p, vec2 a, vec2 b){vec2 pa = p - a, ba = b - a; float h = clamp( dot(pa,ba)/dot(ba,ba), 0., 1.); return length(pa - ba*h);} //source: https://iquilezles.org/articles/distfunctions2d/


void mainImage( out vec4 fragColor, in vec2 fragCoord){

    // TOOLS

    // Read compression values from Buffer A
    vec4 fft, ffts;
    for (int n=0;n<4;n++) 
        fft[n] = getDat( iChannel0, vec2( 0, n)).a,
        ffts[n] = getDat( iChannel0, vec2( 0, n+4)).a;

    // Read analysis results from Buffer C
    float count         = getDat( iChannel2, vec2(0,0)).a; //beat count (0..15)
    float bpm           = getDat( iChannel2, vec2(0,1)).a; //bmp estimate (>90..180)
    float frameRate     = getDat( iChannel2, vec2(0,2)).a; //frame rate average
    float isPause       = getDat( iChannel2, vec2(0,3)).a; //is bass less high than average
    float timeFirstBeat = getDat( iChannel2, vec2(0,4)).a; //time difference to beat drop (16 beat range)
    float isBeat        = getDat( iChannel2, vec2(0,5)).a; //is bass high
    float isFlank       = getDat( iChannel2, vec2(0,6)).a; //is bass just getting high


    // DESIGN

    // Draw status display
    vec2 uv = (2.*fragCoord-iResolution.xy) / max(iResolution.x, iResolution.y); //long edge -1 to 1, square aspect ratio
    vec3 col = vec3(0);

    vec4 tmp = vec4(.46,.26,.34,.22); //top windows
    col += (uv.y > tmp.y)?
        aaStep( sdBox( uv-vec2(-1,1)*tmp.xy, tmp.zw)) * getDat( iChannel1, ((uv-vec2(-1,1)*tmp.xy)/tmp.zw+vec2(1))/2.*iResolution.xy).r : //filter
        aaStep( sdBox( uv-vec2(-1,1)*tmp.xy, tmp.zw)) * getDat( iChannel0, ((uv-vec2(-1,1)*tmp.xy)/tmp.zw+vec2(1))/2.*iResolution.xy * vec2(1,8./512.)).r; //bass
    col += aaStep( sdBox( uv-tmp.xy, tmp.zw)) * getDat( iChannel2, ((uv-tmp.xy)/tmp.zw+vec2(1))/2.*iResolution.xy).r / max(.001,getDat( iChannel2, vec2(0,(180./bpm-1.)*iResolution.y)).r); //bpm certainties
    col += aaStep( sdBox( uv-tmp.xy, tmp.zw)) * aaStep( abs(uv.y - tmp.y + (3.-360./bpm)*tmp.w) - .001); //bpm line

    tmp = vec4(.0,.13,.8,.08); //long mid  window
    col += aaStep( sdBox( uv+tmp.xy, tmp.zw)) * (getDat( iChannel0, ((uv+tmp.xy)/tmp.zw+vec2(1))/2.*iResolution.xy * vec2(1,4./512.)).bbb - .95)/.05 ; //pause   

    tmp = vec4(0,.26,.025,.1); //middle top dots
    col += aaStep( sdCircle( uv-tmp.xy-tmp.xw, tmp.z)) * (isBeat*.95+.05) + clamp(.01/max(.001,sdCircle( uv-tmp.xy-tmp.xw, tmp.z)),.0,1.) * isFlank;
    col += aaStep( sdCircle( uv-tmp.xy+tmp.xw, tmp.z)) * (float(isPause > .13)*.95+.05);

    for (float n=0.; n<4.; n++) //large dots bottom
        col += aaStep( sdCircle( uv+vec2(.6-n*.4,.35), .08)) * float(abs(n-mod(count,4.)+.5)<.5); //count from 1 to 4

    for (float n=0.; n<16.; n++) //small dots bottom
        col += aaStep( sdCircle( uv+vec2(.75-n*.1,.48), .01)) * float(abs(n-count+.5)<.5); //count from 1 to 16

    tmp = vec4(.9,-.5,.5,.003); //side bars
    col += aaStep( sdSegment( vec2(abs(uv.x),uv.y), tmp.xy, tmp.xz) - tmp.w*4.) * aaStep(fract((uv.y-tmp.y)/abs(-tmp.y+tmp.z)*4.) - .03); //side scales
    col += aaStep( sdSegment( uv, tmp.xy, tmp.xy + vec2( 0, abs(tmp.z-tmp.y) * frameRate/120.)) - tmp.w); //framerate percentage of 120fps
    col += aaStep( sdSegment( vec2(-uv.x,uv.y), tmp.xy, tmp.xy + vec2( 0, abs(tmp.z-tmp.y) * pow(1.-isPause,10.))) - tmp.w); //isPause, inverted log

    if (showBuffer){
        tmp.x = mod((iTime-timeFirstBeat)/(60./bpm*2.),32.);
             if (tmp.x< 8.) ;
        else if (tmp.x<10.) col = getDat( iChannel0, fragCoord).ggg; //waveform
        else if (tmp.x<12.) col = getDat( iChannel0, fragCoord).rrr; //audio spectrum
        else if (tmp.x<14.) col = (uv.y<0.)? getDat( iChannel0, fragCoord*vec2(1,8./512.)).rrr : getDat( iChannel1, fragCoord).rrr; //filter beats
        else if (tmp.x<16.) col = getDat( iChannel2, fragCoord).rrr / max(.001,getDat( iChannel2, vec2(0,(180./bpm-1.)*iResolution.y)).r); //bpm certainties
        else if (tmp.x<17.) col = getDat( iChannel2, fragCoord).ggg / min(iResolution.x, iResolution.y); //maximum search
        else if (tmp.x<18.) col = getDat( iChannel0, fragCoord).bbb; //average amplitude maximum
        else if (tmp.x<19.) col = (getDat( iChannel0, fragCoord*vec2(1,8./512.)).b-.95)/.05 * vec3(.9); //filter average amplitude maximum
        else if (tmp.x<20.) col = getDat( iChannel0, fragCoord/vec2(1,iResolution.y/8.)).aaa; //compression amplitudes over time 
        else if (tmp.x<22.) col = vec3(step(.0,(fragCoord.y/iResolution.y-.9))) + .7*dot( step(.0,fft-abs(floor(fragCoord.x/iResolution.x*4.-vec4(0,1,2,3)))-fragCoord.y/iResolution.y/.9),vec4(1)) + .3*dot( step(.0,ffts-abs(floor(fragCoord.x/iResolution.x*4.-vec4(0,1,2,3)))-fragCoord.y/iResolution.y/.9),vec4(1)); //compression amplitudes
    }

    col += .1 * (1.-length(col)); //background
    col -= length(uv) * .03; //vignette
    fragColor = vec4( col, 1.);
}

