// COMMON (0.0) of Audio Analyser by QuantumSuper
// 
// - use with audio in iChannel0 of Buffer A -

#define getDat( buf, addr) texelFetch( buf, ivec2(addr), 0)
#define getDatN( buf, addr) getDat( buf, addr).r / getDat( buf, addr).b

buffer_a_code:
// BUFFER A (1.0) of Audio Analyser by QuantumSuper
// audio spectrum by brightness of frequency by y-axis at time by x-axis (0 now; >0 historic)
// .r: amplitude 
// .g: waveform
// .b: average amplitude
// .a: ( fft, ffts, timestamp, 0..0) variables
// 
// - use with audio in iChannel0 of Buffer A -


#define TRACKDURATIONINFRAMES 3600.
vec4 fft, ffts; //compressed frequency amplitudes


void compressFft(){ //v1.2, compress sound in iChannel0 to simplified amplitude estimations by frequency-range
    fft = vec4(0), ffts = vec4(0);

	// Sound (assume sound texture with 44.1kHz in 512 texels, cf. https://www.shadertoy.com/view/Xds3Rr)
    for (int n=0;n<3;n++) fft.x  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //bass, 0-517Hz, reduced to 0-258Hz
    for (int n=6;n<8;n++) ffts.x  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //speech I, 517-689Hz
    for (int n=8;n<14;n+=2) ffts.y  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //speech II, 689-1206Hz
    for (int n=14;n<24;n+=4) ffts.z  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //speech III, 1206-2067Hz
    for (int n=24;n<95;n+=10) fft.z  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //presence, 2067-8183Hz, tenth sample
    for (int n=95;n<512;n+=100) fft.w  += texelFetch( iChannel0, ivec2(n,0), 0 ).x; //brilliance, 8183-44100Hz, tenth2 sample
    fft.y = dot(ffts.xyz,vec3(1)); //speech I-III, 517-2067Hz
    ffts.w = dot(fft.xyzw,vec4(1)); //overall loudness
    fft /= vec4(3,8,8,5); ffts /= vec4(2,3,3,23); //normalize

	//for (int n=0;n++<4;) fft[n] *= 1. + .3*pow(fft[n],5.); fft = clamp(fft,.0,1.); //limiter? workaround attempt for VirtualDJ [WIP]
}

float estMax(float p){ //estimate changing maximum over time of sound texel by weighted amplitude tracking
    float curVal = clamp( getDat( iChannel0, vec2(p/iResolution.y*512.,0)).x, .0, 1.); //current amplitude
    float maxVal = clamp( getDat( iChannel1, vec2(0,p)).z, .0, 1.); //latest max amp

    if (curVal >= maxVal) maxVal = 0.; //check for new max    

    if (maxVal != 0.) //avoid uninitialized state & deprecated maxVal
        curVal *=    1./TRACKDURATIONINFRAMES,
        maxVal *= 1.-1./TRACKDURATIONINFRAMES;

    return maxVal+curVal; //returns value between 0 and 1
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    compressFft(); //initializes fft, ffts
    fragColor = (fragCoord.x<1.)? vec4( 
        getDat( iChannel0, vec2( fragCoord.y/iResolution.y*512., 0)).x, //.r amplitudes
        getDat( iChannel0, vec2( fragCoord.y/iResolution.y*512., 1)).x, //.g waveform (does not work in VirtualDJ)
        estMax( fragCoord.y), //.b average amplitude
        (fragCoord.y<4.)? fft[int(fragCoord.y)] : //.a compression part 1
        (fragCoord.y<8.)? ffts[int(fragCoord.y)-4] : //.a compression part 2
        (fragCoord.y<9.)? iTime : //.a timestamp
        0.) //.a empty
        : getDat( iChannel1, fragCoord-vec2(1,0)); //history   
}

buffer_b_code:
// BUFFER B (0.1) of Audio Analyser by QuantumSuper
// filter of audio spectrum
// .r: beats (1D)
// .g: 0
// .b: 0
// .a: 0
// 
// - use with audio in iChannel0 of Buffer A -


void mainImage( out vec4 fragColor, in vec2 fragCoord){

    if (fragCoord.x < 1.) { //new data

        vec3 pos = 5. * vec3(0,1,2); //defines spread over frames (max beat width)      

        vec3 amp = vec3(
            (getDatN( iChannel0, vec2(pos.x,1)) + getDatN( iChannel0, vec2(pos.x,2))), //average-normalized amplitudes of 86-258Hz
            (getDatN( iChannel0, vec2(pos.y,1)) + getDatN( iChannel0, vec2(pos.y,2))),
            (getDatN( iChannel0, vec2(pos.z,1)) + getDatN( iChannel0, vec2(pos.z,2)))
            ) * .5; //normalize

        fragColor = vec4(0);
        if (amp.y>.92 && amp.x<amp.y && amp.z<amp.y) //super simple max rise detection
            fragColor.r = 1.;  //beat

    } else
        fragColor = getDat( iChannel1, fragCoord-vec2(1,0)); //history 
}

buffer_c_code:
// BUFFER C (0.9) of Audio Analyser by QuantumSuper
// analyse filtered beats
// .r: bpm estimation, brightness corresponds to certainty, linear 90-180bpm
// .g: buffer to find maximum
// .b: -
// .a: variables ( count (0..15), bmp estimate (>90..180), frame rate estimate, isPause (fract), timeFirstBeat, beat (bool), flank (bool), 0..0)
// 
// - use with audio in iChannel0 of Buffer A -


float isFlank( float shift){ //simple comparison of bool-like data
    return (getDat( iChannel1, vec2( mod(shift,iResolution.x), 0)).r > getDat( iChannel1, vec2( mod(shift+1.,iResolution.x), 0)).r)? 1. : 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord){

    // Set space for search algorithm
    float isVert = float(iResolution.x < iResolution.y); //vertical screen orientation, bool-like
    vec2 orient = vec2( 1.-isVert, isVert); //(1,0) horizontal/square; (0,1) vertical
    vec2 myResolution = bool(isVert)?
        iResolution.yx : iResolution.xy;
    vec2 myFragCoord = bool(isVert)?
        fragCoord.yx : fragCoord.xy; 


    // Estimate average frame rate
    float estFrameRate = (iFrame < int(iResolution.x))? //buffer not yet fully filled 
        (iFrame < 1)? //initialization
            30. : //guess frame rate
            float(iFrame) / (iTime - getDat( iChannel0, vec2( iFrame, 8)).a) : //estimate average frame rate with less accuracy
        iResolution.x / (iTime - getDat( iChannel0, vec2( iResolution.x-1., 8)).a); //estimate average frame rate


    // Estimate BPM certainties (comb-like sum)
    float beat = getDat( iChannel1, vec2(0)).r; //beat ongoing, bool-like
    float flank = isFlank(0.); //rising flank of beat, bool-like
    float amp = 0.;

    if (bool(flank)) //recalculate bpm probability if there is a new beat

        if (fragCoord.x < 1.){

            float minStep = estFrameRate / 3.; //assume max 180 bpm (3bps)
            float stepSize = minStep * (1.+myFragCoord.y/myResolution.y); //assume min >90bpm (>1.5bps)

            for ( float n=0.; n++ < myResolution.x/stepSize; ) //go through beat history at bpm-rate steps
                amp += isFlank( n*stepSize); //add found beat flanks
            amp /= myResolution.x/stepSize; //normalize          

            float prevAmp = getDat( iChannel2, myFragCoord*orient.yx).r;
            amp = (amp + prevAmp*359.) / 360.; //weight, assume stable bpm over about 2 minutes at 180bpm (or 4 min at 90bpm)

        } else 
            amp = getDat( iChannel2, myFragCoord-orient).r; //history     
    else 
        amp = getDat( iChannel2, fragCoord).r; //copy old data (no new info) 

    fragColor.r = amp;


    // Find BPM estimate (search algorithm)
    vec2 compPos = (myFragCoord.x < 1.)? //is first line
        myFragCoord.y + orient.yx : //first neighbour
        vec2(
            getDat( iChannel2, myFragCoord - orient).g,
            getDat( iChannel2, myFragCoord - orient + mod( myFragCoord.x + myFragCoord.y + 1., myResolution.y) * orient.yx ).g ); //next new neighbour

    amp = (getDat( iChannel2, compPos.x*orient.yx).r < getDat( iChannel2, compPos.y*orient.yx).r)? //compare current compPos-bpm-certainties
        compPos.y : compPos.x ; //keep only position of higher certainty
    fragColor.g = amp;  

    float bpm = (iFrame<1)?
        127. : //guess
        180. / (1. + getDat( iChannel2, ceil(iResolution.y*.5)*orient).g / iResolution.y); //most certain beat-rate, range from >90 to 180 bpm


    // Find Beat-offset (watchdog)
    float isPause = 1. - clamp( getDat( iChannel0, vec2(0,1)).b + getDat( iChannel0, vec2(0,2)).b, .0, 2.)/2.; //inverse average max, bool-like intent but fract usage

    float timeFirstBeat = (iFrame < 1)? 0. : //initialization
        (isPause<.05 && getDat( iChannel2, vec2(5,3)).a > .13)? //there was a pause but is no more
            mod( iTime, 60./bpm*16.) : //new first beat
            getDat( iChannel2, vec2(0,4)).a; //old first beat

    float count = mod( (mod( iTime, 60./bpm*16.)-timeFirstBeat) / 60.*bpm, 16.); //count to 16 at bpm from first beat


    // Save variables 
    if (fragCoord.x<1.)
             if (fragCoord.y<1.) amp = count; //beat count (0..15)
        else if (fragCoord.y<2.) amp = bpm; //bmp estimate (>90..180)
        else if (fragCoord.y<3.) amp = estFrameRate; //frame rate average
        else if (fragCoord.y<4.) amp = isPause; //is average max-bass low
        else if (fragCoord.y<5.) amp = timeFirstBeat; //time difference to sudden bass increase
        else if (fragCoord.y<6.) amp = beat; //is bass high
        else if (fragCoord.y<7.) amp = flank; //is bass beginning
        else amp = 0.; //empty
    else amp = getDat( iChannel2, fragCoord-vec2(1,0)).a; //history

    fragColor.a = amp;
}

