// pixel scale
#define SCALEFAC 1.
// color unification
#define COLFAC 1.
// outline threshold
#define THRESHLD 0.05
// line thickness
#define STEPS 3
// line color
#define LINECOL vec4(0.2,0.2,0.2,1.)

vec4 colorAt(vec2 uv){
    vec4 t = texture(iChannel0,uv);
    float r = floor( t.r * 255. / COLFAC ) * COLFAC / 255.;
    float g = floor( t.g * 255. / COLFAC ) * COLFAC / 255.;
    float b = floor( t.b * 255. / COLFAC ) * COLFAC / 255.;
    return vec4(r*1.,g*1.,b,t.a);
}

vec4 colorAtO(vec2 uv){
    return texture(iChannel0,uv);
}

vec2 deScale(vec2 uv, float factor){
    float u = floor( uv.x / factor) * factor;
    float v = floor( uv.y / factor) * factor;
    return vec2(u,v);
}

float colorDe(vec4 col1, vec4 col2){
    vec4 t = col1 - col2;
    return (abs(t.r) + abs(t.g) + abs(t.b))/3.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    // vec2 uv = fragCoord/iResolution.xy;

    vec2 uvO = fragCoord/iResolution.xy;
    if(uvO.x < 0. ){
        fragColor = colorAtO(uvO);
        return;
    }

    vec2 uvi = deScale(fragCoord,SCALEFAC);
    //vec2 uvi = fragCoord;

    if(uvO.x < 0.){
        uvi = fragCoord;
    }

    vec2 uv = uvi/iResolution.xy;

    float t1 = 0.;
    for(int i = 1; i<=STEPS; i++){
        vec2 uuv = uvi - vec2(0.,float(i));
        vec2 duv = uvi + vec2(0.,float(i));
        t1 = t1 + colorDe(colorAt(uuv/iResolution.xy),colorAt(duv/iResolution.xy));
    }
    t1 = t1/float(STEPS);

    float t2 = 0.;
    for(int i = 1; i<=STEPS; i++){
        vec2 luv = uvi - vec2(float(i),0.);
        vec2 ruv = uvi + vec2(float(i),0.);
        t2 = t2 + colorDe(colorAt(luv/iResolution.xy),colorAt(ruv/iResolution.xy));
    }
    t2 = t2/float(STEPS);

    // Time varying pixel color
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    if(t1>=THRESHLD|| t2>=THRESHLD){
        fragColor = LINECOL;
    }
    else{
        fragColor = colorAt(uv);
    }
}

// CC0: Travelling to Neon-Giza
//  Some artifacts left and the normals goes bad after
//  awhile (which is the reason for the 60 sec reset)
//  But I will be travelling for a few days and thought I like to publish this before I go.

// Music: RMC Nighthawk by amazing Skaven: https://soundcloud.com/skaven252/rmc-nighthawk

#define TIME        iTime
#define RESOLUTION  iResolution

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec3 col = texture(iChannel0, q).xyz;
  col = aces_approx(col);
  col = sqrt(col);
  fragColor = vec4(col, 1.0);
}

