      if (rt < MAX_RAY_LENGTH_HI) {
        rcol += diffCol*0.2;
      } else {
        rcol += 0.5*render0(p, r, nr, beat);
      }
    } else {
    }
    rcol += 4.0*(diffCol+0.5)*glowCol1/max(dd.x*dd.x, 0.01);
#else
    rcol += 0.5*render0(p, r, nr);
    rcol += 4.0*(diffCol+0.5)*glowCol1/max(dd.x*dd.x, 0.01);
