#define FLARE_UP        1     // Cause the fire to flare up
#define SPEED           1.
#define RAND            (hash12(uv)-0.5)/100.

// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 position)
{
    vec2 p = (position + mod(iTime, 200.) * 1500. + 50.0);
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

#if FLARE_UP
#define COORD (1.-uv.y)*SPEED
#else
#define COORD 1.*SPEED
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    if(iFrame < 2
#if CLICK_TO_RESET
       || iMouse.z > 0.
#endif
       ) {
        fragColor = vec4(vec3(pow(1.-uv.y, 4.)), 1.-pow(uv.y, 10.));
        return;
    }

    if (
      //length(iMouse.xy - fragCoord) > 10. &&
#if AVG_EVERY_N > 0
      (iFrame % AVG_EVERY_N != 0) &&
#endif
      hash12(fragCoord) > 0.05*COORD
    ) {
        fragColor = texture(iChannel0,uv);
        return;
    }

    vec2 s1 = vec2(1., 1.) / iResolution.xy;
    vec2 d = uv;
    float v = texture(iChannel0, d).a;
    float vv; vec2 dd;
#define TEST(DX, DY)  \
    dd = uv + vec2(DX, DY); \
    vv = texture(iChannel0, dd).a; \
    if (vv > v) { v = vv; d = dd; } \
// EOM
    TEST( s1.x,    0.);
    TEST(-s1.x,    0.);
    TEST(   0.,  s1.y);
    TEST(   0., -s1.y);
    TEST(-s1.x, -s1.y);
    TEST( s1.x,  s1.y);
    TEST(-s1.x,  s1.y);
    TEST( s1.x, -s1.y);
#if LOOKUP_DIST > 1
    vec2 s2 = s1 * 2.;
    TEST( s2.x,   0.);
    TEST(-s2.x,   0.);
    TEST(   0., s2.y);
    TEST(   0.,-s2.y);
    TEST( s2.x, s1.y);
    TEST(-s2.x,-s1.y);
    TEST(-s1.x, s2.y);
    TEST( s1.x,-s2.y);
    TEST( s2.x,-s1.y);
    TEST(-s2.x, s1.y);
    TEST( s1.x, s2.y);
    TEST(-s1.x,-s2.y);
#if LOOKUP_DIST > 2
    TEST( s2.x, s2.y);
    TEST(-s2.x,-s2.y);
    TEST( s2.x,-s2.y);
    TEST(-s2.x, s2.y);
#endif
#endif

    float c = clamp(texture(iChannel0, d).r + RAND, 0., 1.);
    fragColor = vec4(c,0,0,v+0.0001);
}

