#define PI  3.14159265359
#define TAU 6.28318530717

const ivec2 txClicTime = ivec2(1, 1);

//HELPER
float Clamp01(float value)
{
    return clamp(value,0.0,1.0);
}

///Inigo Quilez Signed Distance Field
//https://iquilezles.org/articles/distfunctions2d
///Playlist shadertoy SDF : 
//https://www.shadertoy.com/playlist/MXdSRf&from=36&num=12
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
float sdPie( in vec2 p, in vec2 c, in float r )
{
    p.x = abs(p.x);
    float l = length(p) - r;
    float m = length(p-c*clamp(dot(p,c),0.0,r)); // c=sin/cos of aperture
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

//2DShape
float Disk(vec2 uv, vec2 center, float radius, float blur) 
{
    float sdfCircle = length(uv-center)-radius;
	return Clamp01(sdfCircle * (10.0/blur));
}
float Pie(vec2 uv, vec2 center, float angle,float radius, float blur) 
{

    vec2 pos = uv-center;
    float sdfPie = sdPie( pos, vec2(sin(angle),cos(angle)), radius);
	return Clamp01(sdfPie * (10.0/blur));
}



///COLOR
vec3 HSVcolor(vec3 c)
{
    vec4 K = vec4(1.0, 12.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

//UV
vec2 RotateUV(vec2 uv, float angle)
{	
    return uv * mat2( cos(angle), sin(angle),
                     -sin(angle), cos(angle));
}

