const int idBod = 1, idHead = 2, idEar = 3, idArm = 4, idLegd = 5, idTaild = 6, idNos = 7,
   idJaw = 8, idEye = 9, idCap = 10, idNeck = 11, idScarf = 12, idSeat = 13, idCush = 14,
   idKenWl = 15, idKenRf = 16, idKenFl = 17,
   idFus = 21, idPipe = 22, idWngM = 23, idStrut = 24, idWngT = 25, idTail = 26, idLeg = 27,
   idWhl = 28, idNose = 29, idGun = 30, idPlt = 31;
const float pi = 3.1415927;

#define CosSin(x) (sin ((x) + vec2 (0.5 * pi, 0.)))
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float GrndHt (vec2 p)
{
  vec2 q, t, v;
  float wAmp, h;
  wAmp = 3.;
  h = 0.00013 * dot (p, p);
  q = 0.05 * p;
  for (int j = VAR_ZERO; j < 3; j ++) {
    t = abs (sin (q + 2. * Noisefv2 (q) - 1.));
    v = 1. - (1. - t) * (t + sqrt (1. - t * t));
    h += wAmp * dot (v, v);
    q *= 3. * mat2 (0.8, -0.6, 0.6, 0.8);
    wAmp *= 0.25;
  }
  return h;
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + s * rd;
    h = p.y - GrndHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.25, 0.4 * h) + 0.005 * s;
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = VAR_ZERO; j < 6; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (p.y > GrndHt (p.xz)) sLo = s;
      else sHi = s;
    }
