vec4 FlyerCol (int ig, int id, vec3 vn)
{
  vec4 col4;
  vec2 w;
  float s;
  col4 = (ig == 2) ? vec4 (1., 1., 0.1, 0.2) : vec4 (1., 0.1, 0.1, 0.2);
  if (id == idFus) {
    if (qHit.z > 1.01) {
      col4 = vec4 (0.9, 0.9, 0.9, 0.3);
      if (length (abs (qHit.xy + vec2 (0., 0.05)) * vec2 (0.6, 1.)) < 0.03) col4.rgb *= 1. -
         0.7 * SmoothBump (0.2, 0.8, 0.05, mod (64. * ((ig == 1) ? qHit.y : qHit.x), 1.));
    }
    w = abs (qHit.xz) - vec2 (0.09, 0.12);
    if (abs (qHit.y - 0.1) < 0.04 && Maxv2 (w) < 0.) col4.rgb = vec3 (0.2, 0.15, 0.05);
    col4.rgb = mix (1. - col4.rgb, col4.rgb, Insig (1.5 * qHit.yz + vec2 (0., 0.4), ig));
  } else if (id == idWngM) {
    col4.rgb = mix (col4.rgb, 1. - col4.rgb, step (0., qHit.z - 0.14 - 0.07 *
       abs (sin ((8./1.2) * pi * qHit.x))));
    s = 1.2 / 8.;
    s = mod (qHit.x + 0.5 * s, s) - 0.5 * s;
    col4.rgb *= 1. + 0.1 * SmoothBump (-0.01, 0.01, 0.002, s);
    if (abs (qHit.y) > 0.1 && qHit.y * vn.y > 0.) {
      col4.rgb = mix (1. - col4.rgb, col4.rgb, Insig (vec2 (abs (qHit.x) - 0.9, qHit.z), ig));
    }
