    col4.rgb *= 1. + 0.1 * SmoothBump (-0.005, 0.005, 0.001, s);
  } else if (id == idTail) {
    col4.rgb = mix (1. - col4.rgb, col4.rgb, Insig (2.5 * qHit.yz + vec2 (-0.9, -0.25), ig));
  } else if (id == idNose) {
    col4 = (qHit.z > -0.04) ? mix (col4, vec4 (1., 1., 1., 0.5), 0.5) : vec4 (0.1);
  } else if (id == idPipe) {
    col4 = vec4 (0.8, 0.8, 0.85, 0.3) * (0.5 + 0.5 * SmoothBump (0.1, 0.9, 0.05,
       mod (16. * qHit.z, 1.)));
