  } else if (id == idPlt) {
    col4 = vec4 (0.4, 0.2, 0.1, 0.);
    if (qHit.z > 0.02 && qHit.y < 0.03) col4.rgb = (ig == 2) ? vec3 (0.9) : vec3 (0.6, 0.4, 0.4);
    if (qHit.z > 0. && length (vec2 (abs (qHit.x) - 0.011, qHit.y - 0.015)) < 0.01)
       col4 = vec4 (0., 0.2, 0., 0.2);
