       col4.rgb = vec3 (0.1);
  } else if (id == idWhl) {
    if (length (qHit.yz) > 0.07) col4 = vec4 (0.05);
  }
  return col4;
}

vec4 SnpCol (out vec2 vf)
{
  vec4 col4, cb4;
  vec2 u;
