         cb4 * (0.5 + 0.5 * step (0.03, length (qHit.yz - vec2 (0.2, 0.05))));
    } else if (idObj == idEar) {
      col4 *= 0.93 + 0.07 * sin (32. * pi * qHit.z);
    } else if (idObj == idTaild) {
      col4 *= 0.3 + 0.7 * step (-0.17, qHit.z);
    } else if (idObj == idNos) {
