    ff = Fbm2 (q);
    f = smoothstep (0.1, 0.8, ff);
    fd = smoothstep (0.1, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = 0.7 * (0.8 + 0.5 * ff) * (vec3 (1.) - vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    fd = smoothstep (0.01, 0.1, rd.y);
    skCol = mix (vec3 (0.8, 0.8, 0.75), vec3 (0.3, 0.4, 0.7), 0.3 + 0.7 * fd) +
       step (0.1, sd) * vec3 (1., 1., 0.9) * min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * fd);
  } else {
    col = SkyBg (rd);
  }
  return 0.9 * col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, roo, rog;
  vec2 vf;
  float dstGrnd, dstObj, dstFObj, sh, nDotL;
  int ig, id;
  csEar = CosSin (-0.4 * pi * (1. + sin (0.5 * pi * tCur)));
  szKen = vec2 (1.6, 2.2);
  roo = ro;
  isSh = false;
  dstGrnd = GrndRay (ro, rd);
  rog = ro + dstGrnd * rd;
  if (length (rog.xz) > dstFar - 10.) dstGrnd = dstFar;
  dstFObj = FObjRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (min (min (dstObj, dstFObj), dstGrnd) < dstFar) {
    if (min (dstObj, dstFObj) < dstGrnd) {
      if (dstFObj < dstObj) {
        dstObj = dstFObj;
        ro += dstObj * rd;
        vn = FObjNf (ro);
        ig = idObj / 256;
        id = idObj - 256 * ig;
        col4 = FlyerCol (ig, id, flyerMat[ig] * vn);
      } else {
        ro += dstObj * rd;
        vn = ObjNf (ro);
        col4 = SnpCol (vf);
      }
    } else {
      dstObj = dstGrnd;
      if (dstObj < dstFar) {
        ro = rog;
        vf = vec2 (2., 4.);
        vn = GrndNf (ro, dstObj);
        col4 = mix (vec4 (0.2, 0.3, 0.1, 0.05), vec4 (0., 0.4, 0., 0.), 0.3 * Noisefv2 (ro.xz));
      }
    }
    if (col4.a >= 0.) {
      nDotL = max (dot (vn, sunDir), 0.);
      if (idObj >= idBod && idObj <= idTaild) nDotL *= sqrt (nDotL);
      else if (idObj >= idFus && idObj <= idGun) nDotL *= nDotL;
      if (vf.y > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      sh = min (FObjSShadow (ro + 0.01 * vn, sunDir), ObjSShadow (ro + 0.01 * vn, sunDir));
      col = col4.rgb * (0.2 + 0.2 * max (dot (vn, sunDir * vec3 (-1, 1., -1.)), 0.) +
         0.8 * sh * nDotL) + col4.a * step (0.95, sh) * pow (max (dot (sunDir,
         reflect (rd, vn)), 0.), 32.);
      col = mix (col, SkyBg (rd), 1. - exp2 (min (0., 4. - 7. * dstObj / dstFar)));
    } else {
      col = col4.rgb * (0.5 + 0.5 * max (- dot (rd, vn), 0.));
    }
  } else col = SkyCol (ro, rd);
  if (TrObjRay (roo, rd) < min (dstObj, dstFar)) col = mix (col, mix (vec3 (1.), vec3 (0.8),
     step (0.02, abs (length (qHit.xy) - 0.26))), 0.2);
  return clamp (col, 0., 1.);
}

void PathSetup ()
{
  float hTop, sHzStr, sDnRamp, sUpRamp, pScl;
  pScl = 0.7;
  P.ptFac = 20. * pScl;
  P.cPtOrg = vec3 (2. * P.pathDir, 0., -3.) * pScl;
  hTop = 1.5 * pScl;
  P.rLoop = 2.2 * pScl;
  P.sHzRamp = 1. * pScl;
  P.rCirc = 2. * pScl;
  sDnRamp = 1.5 * pScl;
  sUpRamp = 1.3 * sDnRamp;
  sHzStr = sDnRamp - sUpRamp + 3. * P.sHzRamp;
  P.vfLoop = 0.3 * pScl;
  P.cDnCirc = P.cPtOrg + vec3 (- P.rCirc * P.pathDir, 0., -2. * P.sHzRamp);
  P.cUpCirc = P.cPtOrg + vec3 (- P.rCirc * P.pathDir, 2. * hTop, 2. * sDnRamp + 6. * P.sHzRamp);
  P.cpt[0] = P.cDnCirc;
  P.cpt[0].xz += vec2 (- P.rCirc * P.pathDir, sHzStr);
  P.cpt[1] = P.cpt[0];
  P.cpt[1].z += sHzStr;
  P.cpt[3] = P.cUpCirc;
  P.cpt[3].x -= P.rCirc * P.pathDir;
  P.cpt[4] = P.cUpCirc;
  P.cpt[4].x += P.rCirc * P.pathDir;
  P.cpt[2] = P.cpt[3];
  P.cpt[2].z -= 2. * P.sHzRamp;
  P.cpt[5] = P.cpt[4];
  P.cpt[5].z -= 2. * P.sHzRamp;
  P.cpt[7] = P.cPtOrg;
  P.cpt[8] = P.cPtOrg;
  P.cpt[6] = P.cpt[7];
  P.cpt[6].z += 4. * P.sHzRamp;
  P.cpt[9] = P.cDnCirc;
  P.cpt[9].x += P.rCirc * P.pathDir;
  P.cpt[10] = P.cDnCirc;
  P.cpt[10].x -= P.rCirc * P.pathDir;
  P.cpt[11] = P.cpt[0];
  P.tLen[0] = 0.;
  for (int k = 1; k < N_SEG; k ++) P.tLen[k] = length (P.cpt[k] - P.cpt[k - 1]);
  P.tLen[4] = pi * P.rCirc;
  P.tLen[8] = 2. * pi * P.rLoop * (1. + P.vfLoop);
  P.tLen[10] = pi * P.rCirc;
  for (int k = 1; k < N_SEG; k ++) P.tLen[k] += P.tLen[k - 1];
}

vec3 TrkPos (float t)
{
  vec3 r;
  vec2 cs;
  t *= 0.5 / (2. * pi);
  t = fract (t);
  cs = CosSin (2. * pi * t);
  r.xz = vec2 (1., cs.y);
  r.xz = 30. * cs.x * r.xz / dot (r.xz, r.xz);
  r.y = 14. + 4. * sin (2. * pi * t);
  r.z += 12.;
  return r;
}

void FlyerPM1 (float t, float vu, out vec3 flPos, out mat3 flMat)
{
  vec3 vel, acc, va, flVd, fpF, fpB;
  vec2 cs;
  float oRl, dt;
  dt = 0.1;
  flPos = TrkPos (t);
  fpF = TrkPos (t + dt);
  fpB = TrkPos (t - dt);
  vel = (fpF - fpB) / (2. * dt);
  acc = (fpF - 2. * flPos + fpB) / (dt * dt);
  va = cross (acc, vel) / length (vel);
  flVd = normalize (vel);
  oRl = 0.02 * length (va) * sign (va.y);
  oRl = smoothstep (0.1, 0.2, abs (oRl)) * oRl;
  cs = CosSin (oRl);
  flMat = mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.) *
     mat3 (flVd.z, 0., flVd.x, 0., 1., 0., - flVd.x, 0., flVd.z);
}

void FlyerPM2 (float t, float vu, out vec3 flPos, out mat3 flMat)
{
  vec3 p, p1, p2, u;
  vec2 cs;
  float w, ft, ftt, s, h, oRl;
  int ik;
  h = 2. * sin (0.43 * pi * t) + sin (0.79 * pi * t);
  oRl = 0.01 * pi * h;
  t = mod (t, P.tLen[N_SEG - 1]);
  for (int k = 1; k < N_SEG; k ++) {
    if (t < P.tLen[k]) {
      t -= P.tLen[k - 1];
      p1 = P.cpt[k - 1];
      p2 = P.cpt[k];
      w = P.tLen[k] - P.tLen[k - 1];
      ik = k;
      break;
    }
  }
  P.oNorm = vec3 (0., 1., 0.);
  ftt = t / w;
  ft = ftt;
  if (ik == 4 || ik == 10) oRl += -0.15 * pi * SmoothBump (0.05, 0.95, 0.05, ft);
  if (ik == 4) oRl += 2. * pi * smoothstep (0.4, 0.6, ft);
  if (ik == 2 || ik == 6) {
    P.oDir = p2 - p1;
    p.xz = p1.xz + P.oDir.xz * ft * vec2 (P.pathDir, 1.);
    p.y = p1.y + P.oDir.y * smoothstep (0., 1., ft);
    P.oDir.y *= 6. * ft * (1. - ft);
    P.oDir = normalize (P.oDir);
  } else if (ik == 4) {
    ft *= pi;
    p = P.cUpCirc;
    u = vec3 (- cos (ft) * P.pathDir, 0., sin (ft));
    p.xz += P.rCirc * u.xz;
    P.oDir = cross (P.oNorm, u);
    P.oDir.xz *= P.pathDir;
  } else if (ik == 8) {
    ft = (ft < 0.5) ? ft * (1. + P.vfLoop * (1. - 2. * ft)) :
       ft * (1. + 2. * P.vfLoop * (ft - 1.5)) + P.vfLoop;
    p = 0.5 * (P.cpt[7] + P.cpt[8]);
    ft *= 2. * pi;
    u = vec3 (0., cos (ft), sin (ft));
    p.yz += P.rLoop * (vec2 (1., 0.) - u.yz);
    P.oNorm = u;
    P.oDir = normalize (vec3 (0., 2. * pi * P.rLoop * vec2 (sin (ft), - cos (ft))));
  } else if (ik == 10) {
    ft *= pi;
    p = P.cDnCirc;
    u = vec3 (cos (ft) * P.pathDir, 0., - sin (ft));
    p.xz += P.rCirc * u.xz;
    P.oDir = cross (P.oNorm, u);
    P.oDir.xz *= P.pathDir;
  } else {
    P.oDir = p2 - p1;
    p = p1 + P.oDir * ft;
    P.oDir = normalize (P.oDir);
  }
  p *= P.ptFac;
  p.yz += vec2 (15. + 0.1 * h, 80.);
  flPos = p;
  if (vu > 0.) {
    P.oDir *= -1.;
    oRl *= -1.;
  }
  cs = CosSin (oRl * P.pathDir);
  flMat = mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.) * AxToRMat (P.oDir, P.oNorm);
}

vec3 GlareCol (vec3 rd, vec3 sd, vec2 uv)
{
  vec3 col;
  vec2 e;
  e = vec2 (1., 0.);
  if (sd.z > 0.) col = 0.05 * pow (abs (sd.z), 8.) *
     (2. * e.xyy * max (dot (normalize (rd + vec3 (0., 0.3, 0.)), sunDir), 0.) +
      e.xxy * SmoothBump (0.03, 0.05, 0.01, length (uv - 0.7 * sd.xy)) +
      e.yxx * SmoothBump (0.2, 0.23, 0.02, length (uv - 0.5 * sd.xy)) +
      e.xyx * SmoothBump (0.6, 0.65, 0.03, length (uv - 0.3 * sd.xy)));
  else col = vec3 (0.);
  return col;
}

#define N_WIN  1

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, dateCur;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, uvv, mMid[N_WIN], ut[N_WIN], mSize, msw;
  float el, az, zmFac, asp, sr, azH, elH;
  int vuId, regId;
  bool sWin;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur += 66.;
  asp = canvas.x / canvas.y;
  sWin = true;
  if (sWin) {
    mSize = (1./6.) * vec2 (asp, 1.);
    mMid[0] = (1. - mSize.y) * vec2 (- asp, -1.);
    for (int k = 0; k < N_WIN; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  }
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    if (sWin) {
      for (int k = 0; k < N_WIN; k ++) {
        msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
        if (Maxv2 (abs (msw)) < mSize.y) {
          regId = k + 1;
          msw /= 2. * mSize.y;
          break;
        }
      }
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  if (sWin) {
    for (int k = 0; k < N_WIN; k ++) {
      if (Maxv2 (ut[k]) < 0.) {
        uv = (uv - mMid[k]) / mSize.y;
        vuId = k + 1;
        break;
      }
    }
  }
  if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0. && vuId == regId) {
    az += 0.2 * 2. * pi * msw.x;
    el += 0.2 * 0.5 * pi * msw.y;
  }
  flSzFac = 1.5;
  snpSzFac = 0.3;
  P.pathDir = 1.;
  PathSetup ();
  if (length (uv * vec2 (1. / asp, 1.)) < 1.5 * SmoothBump (0.25, 0.75, 0.01,
     fract (tCur / 80.))) vuId = 1 - vuId;
  flyVel = (vuId == 0) ? 0.5 : 0.5;
  if (vuId == 0) {
    for (int k = 0; k < N_FLYER; k ++)
       FlyerPM1 (tCur * flyVel + ((k == 0) ? 0. : 1.), 0., flyerPos[k], flyerMat[k]);
  } else {
    for (int k = 0; k < N_FLYER; k ++)
       FlyerPM2 ((tCur + ((k == 0) ? 0. : 2.)) * flyVel, 0., flyerPos[k], flyerMat[k]);
  }
  ro = vec3 (0., 11., 0.);
  vd = normalize (0.5 * (flyerPos[0] + flyerPos[1]) - ro);
  azH = atan (vd.z, - vd.x) - 0.5 * pi;
  elH = asin (vd.y);
  vuMat = StdVuMat (el + elH, az + azH);
  csHeadAz = CosSin (clamp (azH, -0.3 * pi, 0.3 * pi));
  csHeadEl = CosSin (clamp (elH, -0.1 * pi, 0.2 * pi));
  ro.xz -= vec2 (1., 5.);
  ro = vuMat * ro;
  zmFac = 4.;
  sunDir = normalize (vec3 (0.5, 1., -1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.02 * tCur);
  dstFar = 400.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) *
       pi)) / zmFac;
    rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  col += GlareCol (rd, sunDir * vuMat, uv);
  if (sWin) {
    for (int k = 0; k < N_WIN; k ++) {
      if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 3.) col = vec3 (0.7, 0.3, 0.3);
    }
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (vec3 (p.xy, sign (p.z) * (max (0., abs (p.z) - h)))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (vec2 (p.x, sign (p.y) * (max (0., abs (p.y) - h)))) - r;
}

float PrConeDf (vec3 p, vec3 b)
{
  return max (dot (vec2 (length (p.xy), p.z), b.xy), abs (p.z) - b.z);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 AxToRMat (vec3 vz, vec3 vy)
{
  vec3 vx;
  vx = normalize (cross (vy, vz));
  vy = cross (vz, vx);
  return mat3 (vec3 (vx.x, vy.x, vz.x), vec3 (vx.y, vy.y, vz.y), vec3 (vx.z, vy.z, vz.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float colorAlpha;
    float tipThreshold = (1.0 - sin(iTime)) * PI;

    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    float angle = atan(uv.y, uv.x);

    if (angle < 0.0) angle += 2.0 * PI;

    float startAngle = 0.0;

    startAngle += tipThreshold;

    if (angle >= startAngle) {
        colorAlpha = 0.0;
    } else {
        colorAlpha = 1.0;
    }

    fragColor = vec4(vec3(colorAlpha),1.0);
}

vec3 palette( float t ) {
    vec3 a = vec3(0.238, 0.210, 0.078);
    vec3 b = vec3(0.798, 0.368, 0.958);
    vec3 c = vec3(0.036, 0.256, 0.256);
    vec3 d = vec3(1.917, 1.337, 2.667);

    return a + b*cos( 6.28318*(c*t*d) );
} 
//used http://dev.thi.ng/gradients/ to pick palette

float sdOctogon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.9238795325, 0.3826834323, 0.4142135623 );
    p = abs(p);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
} 
//https://iquilezles.org/articles/distfunctions2d/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);


    for (float i = 0.0; i < 3.0; i++){
        uv = fract (uv * 1.4) - .5;

        float d = sdOctogon( uv, 0.35 );

        vec3 col = palette(length(uv0) + i*.8+ iTime*.8);

        d = sin(d * 9. + iTime)/9.;
        d = abs(d);
        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
}


    fragColor = vec4(finalColor, 1.0);

}

